    box-shadow: 0 1px 0 0 hsla(0, 0%, 40%, .35);
}

.config-body label:not(:first-child) {
    border-top: 1px solid hsl(0, 0%, 0%)!important;
    box-shadow: inset 0 1px 0 0 hsla(0, 0%, 40%, .35);
}

#options > #actions {
    border: 0!important;
    box-shadow: inset 0 1px 0 hsla(0, 0%, 40%, .35)!important;
}

#options-close-icon:hover .svg-icon {
