        position: absolute!important;
        margin: 1px 4px 0!important;
        padding: 1px 3px!important;
        border-radius: 4px!important;
        font-size: 11px!important;
        font-weight: bold!important;
        text-indent: 0!important;
        letter-spacing: .5px;
        content: "UC";
        text-transform: uppercase!important;
        background-color: hsla(180, 100%, 20%, 1)!important;
        opacity: 1!important;
        color: hsl(0, 0%, 90%)!important;
        text-shadow: -1px -1px 0 hsl(0, 0%, 0%), 1px -1px 0 hsl(0, 0%, 0%), -1px 1px 0 hsl(0, 0%, 0%), 1px 1px 0 hsl(0, 0%, 0%)!important;
    }
    .newUI .entry.disabled.usercss .style-name:before {
        opacity: .3!important;
        transition: opacity .25s!important;
        will-change: opacity;
    }
    .newUI .entry.disabled.usercss:hover .style-name:before {
        opacity: 1!important;
    }
    html.newUI .entry .style-name::before {
        display: none!important;
    }
    .newUI .homepage:not([href=""]) {
        position: relative!important;
    }
    .newUI .checker {
        position: absolute!important;
        margin: 0!important;
        top: 2px!important
    }
    .newUI #stylus-manage .style-name {
        position: relative!important;
    }
    .newUI .update[title="Install update (local edits will be overwritten)"]:after {
        margin: -3px 0 0 -248px!important;
    }
    .newUI .up-to-date[title="Style is up to date."]:after,
    .newUI .updated[title="Update completed."]:after {
        margin: -3px 0 0 -128px!important;
    }
    .newUI .update[title="Install update"]:after {
        margin: -3px 0 0 -103px!important;
    }
    .newUI #installed {
        display: flex!important;
        flex-wrap: wrap;
        align-content: flex-start;
    }
    .newUI #installed .entry.hidden,
    .newUI #installed .entry .applies-to {
        display: none!important;
    }
    .newUI #installed .entry {
        height: 30px!important;
        margin: 0!important;
        padding: 0 12px!important;
        flex-grow: 1;
        display: flex!important;
        justify-content: space-between;
        align-items: center;
        box-sizing: border-box;
    }
    .newUI .actions {
        width: auto!important;
        flex-wrap: nowrap;
        display: flex!important;
        justify-content: flex-end;
        align-items: center;
    }
    .newUI .actions > .homepage {
        margin: 0 4px 0 0!important;
    }
    .newUI .actions > .updater-icons {
        margin: 0 5px 0 0!important;
    }
    .newUI .actions > [title="Delete"] {
        order: 3!important;
        margin: 0!important;
    }
    .newUI .entry > * {
        padding: 0!important;
        text-indent: 0!important;
    }
    .newUI .svg-icon:not(.info):not(.checked):not(.select-arrow) {
        position: relative!important;
        margin: 0!important;
    }
    .newUI .style-name {
        white-space: nowrap!important;
        overflow: hidden!important;
        text-overflow: ellipsis!important;
    }
}

/*[[ColumnNumber]]*/

@media (min-width: 1024px) {
:root { --columns: 2 }

html.newUI #installed .entry {
    width: 50%!important;
}

html.newUI .entry.odd {
    background-color: transparent!important;
}

html.newUI .entry:nth-child(4n+1),
html.newUI .entry.odd:nth-child(4n+4) {
    background-color: hsla(0, 0%, 0%, .125)!important;
}
}

@media (min-width: 1024px) {
:root { --columns: 4 }

html.newUI #installed .entry {
    width: 25%!important;
}

html.newUI .entry.odd {
    background-color: transparent!important;
}

html.newUI .entry:nth-child(8n+1),
html.newUI .entry:nth-child(8n+3),
html.newUI .entry:nth-child(8n+6),
html.newUI .entry:nth-child(8n+8) {
    background-color: hsla(0, 0%, 0%, .125)!important;
}
}
