style.css:
@-moz-document url-prefix("about:stylish-edit") {
/*

	This Style is created by Dave Tucker
	Version: 0.1.1
	More Styles -> https://userstyles.org/users/351088

*/

#stylish-edit, #stylish-manage {
    background: linear-gradient(
      rgba(0, 0, 0, 0.3), 
      rgba(0, 0, 0, 0.3)
    ), url('http://fullhdpictures.com/wp-content/uploads/2016/01/Most-Beautiful-Audi-TT-Wallpaper.jpg');
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    background-attachment: fixed;
    background-color: #F7F7F7;
    color: #fff;
}

#stylish-edit button, #stylish-manage button {
	border: 0;
    padding: 5px 10px 5px 10px;
    background-color: #F0F0F0;
    outline: none;
    transition: ease-in-out 0.3s;
}
#stylish-edit button:hover, #stylish-manage button:hover {
    background-color: #C4C4C4;
}

#stylish-edit #name, #stylish-manage #name {
    padding: 5px 10px 5px 10px;
    outline: 0;
    border: 0;
    margin: 0 0 2px 0;
    transition: ease-in-out 0.3s;
}
#stylish-edit #name:focus, #stylish-manage #name:focus {
    background-color: #D1D1D1;
}

#stylish-edit input[type='number']:focus {
	outline: 0;
}

#stylish-manage #manage-text a {
	color: #E6E6E6;
    transition: ease-in-out 0.3s;
}
#stylish-manage #manage-text a:hover {
	color: #C4C4C4;
}

#stylish-edit button, #stylish-edit select, #stylish-edit a, #stylish-edit .option, #stylish-edit img, #stylish-manage button, #stylish-manage select, #stylish-manage a, #stylish-manage .option, #stylish-edit img {
	cursor: pointer;
}


#stylish-edit:after, #stylish-manage:after {
	content: 'Stylish - DT | v0.1.1';
    position: fixed;
    right: 5px;
    bottom: 5px;
    font-size: 17px;
}
}
