.block {
	border-bottom : 1px solid #DCDCCC;
}
html.opera #notes ,
#notes {
	background-color   : #3F3F3F;
	border-top         : 1px solid #DCDCCC;
	-webkit-box-shadow : 0 0 0 0 transparent;
	color              : #DCDCCC;
}

input[type="number"] , 
input[type="color"] , 
button {
	background-color : #3F3F3F;
	border           : 1px solid #DCDCCC;
	color            : #DCDCCC;
}
button {
	border : 1px solid #DCDCCC !important;
	color  : #DCDCCC !important;
}
input[type="number"]:hover ,
input[type="color"]:hover , 
button:hover {
	background-color : #000;
}
#actions button {
	background-image : none;
	box-shadow       : 0px 0px 0px transparent;
	text-shadow      : 0px 0px 0px transparent;
}
input.slider {
	background-color : transparent !important;
	border : 0px !important;
}
.onoffswitch span {
	background-color : #3F3F3F;
}
.onoffswitch input:checked + span {
	background-color : #3F3F3F;
}
.onoffswitch span:before {
	background : #888;
}
.onoffswitch input:checked + span:before {
	background-color : #01d4b5;
}

#advanced.collapsible.collapsed h1 {
	color : #AAA;
}
#advanced.collapsible.collapsed h1 .svg-icon {
	fill : #AAA;
}
#advanced.collapsible.collapsed:hover h1 {
	color : #FFF;
}
#advanced.collapsible.collapsed:hover h1 .svg-icon {
	fill : #FFF;
}
#advanced.collapsible h1 {
	color : #FFF;
}
#advanced.collapsible h1 .svg-icon {
	fill : #FFF;
}
#advanced.collapsible:not(.collapsed):hover h1 {
	color : #AAA;
}
#advanced.collapsible h1:not(.collapsed):hover .svg-icon {
	fill : #AAA;
}
}
