style.css:
@-moz-document url-prefix("http://confluence"), url-prefix("https://confluence") {
#full-height-container{
    filter: invert(100%) !important;
    -webkit-filter: invert(100%) !important;
}
#header{
    filter: invert(100%) !important;
    -webkit-filter: invert(100%) !important; 
}

header.aui-page-header{
    background-color: #fff !important;
}
    
#announcement-banner{
    margin: 0px !important;
    padding: 0px !important;
}

html{
    background-color: #000;
}

#main{
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    -webkit-text-stroke: 0.5px;
}

a.confluence-userlink.current-user-mention, .aui-lozenge{
 	-webkit-text-stroke: 0;   
}

img, .aui-avatar-inner{
    filter: invert(100%);
    -webkit-filter: invert(100%);
}
#cp-img{
    filter: invert(0%);
    -webkit-filter: invert(0%);
}
}
