*/

body::-webkit-scrollbar
{
    width: 1em;
}

body::-webkit-scrollbar-thumb
{
    background-color: darkgrey;
}

body, #navigation, #view
{
    background: none;
}

/*[[header_color]]*/

/*[[panel]]*/

#root
{
    background: url(/*[[background]]*/) /*[[position]]*/ fixed;
    background-size: /*[[size]]*/;
}

/* Forum: */
#Body { color: black; }

/*END MOZ SPECS */}

options.value:
#copyrights { color: white; }
#footer { background-color: #2e3e49; }
#footer .logo { background-color: rgba(255, 255, 255, 0.8); border-radius: 1em; padding: 0.5em; }

#header {
    background-color: #e7eaef;
}

#search_input {
    border: solid 1px #3498db;
}

#search_input input, .header_button a {
    color: #3498db !important;
}

a, #pages a { color: #aec7ec; }
a:visited, #pages a:visited {color: #d3bae6;}
body { color: #eeeeee; }
pre { background-color: black; }

#nav-view { background: rgba(46, 62, 73, 0.8); }

#applies_link, #headline, #information_key, #information_value, #infomation_key_left, #infomation_value_left, #information_key_left, #information_value_left, .navigation_title, #share_div, .style_title, .text_style_page { color: white; }
.navigation_link img { background: white; border-radius: 50%; }
.see_more,  .setting_div { color: #2e3e49; }
