style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

  /* video info */
  #info #menu-container {display:none}
  #subscribe-button {display:none !important;}
  
  /* comments */
  #comments {display:none}
  
  /* suggestions */
  #upnext {display:none}
  ytd-compact-radio-renderer {display:none}
  ytd-compact-video-renderer {display:none}
  ytd-compact-playlist-renderer {display:none}
  #related #continuations {display:none}
  ytd-compact-autoplay-renderer {border:none}
  #related #head {opacity: 0.2}
  
  /* search bar on hover */
  #masthead {display:none}
  #masthead-container {height: 60px}
  #masthead-container:hover #masthead {display: block}
  #masthead-container.ytd-app::after {display:none}
  
  /* overlay ads */
  .annotation {display:none}
  .ytp-button {display:none}
  .ytp-cards-teaser {display:none}
  
  /* landing page */
  ytd-browse[page-subtype="home"] {display:none}
  #guide {display:none}
}
