style.css:
@-moz-document url-prefix("https://www.clien.net") {
/* common */

    body,
    body.main {
        padding-left: 219px;
    }
    body.bg {
        padding-left: 0;
    }

    input[type="text"],
    input[type="password"],
    input[type="tel"],
    input[type="email"],
    input[type="url"] {
        border: 0;
        outline: none;
        -webkit-appearance: none;
    }

    .back-to-top {
        height: 0;
    }

    .board-foot .select-year .line {
        padding: 7px 0 6px 0;
        border: 0;
        border-radius: 0px;
        height: 32px;
    }

        .bootstrap-select .btn {
            color: inherit;
        }


/* nav */

    .nav {
        width: 219px;
        height: auto;
        position: absolute;
        left: 0;
        top: 0;
        background: none;
        z-index: 1;
    }

        .nav .container {
            width: auto;
            background: none;
        }

        .nav .nav-logo {
            float: none;
            padding: 50px 0;
            width: auto;
            height: auto;
            border: 0;
            background: none;
        }

        .nav .nav-logo .logo-region {
            display: block;
            margin: 0 auto;
        }

        .nav .nav-menu {
            background: none;
            float: none;
            width: auto;
        }

        .dropdown-toggle.menu-toggle {
            display: none;
        }

        .nav .dropdown-menu {
            display: block;
            position: relative;
            top: auto;
            float: none;
            background: none;
            box-shadow: none;
        }

        .sub-menu ul {
            border: 0;
            float: none;
            min-height: 0;
        }

        .sub-menu ul:hover {
            background: none;
        }

        .sub-menu ul .view-all {
            border: 0;
        }

        .sub-menu li a {
            width: auto;
            padding: 8px 0 8px 25px;
        }

        .sub-menu li a:hover {
            color: inherit;
        }

        .sub-menu .view-all a {
            font-weight: normal;
            padding: 8px 0 8px 7px;
        }

        .sub-menu .view-all a:before {
            font-family:'FontAwesome';
            content: "\f107";
            display: inline-block;
            position: relative;
            width: 16px;
            height: 16px;
            margin-top: auto;
            left: auto;
            font-size: 16px;
            margin-right: 5px;
            vertical-align: -1px;
            text-align: center;
        }

        .sub-menu ul .view-all .icon-board {
            background-color: inherit;
            float: right;
            position: relative;
            top: 1px;
            color: inherit;
            width: 14px;
            height: 14px;
            font-size: 10px;
            margin-right: 10px;
            line-height: 16px;
            border-radius: 3px;
        }
            
        .sub-menu ul .view-all .icon-board .fa {
            display: none;
        }

        .sub-menu ul .view-all .icon-board:before {
            font-family:'FontAwesome';
            content: "\f0a2";
        }

        .sub-menu ul .none, .sub-menu ul .block {
            height: 0;
        }

        .sub-menu .extras {
            padding: 0;
            background: none;
            border: 0;
        }

        .sub-menu .extras a {
            display: block;
            width: auto;
            padding: 8px 0 8px 7px;
            font-size: 12px;
        }

        .sub-menu .extras a .fa {
            width: 16px;
            height: 16px;
            padding: 0;
            font-size: 14px;
            top: auto;
            margin-right: 5px;
            vertical-align: -1px;
            text-align: center;
        }

        .sub-menu .extras .button-promo-board {
            width: auto;
            float: none;
            font-weight: normal;
        }

        .sub-menu .extras .button-promo-board .icon-board {
            display: none;
        }

        .sub-menu .extras a:before {
            width: 16px;
            height: 16px;
            padding: 0;
            font-size: 14px;
            top: auto;
            margin-right: 5px;
            vertical-align: -1px;
            text-align: center;
            display: inline-block;
        }

        .sub-menu .extras .button-promo-board:before {
            font-family:'FontAwesome';
            content: "\f291";
        }

        .nav .nav-group {
            float: none;
            width: auto;
            background: none;
            right: auto;
        }

        .nav .nav-group:hover {
            background: none;
        }

        .nav-group .group-menu-toggle {
            text-align: left;
            padding: 8px 0 8px 7px;
            border-right: 0;
            border-left: 0;
            font-size: 12px;
            height: auto;
        }

        .nav-group .group-menu-toggle:before  {
            width: 16px;
            height: 16px;
            padding: 0;
            font-size: 14px;
            top: auto;
            margin-right: 5px;
            vertical-align: -1px;
            text-align: center;
            display: inline-block;
        }

        .nav-group .group-menu-toggle:before {
            font-family:'FontAwesome';
            content: "\f0f4";
        }

        .nav-group .group-menu {
            width: auto;
            display: none;
        }

        .nav-group.open .group-menu {
            display: block;
        }

        .group-menu-inner {
            margin-top: 0;
        }

        .group-menu ul {
            float: none;
            margin: 0;
            border: 0;
        }

        .group-menu ul:nth-child(2) {
            border: 0;
        }

        .group-menu li {
            width: auto;
            padding: 0;
        }

        .group-menu li a {
            padding: 8px 0 8px 25px;
        }

        .group-menu .group-sub-menu {
            float: none;
            background: none;
            width: auto;
        }

        .group-menu .group-sub-menu ul {
            background-color: transparent;
            width: auto
        }

        .group-menu .group-sub-menu .group-menu-action {
            padding: 0;
            width: auto;
            background: none;
            position: relative;
            top: auto;
            float: none;
        }

        .group-menu .group-sub-menu .group-menu-action a {
            padding: 8px 0 8px 7px;
            margin: 0;
            float: none;
        }

        .group-menu .group-sub-menu .group-menu-action .button-list {
            width: auto;
            border-right: 0;
        }

        .group-menu .group-sub-menu li {
            padding: 0;
            float: none;
        }

        .group-menu .group-sub-menu li a {
            padding: 8px 0 8px 7px;
        }

        .icon-bullet {
            display: inline-block;
            background: none;
            margin: 0;
            width: 16px;
            height: 16px;
            font-size: 10px;
            margin-right: 5px;
            line-height: 16px;
            border-radius: 3px;
            vertical-align: middle;
            text-align: center;
        }

        .icon-bullet:before {
            font-family:'FontAwesome';
            content: "\f0f4";
        }

        .contents-title {
            width: auto;
            float: none;
        }

        .contents-title .board-search {
            padding: 0;
            width: auto;
            height: auto;
            border-right: 0;
        }

        .contents-title .board-search .side-search {
            margin-left: 0;
        }

        .contents-title .board-search .side-search .search {
            width: auto;
        }

        .search {
            position: relative;
        }

            .search .button-search {
                width: 30px;
                height: 32px;
                padding: 0;
                margin: 0;
                font-size: 0;
                position: absolute;
                top: 0;
                left: 0;
            }

            .search .button-search span {
                width: 16px;
                height: 16px;
                position: absolute;
                left: 7px;
                top: 8px;
                text-align: center;
                font-size: 14px;
            }

            .search .search-input {
                padding: 8px 0 8px 31px;
                width: 100%;
                height: 32px;
                color: inherit;
                background: none;
                -webkit-transition: none;
                transition: none;
                font-size: 12px;
            }


/* main contents */

    .main-container {
        margin: 0;
        background: none;
        border-left: 0;
    }

        .nav-container {
            min-width: 578px;
            z-index: 5;
        }

        .container {
            width: auto;
            margin: 0;
        }

        .sidemenu {
            display: none;
        }

        .content {
            float: none;
            padding: 0;
            width: auto;
            margin-right: 220px;
            border-right: 0;
            background: none;
        }

        .error .content {
            margin-right: 0;
        }

    .action {
        height: 122px !important;
        padding-left: 10px;
        margin-top: 0;
        margin-left: 0;
        margin-bottom: 6px;
        position: relative;
    }

        .action.affix {
            position: relative;
            margin-top: 0;
            top: auto;
            z-index: 9996;
        }

        .action .action-box {
            height: 24px;
            padding: 7px 0;
            background: none;
            border: 0;
            padding-right: 0;
            position: relative;
            top: 46px;
        }

        .action .action-box ul {
            display: inline-block;
            overflow: hidden;
            font-size: 0;
            border-radius: 3px;
            white-space: nowrap;
        }

        .action .action-box ul {
            border: 1px #424242 solid;
        }

        .action .dropdown {
            display: inline-block;
        }

            .new-post {
                bottom: auto;
                right: auto;
                left: auto;
            }

            .action .write .blue {
                background: none;
                border: 0;
                padding-top: 0;
                color: inherit;
            }

        .action .action-icon {
            width: 31px;
            height: 21px;
            padding: 0;
            border-top: 0;
            font-size: 16px;
            margin: 0;
            display: inline-block;
            vertical-align: top;
            margin-right: -1px;
        }

        .action .action-icon:first-child {
            margin-left: -1px;
        }

        .action .action-icon a {
            width: 100%;
            display: block;
            line-height: 21px;
        }

        .action .tooltip .tooltiptext {
            display: none;
        }

        .action .action-icon .fa {
            top: auto;
        }

        .action .action-icon .fa-angle-double-left,
        .action .action-icon .fa-angle-left,
        .action .action-icon .fa-angle-right,
        .action .action-icon .fa-angle-double-right {
            top: -1px;
        }

        .action .action-icon.fix .fa-text-height {
            top: auto;
        }

        .action .action-icon.fix .fa {
            top: auto;
        }

        .action .action-icon .fa.fa-commenting {
            top: -1px;
        }

        .action .action-icon .fa.fa-list {
            top: 1px;
        }

        .action .action-icon .fa.fa-caret-up {
            top: -1px;
        }


    .main-primary {
        margin: 0;
        position: relative;
    }

        .main-content .primary-news {
            float: none;
            padding: 0;
            position: relative;
            width: auto;
            margin-bottom: 25px;
            /*margin-right: 130px;*/
            overflow: hidden;
        }

        .div-rs-side-foot {
            border: 0;
            width: auto;
            height: 626px;
            margin: 0px auto;
            border-radius: 5px;
            padding-top: 25px;
        }

        .div-rs-side-foot.affix {
            position: relative;
            top: auto;
        }

    /* ad */

        /*.div-rs-main-top,
        .div-rs-main-center,
        .div-rs-header,
        .div-rs-under,
        .div-rs-side-foot
        {
            display: none !important;
        }*/

        .div-rs-main-top {
            width: 336px;
            height: 80px;
            overflow: hidden;
            position: absolute;
            right: 10px;
            top: -110px;
            margin-top: 0;
            background: none;
            box-sizing: border-box;
            border-radius: 5px;
        }

            .div-rs-main-top:before {
                font-size: 32px;
                line-height: 90px;
                position: absolute;
                z-index: 1;
                width: 100%;
                height: 100%;
                margin-top: 0;
            }

            .div-rs-main-top:after {
                display: none;
            }

            .div-rs-main-top > div {
                position: absolute;
                top: 50%;
                left: 0;
                /*margin-left: -168px;*/
                margin-top: -140px;
                /*transform: scale(0.42);*/
            }

        .div-rs-main-center {
            margin: 0;
            margin-bottom: 25px;
            position: relative;
            display: block;
            width: 100%;
            height: 90px;
            overflow: hidden;
        }

            .div-rs-main-center > div {
                position: absolute;
                top: 0;
                left: 50%;
                margin-left: -485px;
            }

        .div-rs-header {
            margin: 0;
            height: 90px;
            width: 100%;
            position: relative;
            background: none;
        }

            .div-rs-header > div {
                position: relative;
                top: 0;
                left: -1px;
                margin: 0 auto; 
            }

        .div-rs-under {
            margin: 0;
            height: 90px;
            width: 100%;
            position: relative;
            background: none;
            overflow: hidden;
        }

            .div-rs-under:after {
                width: 100%;
            }

            .div-rs-under > div {
                position: absolute;
                top: 0;
                left: 50%;
                margin-left: -485px;
            }

    .main-content .preview,
    .sympathyList, .recentlyList {
        margin-bottom: 25px;
        float: none;
        width: auto;
        padding: 0;
    }

        .main .preview.last,
        .sympathyList, .recentlyList {
            width: auto;
            padding: 0;
            border-bottom: 0;
        }

        .main-content .symph-list {
            padding: 0;
        }

        .recentlyList {
            margin: 0;
        }
            .recentlyList .list-time {
                border: 0;
            }

        .main-content h4,
        .main-content .primary-news h4 {
            padding: 3px 0 3px 10px;
            margin-bottom: 0;
            border-bottom: 0;
            color: inherit;
            height: auto;
            font-size: 11px;
        }

        h4.post-title {
            padding: 6px 0 3px 10px;
            margin-top: 0;
            background: none;
            border: 0;
            color: inherit;
        }

        .sympathyList h4.post-title,
        .recentlyList h4.post-title {
            border: 0;
        }

        .preview h4 a,
        .sympathyList h4 a,
        .recentlyList h4 a {
            font-size: 11px;
            color: inherit;
        }

        .main-primary .preview h4 a {
            font-size: 11px;
        }

        .main-content .primary-news li,
        .preview-list li {
            height: auto;
            position: relative;
            padding: 5px 45px 5px 35px;
            box-sizing: content-box;
        }

        /* .main-content .primary-news li:before {
            font-family:'FontAwesome';
            content: "\f1ea";
            display: block;
            position: absolute;
            width: 16px;
            height: 16px;
            top: 50%;
            margin-top: -8px;
            left: 10px;
            font-size: 13px;
            line-height: 16px;
            text-align: center;
        } */

        .main-content .primary-news li a,
        .preview-list li a {
            display: inline-block;
            color: inherit;
            width: auto;
            font-size: 13px;
            font-weight: 500;
            position: static;
            line-height: 1.1166;
            vertical-align: middle;
        }

        .preview .preview-list a {
            color: inherit;
            float: none;
            font-size: 13px !important;
            margin-right: 3px;
        }

        .preview .preview-list a .subject-shortname,
        .subject-shortname,
        .preview-list .list-group {
            background: none;
            padding: 0;
            font-weight: 300;
            font-size: 11px;
            line-height: 16px;
            margin-right: 0;
            margin-bottom: 3px;
            border-right: 0;
            min-width: 100%;
            display: block;
            text-align: left;
        }

        .preview .preview-list .icon-image,
        .board-list .icon-image {
            margin-left: 0;
            position: absolute;
            width: 16px;
            height: 16px;
            top: 23px;
            margin-top: 0;
            left: 10px;
        }

        .main-content .primary-news li a .date,
        .preview-list li a .date {
            color: inherit;
            padding-right: 20px;
            font-size: 11px;
            position: absolute;
            top: 5px;
            right: 10px;
            line-height: 1.1166;
        }

        .main-content .primary-news li a .date:before,
        .preview-list li a .date:before {
            font-family:'FontAwesome';
            content: "\f133";
            display: block;
            position: absolute;
            width: 16px;
            height: 16px;
            top: 50%;
            margin-top: -7px;
            right: 0;
            font-size: 13px;
            line-height: 16px;
            text-align: center;
        }

        .main-content .primary-news li .icon-bullet,
        .preview-list li .icon-bullet {
            text-align: left;
            font-size: 12px;
            vertical-align: middle;
            position: absolute;
            left: 10px;
            top: 8px;
        }

        .main-content .primary-news li .icon-bullet:before,
        .preview-list li .icon-bullet:before {
            content: "\f291";
            position: relative;
            top: -2px;
        }

        .badge-reply {
            padding: 0;
            padding-right: 20px;
            display: block;
            height: 16px;
            font-size: 11px;
            cursor: default;
            position: absolute;
            top: 22px;
            right: 10px;
            margin-top: 0;
            border-left: 0;
            text-align: center;
            color: inherit;
        }

        .badge-reply:before {
            font-family:'FontAwesome';
            content: "\f27b";
            display: block;
            position: absolute;
            width: 16px;
            height: 16px;
            top: 50%;
            margin-top: -9px;
            right: 0;
            font-size: 13px;
            line-height: 16px;
            text-align: center;
        }

            .board-list .list-row.notice-row .badge-reply,            
            .board-list .list-row.ad-row .badge-reply {
                right: 65px;
            }

        .list-foot {
            width: auto;
            padding: 0;
            margin-bottom: 0;
            right: auto;
            position: relative;
            border: 0;
            display: block;
            background: none;
            float: none;
        }

        .board-foot {
            width: auto;
            padding: 30px 30px 55px 30px;
            margin: 0;
            box-sizing: content-box;
        }

            .board-foot .button-region {
                top: 35px;
                right: 30px;
            }

        .main-tertiary {
            float: none;
            width: auto;
            margin-bottom: 0;
            margin-top: 0;
        }

        .main-gallery {
            padding: 0;
        }

        .preview .preview-gallery {
            width: auto;
            margin: 0 35px;
        }

        .main-gallery .preview-gallery li {
            width: 120px;
            border-right: 5px solid transparent;
            display: inline-block;
            vertical-align: top;
        }

        .main-gallery .preview-gallery li .image-border {
            height: auto;
        }

    /* list */

        .board-head {
            padding: 0 20px;
            border-bottom: 0;
            margin-bottom: 13px;
        }

            .board-head .board-title {
                display: block;
                padding: 0;
                padding-top: 2px;
                letter-spacing: -0.5px;
                border-right: 0;
                margin-right: 10px;
                font-size: 12px;
                color: inherit;
                min-width: 0;
            }

                .board-head .board-title .board-icon {
                    text-transform: uppercase;
                    font-weight: 500;
                    width: 13px;
                    height: 13px;
                    font-size: 9px;
                    display: inline-block;
                    text-align: center;
                    margin-right: 6px;
                    line-height: 13px;
                    vertical-align: middle;
                }

                .board-head .board-title a {
                    font-size: 11px;
                    vertical-align: middle;
                }

        .board-nav {
            padding: 0 10px;
            border-bottom: 0;
            text-align: right;
            position: relative;
        }

            .board-nav .text-large,
            .board-nav .list-small {
                display: none;
            }

            .board-nav .board-filter {
                float: none; 
                padding: 0;
                position: absolute;
                top: -30px;
                right: 10px;
            }

                .board-nav .board-filter .bootstrap-select.line {
                    padding: 0;
                    border: 0;
                    border-radius: 0px;
                }
                .board-nav .board-filter .bootstrap-select .btn {
                    font-size: 11px;
                }

        .board-category {
            padding: 0 25px;
            background-color: transparent;
            font-size: 0;
        }
            .board-category li {
                width: auto;
                font-size: 11px;
            }

            .board-category li .current {
                background-color: transparent;
                color: inherit;
            }

            .board-category li a {
                padding: 5px 10px;
                margin: 0;
            }

            .board-category li .current:before {
                vertical-align: 1px;
            }

        .board-list {
            display: block;
            border-left: 0;
        }

            .board-list .list-row {
                text-align: left;
                display: block;
                width: auto;
                border-right: 0;
                padding: 10px 10px 10px 35px;
                position: relative;
            }

                .board-list .list-row.notice-row,
                .board-list .list-row.ad-row {
                    background: none;
                    padding: 8px 55px 8px 35px;
                }

                .board-list .list-row .item {
                    display: block;
                    text-align: left;
                }

                .board-list .list-symph,
                .board-list .list-title,
                .board-list .list-time,
                .board-list .button-region {
                    border-bottom: 0;
                    display: block;
                }

                    .board-list .list-symph {
                        width: auto;
                        border-left: 0;
                        background: none;
                        padding: 0;
                        font-weight: 300;
                        font-size: 11px;
                        line-height: 16px;
                        margin-right: 0;
                        margin-bottom: 3px;
                        border: 0;
                        min-width: 100%;
                        display: block;
                        text-align: left;
                    }

                    .subject-category {
                        color: inherit;
                    }

                    .subject-status, .subject-category, .subject-soldout, .subject-shortname {
                        border-right: 0;
                    }

                    .sympathyList .board-list .list-title, .sympathyList .board-list .list-time, .sympathyList .board-list .list-symph, .recentlyList .board-list .list-title, .recentlyList .board-list .list-time, .recentlyList .board-list .list-symph {
                        height: auto;
                    }

                    .board-list .list-symph:hover {
                        border-left: 0;
                    }

                        .board-list .list-symph span {
                            padding: 0;
                            font-size: 12px;
                            font-weight: 400;
                        }

                        .board-list .list-symph span {
                            background: none;
                            color: inherit;
                        }
                        .board-list .list-symph[data-role="list-like-count"] span:before,
                        .list-symph span:before {
                            font-family:'FontAwesome';
                            content: "\f087";
                            display: inline-block;
                            width: 16px;
                            height: 16px;
                            font-size: 12px;
                            line-height: 16px;
                        }
                        .list-row.ad-row .list-symph span:before,                        
                        .list-row.notice-row .list-symph span:before {
                            display: none;
                        }

                    .board-list .list-row.ad-row .list-symph {
                        border: 0;
