style.css:
@-moz-document domain("osu.ppy.sh") {
@font-face {
	font-family: Ubuntu;
	src: url('https://fonts.googleapis.com/css?family=Ubuntu');
}
* {
	font-family: 'Ubuntu', Calibri;
	font-weight: normal!important;
	border-radius: 0px!important;
}
strong {
	font-weight: bold!important;
}
/*[[TAGNAMEHERE]]*/

.mainbody2 #coolheader {
	background-image: url('/*[[wallpaperurl]]*/')!important;
}
.final_footer {
	background: url('/*[[wallpaperurl]]*/')!important
}
/* ----------Remove Stuff---------- */

#top_facebook {
	display: none;
}
#top_follow {
	display: none;
}
#top_support {
	display: none;
}
.mainbody2 #coolheader {
	width: 100%;
	z-index: 0;
	margin-top: 50px;
	height: 180px;
	background-size: cover;
	background-position: center;
}
.main-osu-logo {
	display: none;
}
.main-osu-logo-clickable {
	display: none;
}
#playstats {
	display: none;
}
.mainbody2 {
	background-image: none;
}
.sf-sub-indicator {
	display: none;
}
.bodytopbg {
	background: none;
}
#cse-search-box {
	display: none;
}
.footer {
	background-image: none;
}
.final_footer {
	background-position: center center!important;
	height: 100%;
	padding: 0px;
	position: fixed;
	top: 0;
	left: 0;
	z-index: -1;
	filter: blur(5px);
	transform: scale(1.1);
}
.footer_columns {
	display: none;
}
body {
	background: none;
}
/* --------------------Div Organization-------------------- */

.header {
	height: 230px;
}
.mainbody {
	width: 80%;
	width: 1000px;
}
.sectionContents br {
	display: none;
}
.blurb {
	background: rgba(0, 0, 0, 0.4);
	color: white;
	border: none;
	padding: 10px;
	margin-bottom: 10px;
}
.login-dropdown {
	background: rgba(0, 0, 0, 0.4);
	border: none;
	top: 50px;
	right: 0px;
	color: white;
	text-shadow: 1px 1px 3px black;
	height: 160px;
	text-align: center;
}
.login-dropdown input.btn {
	height: 30px;
	background: rgba(0, 0, 0, 0.4);
}
.login-dropdown .post {
	background: rgba(0, 0, 0, 0.4);
	height: 15px;
	padding: 5px;
}
/* --------------------General-------------------- */

input:focus {
	box-shadow: none;
}
a,
a:visited,
a:link,
a:active {
	color: white!important;
	text-shadow: 1px 1px 3px black!important;
	text-decoration: none!important;
}
li:active {
	background: none!important;
}
a:hover {
	color: white!important;
	text-shadow: 1px 1px 3px black!important;
	text-decoration: underline!important;
}
h1,
h2,
h3,
h4,
h5,
h6 {
	color: white;
	text-shadow: 1px 1px 3px black!important;
}
table {
	border-spacing: 0px;
	border-collapse: separate;
}
.row2 span,
.maintext span {
	color: white!important;
}
/* --------------------News-------------------- */
.content .blog-post {
	background: rgba(0, 0, 0, 0.4);
	color: white;
	text-shadow: 1px 1px 3px black;
	margin: auto;
}
.content .blog-post hr {
	background: white;
	box-shadow: none;
}
.content .blog-post h2 img {
	padding: 50px 0px 50px 0px;
	filter: brightness(300%) contrast(200%);
}
.content .blog-post img {
	max-width: 100%;
}
.main-left {
	width: 595px;
}
.main-right {
	width: 395px;
	margin-top: 45px;
}
.main-right .main-download img {
	width: 100%;
	height: auto;
}

.osu-timecode {
	background: rgba(0, 0, 0, 0.2);
	border: none;
}
.main-right .main-video iframe {
	width: 100%;
	height: 222px;
	margin-bottom: 10px;
}
.ntitle {
	line-height: 20px;
	margin: 0px 0px 10px 0px;
}
#most_played .topt {
	margin-right: 10px;
}
#most_played .row1,
#most_played .row2 {
	padding: 0px;
}
#most_played {
	margin-bottom: 10px;
}
#most_played tr td {
	padding: 10px;
}
#most_played tr:nth-child(1) {
	background: rgba(0, 0, 0, 0.2);
	padding: 5px;
	border: none;
	font-size: 15px;
}
#most_played tr:nth-child(even) {
	background: rgba(0, 0, 0, 0.3);
	color: white;
	text-shadow: 1px 1px 3px black;
}
#most_played tr:nth-child(odd) {
	background: rgba(0, 0, 0, 0.4);
	color: white;
	text-shadow: 1px 1px 3px black;
}
.news-heading {
	border: none;
	line-height: 15px;
}
.news-text {
	border: none;
	background: rgba(0, 0, 0, 0.4);
	border-radius: 0px;
	margin: 5px 0px 10px 0px;
	padding: 10px;
}
.news-text p {
	font-size: 12px;
}
.main-left .hug-bottom img {
	padding: 0px;
	width: 100%;
	height: auto;
}
.hug-bottom {
	margin: 0px;
	width: 100%;
	padding: 0px;
	position: static;
}
.hug-bottom h2 {
	margin: 0px 0px 10px 0px;
}
.hug-bottom .margin {
	border: none;
	margin: 0px;
	padding: 0px;
}
.hug-bottom #chat tr:nth-child(even) {
	background: rgba(0, 0, 0, 0.3);
	color: white;
	text-shadow: 1px 1px 3px black;
}
.hug-bottom #chat tr:nth-child(odd) {
	background: rgba(0, 0, 0, 0.4);
	color: white;
	text-shadow: 1px 1px 3px black;
}
.hug-bottom #chat tr td:nth-child(1) {
	line-height: 20px;
	text-align: center;
	color: white;
}
.hug-bottom #chat tr td:nth-child(2) {
	line-height: 20px;
	padding: 5px;
	font-size: 12px;
}
#chat table {
	padding: 0px;
	margin: 0px;
}
/* --------------------Main Menu-------------------- */

.header .menu * {
	box-shadow: none;
}
.header .menu {
	margin: 0px auto 0px auto;
	width: 100%;
	height: 190px;
}
.menu a,
.menu a:visited {
	color: white;
	text-shadow: 1px 1px 3px black;
}
.header .menu li {
	background: rgba(0, 0, 0, 0.4)!important;
	border-radius: 0px;
	margin: 10px 10px 0px 0px;
	font-size: 12px;
	height: 30px;
	min-width: 100px;
}
.header .menu li a {
	padding: 0px;
	margin: auto;
	line-height: 30px;
}
.header .menu .sf-menu li li {
	text-align: center;
	width: 100px;
	margin: 0px;
	padding: 0px;
}
.menu a:hover {
	background: rgba(0, 0, 0, 0.4);
	transition: 0.5s;
}
.menu a:active {
	background: rgba(0, 0, 0, 0.4);
}
.sf-menu ul {
	width: 100px;
}
.sf-menu li:hover ul,
.sf-menu li.sfHover ul {
	top: 30px;
	transition: 0.5s;
}
.sf-menu a:link {
	background: none!important;
}
/* --------------------User Bar-------------------- */

.mini-avatar {
	display: none;
}
.content-infoline {
	padding: 10px 0px 0px 0px;
	line-height: 30px;
    height: 30px;
}
.content-infoline .inline-form {
	margin: 0px;
}
#user-search,
#beatmap-search {
	border: 0px;
	background: rgba(0, 0, 0, 0.4);
	border-radius: 0px;
	padding: 0px 0px 0px 10px;
	height: 30px;
	text-align: left;
	font-size: 12px;
	width: 200px;
	margin: 0px 10px 0px 0px
}
input {
	color: white!important;
	text-shadow: 1px 1px 3px black!important;
	border-radius: 0px!important;
	border: none!important;
    background: rgba(0, 0, 0, 0.2);
	height: 15px;
    padding: 5px;
}
input::placeholder {
	color: white;
	text-shadow: 1px 1px 3px black;
}
.content-infoline div a {
	color: white;
	padding: 0px;
	font-size: 12px;
	text-shadow: 1px 1px 3px black;
}
.content-infoline div {
	color: white!important;
	text-shadow: 1px 1px 3px black;
	background: rgba(0, 0, 0, 0.4);
	padding: 0px 10px 0px 10px;
}
.content-infoline i {
	margin: 0px 5px 0px 5px;
	color: white;
	text-shadow: 1px 1px 3px black;
}
/* --------------------Left Side of Userpage-------------------- */

.content-with-bg {
	background: none;
	padding: 0px;
    margin-top: 10px;
}
.prof-left {
	width: 210px;
	padding: 0px;
	margin-right: 5px;
	background: none;
}
.prof-left .centrep,
.prof-left .profile-details,
.prof-left .playstyle-container {
	background: rgba(0, 0, 0, 0.4);
}
.frozen-header {
	margin-top: 10px;
}
.prof-left>.userbox {
	margin: 0px;
}

.avatar-holder {
	box-shadow: none;
	border: none;
	padding: 0px;
	margin: 10px;
	max-width: 190px!important;
	max-height: 190px!important;
}
.avatar-holder img {
	max-width: none!important;
	max-height: 190px!important;
	width: auto;
    height: 190px;
}
.profile-username {
	padding: 0px;
	color: white;
	font-size: 24px;
	text-shadow: 1px 1px 3px black;
}
.profile-details {
	padding: 10px;
}
.profile-details,
.profile-details i {
	color: white;
	text-shadow: 1px 1px 3px black;
}
.playstyle-container {
	border-radius: 0px;
	background: none;
	margin: 0px 0px 10px 0px;
}
.prof-left img {
	margin: 0px!important;
}
.prof-left #report-button {
	font-size: 12px;
	line-height: 30px;
}
.prof-left .icon-exclamation-sign {
	color: white!important;
	padding: 10px;
}
.prof-left .btn {
	background: none!important;
}
.prof-left .separator {
	display: none;
}
.playstyle-container {
	padding: 10px;
}
/* --------------------Right Side of Userpage-------------------- */

.prof-right {
	width: 780px;
	margin-left: 5px;
	background: none;
}
.prof-right .userpage {
	max-height: 1000px!important;
	margin: 0px 0px 10px 0px;
	padding: 10px;
	background: rgba(0, 0, 0, 0.4);
	color: white;
	text-shadow: 1px 1px 3px black;
}
.userpage .pack-msg {
	background: rgba(0, 0, 0, 0.1)!important;
	border: none;
	color: white;
	text-shadow: 1px 1px 3px black;
}
.userpage-expand {
	display: none;
}
.userpage-expand:hover {
	background: rgba(0, 0, 0, 0.6);
}
.spoiler {
	margin: 0px;
}
.spoiler_head {
	border: none;
	background: rgba(0, 0, 0, 0.1);
	padding: 5px;
	color: white;
	text-shadow: 1px 1px 3px black;
}
.spoiler .spoiler_head:hover {
	background-color: rgba(0, 0, 0, 0.2);
	z-index: 60;
}
.spoiler:hover .spoiler_head {
	background-color: rgba(0, 0, 0, 0.1);
	z-index: 50;
}
.spoiler_body {
	background: rgba(0, 0, 0, 0.2);
}
.profileGameModes {
	background: rgba(0, 0, 0, 0.0);
	height: 30px;
}
.profileGameModes div {
	height: 30px;
	margin: auto;
}
.profileGameModes a:hover {
	background: rgba(0, 0, 0, 0.6);
	transition: 0.5s;
}
.profileGameModes .active {
	background: rgba(0, 0, 0, 0.4);
	text-decoration: underline!important;
}
.profileGameModes a {
	margin: 0px 5px 0px 5px;
	background: rgba(0, 0, 0, 0.4);
	border: none;
	border-radius: 0px;
}
.profile-tabs {
	margin: 5px 0px 5px -10px;
	border-collapse: separate;
	table-layout: fixed;
	border-spacing: 10px 0px;
	width: 800px;
}
.profile-tabs td {
	background: rgba(0, 0, 0, 0.4);
	border: none;
	padding: 0px;
	border-radius: 0px;
	height: 30px;
	color: white;
	font-size: 12px;
	text-shadow: 1px 1px 3px black;
}
.profile-tabs td:hover {
	background: rgba(0, 0, 0, 0.6);
	transition: 0.5s;
}
.prof-right .sectionHeading {
	border: none;
	background: rgba(0, 0, 0, 0.4)!important;
	height: 30px;
	text-shadow: 1px 1px 3px black;
	padding: 0px 10px 0px 10px;
}
.prof-right .sectionHeading:hover {
	background: rgba(0, 0, 0, 0.6)!important;
	transition: 0.5s;
}
.prof-right .beatmapListing .nice tr {
	background: rgba(0, 0, 0, 0.4)!important;
	color: white;
	text-shadow: 1px 1px 3px black;
}
.prof-right .beatmapListing .nice td {
	padding: 5px;
	text-align: center;
	background: none!important;
}
.prof-right .beatmapListing .nice th {
	padding: 10px;
	font-size: 12px;
	background: none;
	border: none;
	color: white;
	text-shadow: 1px 1px 3px black;
}
.beatmapListing tr td div[style="text-align: center; color: red; padding: 5px;"] {
	color: white!important;
	text-shadow: 1px 1px 3px black;
	background: rgba(0, 0, 0, 0.4);
	padding: 10px!important;
}
.prof-right .sectionContents {
	background: rgba(0, 0, 0, 0.4);
	padding: 0px;
	color: white;
	text-shadow: 1px 1px 3px black;
}
.prof-right .beatmapListing {
	border-spacing: 0px 5px;
}
.prof-right .profileStatLine {
	background: none;
	color: white;
	text-shadow: 1px 1px 3px black;
	margin: 10px 0px 10px 0px;
}
.prof-right .profileStatHeader {
	background: none;
	color: white;
	margin: 10px 0px 10px 0px;
	text-shadow: 1px 1px 3px black;
}
.prof-right .prof-beatmap {
	background: none;
	border: none;
	margin: 0px;
	padding: 5px;
}
.prof-right .nv-legend-symbol {
	fill: white!important;
	stroke: white!important;
}
#beatmaps.expanded {
	margin-top: 10px;
}
.achievements-break {
	border: none;
	margin: 10px 0px 10px 0px;
}
.prof-right .nv-series .nv-legend-text text {
	color: white!important;
}
.nv-group.nv-series-0 {
	stroke: white!important;
	fill: white!important;
}
#chart1 {
	fill: white;
}
#chart1 .tick line {
	display: none;
}
#chart1 rect {
	stroke: white;
	fill: rgba(0, 0, 0, 0.2);
	opacity: 1!important;
}
.sectionContents .expanded {
	padding: 0px 10px 10px 10px;
}
.sectionContents h2 {
	font-size: 15px!important;
	margin: 10px 0px 10px 0px;
	color: white;
	text-shadow: 1px 1px 3px black;
	padding: 0px;
}
.prof-right .profileStatLine,
.prof-right .profileStatHeader {
	border: none;
}
.levelMetre {
	border: none;
	box-shadow: 1px 1px 3px black;
}
.levelMetre td {
	border: none;
	background-color: transparent!important;
}
td.levelPercent {
	background-color: white!important;
	border-right: none;
	box-shadow: none;
	text-indent: -30px;
	text-align: left;
	text-shadow: 1px 1px 3px black;
	position: relative;
}
.prof-beatmap .pp-display,
.prof-beatmap .pp-display-weight {
	color: white;
	text-shadow: 1px 1px 3px black;
}
.profileBeatmapHeader {
	margin: 10px 0px 0px 0px;
	border: none;
}
.profileBeatmapHeader:hover {
	background: rgba(0, 0, 0, 0.2);
	color: white;
	transition: 0.5s;
}
#achievements .clear {
	display: none;
}
#achievements .achievement {
	margin: 0px 5px 5px 5px;
	width: 140px;
}
#general .nvtooltip {
	background: rgba(0, 0, 0, 0.2);
	border: none;
}
#general .nvtooltip h3 {
	background: none;
	border: none;
}
#kudos br {
	display: block;
}

/* --------------------Performance Rankings-------------------- */

.content .paddingboth {
	padding: 0px;
}
.content .content-with-bg h1 {
	text-align: center;
	padding: 0px;
	margin: 10px;
}
.content .content-with-bg .paddingboth .pagination {
	padding: 0px;
	margin: 10px 0px 10px 0px;
	color: white;
	text-shadow: 1px 1px 3px black;
	font-size: 12px;
}
.content .content-with-bg .pagination {
	padding: 0px;
	margin: 10px 0px 10px 0px;
	color: white;
	text-shadow: 1px 1px 3px black;
	font-size: 12px;
}
.content .content-with-bg .paddingboth .pagination a,
.content .content-with-bg .paddingboth .pagination b {
	margin: 1px;
}
.content .content-with-bg .paddingboth center img {
	margin: 0px 0px 0px 0px;
}
.content .content-with-bg .paddingboth form {
	color: white;
	text-shadow: 1px 1px 3px black;
	font-size: 12px;
	line-height: 12px;
	margin: 10px 0px 10px 0px;
}
.content .content-with-bg .paddingboth #f {
	margin: 0px;
}
#tablist {
	margin: 0px;
	border: none;
	background: none;
	float: left;
	width: 100%;
	overflow: hidden;
	position: relative;
	padding-bottom: 10px;
	padding: 0px;
	height: auto;
}
#tablist ul {
	float: left;
	margin: 0;
	padding: 0;
	position: relative;
	left: 50%;
}
#tablist ul li {
	float: left;
	border: none;
	margin: 0px 10px 10px 0px;
	position: relative;
	right: 50%;
	height: 30px;
	width: auto;
	text-align: center;
}
#tablist a {
	background: rgba(0, 0, 0, 0.4);
	font-size: 15px;
	height: 30px;
	line-height: 30px;
}
#tablist a:hover {
	background: rgba(0, 0, 0, 0.6);
	transition: 0.5s;
}
#tablist a.active {
	background: rgba(0, 0, 0, 0.4)!important;
	text-decoration: underline!important;
}
.content .content-with-bg .paddingboth .beatmapListing {
	text-align: center;
	font-size: 12px;
	line-height: 20px;
	color: white;
	text-shadow: 1px 1px 3px black;
}
.content .content-with-bg .paddingboth .beatmapListing th {
	background: none;
	padding: 5px;
	border: none;
	font-size: 15px;
}
.content .content-with-bg .paddingboth .beatmapListing tr:nth-child(even) {
	background: rgba(0, 0, 0, 0.3);
	color: white;
	text-shadow: 1px 1px 3px black;
}
.content .content-with-bg .paddingboth .beatmapListing tr:nth-child(odd) {
	background: rgba(0, 0, 0, 0.4);
	color: white;
	text-shadow: 1px 1px 3px black;
}
.content .content-with-bg .paddingboth .beatmapListing td:nth-child(2) {
	text-align: left!important;
	text-indent: 60px;
}
.content .content-with-bg .paddingboth .beatmapListing th:nth-child(2) {
	width: auto;
}
.content .content-with-bg .paddingboth .beatmapListing td:nth-child(3) {
	text-align: left!important;
	text-indent: 60px;
}
.centrep {
	padding: 0px;
}
.content .content-with-bg .paddingboth {
	color: white;
	text-shadow: 1px 1px 3px black;
}
/* --------------------Supporting osu-------------------- */

.dialog-area {
	background: rgba(0, 0, 0, 0.4)!important;
	margin: 0px;
}
.dialog-area .icon-heart {
	color: white!important;
}
.dialog-area #progressbar {
	color: rgba(0, 0, 0, 0.4)!important;
	border: none;
}
.dialog-area .ui-widget-header {
	background: white!important;
	padding: 0px;
	margin: 0px;
}
/* --------------------Download-------------------- */

.centrep .download {
	border: none;
	background: rgba(0, 0, 0, 0.4);
	padding: 10px;
}
.centrep .download:hover {
	background: rgba(0, 0, 0, 0.6);
	box-shadow: none;
}
.centrep .download:active {
	background: rgba(0, 0, 0, 0.6);
	box-shadow: none;
	border: none;
}
.centrep .download>div {
	filter: grayscale(1.0);
}










