style.css:
@-moz-document domain("tweakers.net") {
body,
#contentArea,
.notificationsContainer,
#searchbar,
.darkFpBlock,
.userReviewDescription,
#categoryBrowser,
.mainlist,
.sublist,
#entity,
.pageTabsContainer,
#filter {
	background-color: #222 !important;
	color: #EEE;
}

#categoryBar li a,
.fpaItem a,
#news td.title > a,
a.newsSubmit,
#categoryBar li.more a,
#recentlyTested .title a,
#videos ul a,
.title a,
a.readMore,
.userInfo a,
#categoryBrowser li a {
	color: #FFF !important;
}

.darkFpBlock,
.userReviewDescription {
	background-image: none !important;
}

.fpaMeta a {
	color: #222;
}
}
