* {
    border: none!important;
    border-radius: 0px!important
}
/* Upper navbar */

.rbx-header .rbx-navbar li .nav-menu-title {
    transition: 0.5s ease
}
.rbx-header .rbx-navbar li .nav-menu-title:hover,
.rbx-header .rbx-navbar li .nav-menu-title:focus {
    transition: 0.5s ease
}
/* Getting rid of those pesky ads (DELETE HERE) */
a.ad img {
    display: none
}
.abp .ad-identification-text {
    display: none
}
.abp .UpsellAdButton,
.abp .UpsellAdButton:link,
.abp .UpsellAdButton:visited,
.abp .BadAdButton,
.abp .BadAdButton:link,
.abp .BadAdButton:visited {
    display: none
}
.catalog-container {
    margin-top: 25px
}
/* DO NOT DELETE PAST THAT POINT */
/* Dropdown tab */

.dropdown-menu li {
    transition: 0.5s ease
}
.dropdown-menu li:hover {
    transition: 0.5s ease
}
/* Chat Area */

.chat-main {
    transition: 0.5s ease;
    box-shadow: none
}
.dialog-container {
    transition: 0.5s ease;
    box-shadow: none
}
.dialog-container.dialog-visible {
    transition: 0.5s ease
}
.chat-friend-list .chat-friend-container {
    transition: 0.5s ease
}
.chat-friend-list .chat-friend-container:hover {
    transition: 0.5s ease
}
.dialog-container .dialog-message-container .dialog-message {
    box-shadow: none
}
/* Chat buttons */

.btn-control-md {
    transition: 0.5s ease;
    background-color: #00A2FF;
    color: white;
}
.btn-control-md:hover,
.btn-control-md:focus {
    transition: 0.5s ease;
    background-color: #32B5FF;
    box-shadow: 0 4px 0 0 #00A2FF;
    color: white
}
/* Game Card */

.game-card-footer {
    display: none!important
}
.vote-counts {
    display: none!important
}
.game-card-container {
    transition: 0.5s ease;
    box-shadow: none
}
.game-card-container:hover {
    transition: 0.5s ease;
    box-shadow: 0 -4px 0 0 #00A2FF inset;
    background-color: #eee
}
/* Section Content */

.section-content {
    box-shadow: none
}
.stack .stack-list .stack-row {
    box-shadow: none
}
/* Nav Things */

.rbx-divider {
    display: none
}
.rbx-left-col .rbx-scrollbar a,
.rbx-left-col .rbx-scrollbar a:hover,
.rbx-left-col .rbx-scrollbar a:focus {
    transition: 0.5s ease
}
#nav-home:hover,
#nav-inventory:hover {
    transition: 0.5s ease;
    background-color: #eee
}
#nav-profile:hover,
#nav-character:hover {
    transition: 0.5s ease;
    background-color: #eee
}
#nav-message:hover,
#nav-friends:hover,
#nav-trade:hover,
#nav-group:hover,
#nav-forum:hover,
#nav-blog:hover,
#nav-shop:hover {
