style.css:
@-moz-document domain("archiveofourown.org")
{

/* Hides scrollbar */
::-webkit-scrollbar {
    display: none;
}

/* Number of chapters */
dd.chapters {
    filter: blur(10px);
    transition-duration: .3s;
    transition-property: -webkit-filter;
}

dd.chapters:hover, .spoiler:focus {
    filter: blur(0px);
}

/* Date published */
dd.published {
    filter: blur(10px);
    transition-duration: .3s;
    transition-property: -webkit-filter;
}

dd.published:hover, .spoiler:focus {
    filter: blur(0px);
}

/* Date completed */
dd.status {
    filter: blur(10px);
    transition-duration: .3s;
    transition-property: -webkit-filter;
}

dd.status:hover, .spoiler:focus {
    filter: blur(0px);
}

/* Word count */
dd.words {
    filter: blur(10px);
    transition-duration: .3s;
    transition-property: -webkit-filter;
}

dd.words:hover, .spoiler:focus {
    filter: blur(0px);
}

/* Author notes */
div.notes.module {
    filter: blur(10px);
    transition-duration: .3s;
    transition-property: -webkit-filter;
}

div.notes.module:hover, .spoiler:focus {
    filter: blur(0px);
}

/* Chapter number */
h3.title a {
    filter: blur(10px);
    transition-duration: .3s;
    transition-property: -webkit-filter;
}

h3.title a:hover, .spoiler:focus {
    filter: blur(0px);
}

/* Rating on story page */
dd.rating.tags {
    filter: opacity(0%);
    transition-duration: .3s;
    transition-property: -webkit-filter;
}

dd.rating.tags:hover, .spoiler:focus {
    filter: opacity(100%);
}

/* Rating icons */
span.rating-explicit.rating {
    filter: opacity(0%);
    transition-duration: .3s;
    transition-property: -webkit-filter;
}

span.rating-explicit.rating:hover, .spoiler:focus {
    filter: opacity(100%);
}

span.rating-general-audience.rating {
    filter: opacity(0%);
    transition-duration: .3s;
    transition-property: -webkit-filter;
}

span.rating-general-audience.rating:hover, .spoiler:focus {
    filter: opacity(100%);
}

span.rating-mature.rating {
    filter: opacity(0%);
    transition-duration: .3s;
    transition-property: -webkit-filter;
}

span.rating-mature.rating:hover, .spoiler:focus {
    filter: opacity(100%);
}

span.rating-notrated.rating {
    filter: opacity(0%);
    transition-duration: .3s;
    transition-property: -webkit-filter;
}

span.rating-notrated.rating:hover, .spoiler:focus {
    filter: opacity(100%);
}

span.rating-teen.rating {
    filter: opacity(0%);
    transition-duration: .3s;
    transition-property: -webkit-filter;
}

span.rating-teen.rating:hover, .spoiler:focus {
    filter: opacity(100%);
}
  }
