style.css:
@-moz-document domain("basecamp.com") {
/** Disable completed Todos. **/

.recordable--todoset ul.todos.completed {
    display: none
}
.todos.remaining .todo .checkbox__content.unassigned {
    margin-right: 0;
    padding-right: 0;
}
.todos.remaining .todo .checkbox__content {
    display: flex;
    border-radius: 4px;
}
.todos.remaining .todo .checkbox__content > a {
    flex-grow: 1;
}
.todos.remaining .todo .checkbox__content > a.todo-description-icon,
.todos.remaining .todo .checkbox__content > a.todo_comments {
    flex-grow: 0;
    white-space: nowrap;
}
.todos.remaining .todo {
    border: 1px solid transparent;
    border-radius: 4px;
    padding: 0 1em;
    width: 100%;
}
.todos.remaining .todo:hover {
    background: #E3F2FD;
    border-color: #90caf9;
}
/** Expand main views **/

.panel {
    max-width: none;
    padding: 0 4rem;
}
.perma-toolbar {
    right: 5rem;
}
}
