style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://gym-k.com/shift.html"){
 /*dummy*/
}

@-moz-document regexp("http:\/\/gym-k\.com\/shift(?:\.html)?")
{
  /*-----------------シフト表-----------------*/
  :root
  {
    --shift: "7月のシフト\A\A完売";
    --star: "★";
  }

  #p_shun > td:nth-child(2),
  #p_shun > td:nth-child(3),
  #p_shun > td:nth-child(4),
  #p_shun > td:nth-child(5),
  #p_shun > td:nth-child(6),
  #p_shun > td:nth-child(7),
  #p_shun > td:nth-child(8){
    font-size: 0px !important;
  }

  #p_shun > td:nth-child(2)::before,
  #p_shun > td:nth-child(3)::before,
  #p_shun > td:nth-child(4)::before,
  #p_shun > td:nth-child(5)::before,
  #p_shun > td:nth-child(6)::before,
  #p_shun > td:nth-child(7)::before,
  #p_shun > td:nth-child(8)::before{
    font-size: 12.0833px !important;
    font-weight: bold !important;
    white-space: pre !important;
  }

  #p_shun > td:nth-child(2)::before,
  #p_shun > td:nth-child(4)::before{
    color: rgb(238,68,68) !important;
    content: var(--shift);
  }

  #p_shun > td:nth-child(3)::before,
  #p_shun > td:nth-child(5)::before,
  #p_shun > td:nth-child(6)::before,
  #p_shun > td:nth-child(7)::before,
  #p_shun > td:nth-child(8)::before{
    /*color: rgb(51,51,51) !important;*/
    color: rgb(238,68,68) !important;
    content: var(--star);
  }
}
