style.css:
@-moz-document domain("synowhisper.herokuapp.com") {
body {
	background-color: #272822;
	color: #AAA;
	font-size: 11px;
}
h1 {
	font-size: 12px;
	font-weight: bold;
	display: none;
}
p {
	display: none;
}

form {
	width: 100% !important;
	padding: 0;
	margin: 0;
}

select,
input.form-control,
textarea.form-control {
	background-color: #272822;
	color: #FFF;
	border: none;
	line-height: 1em;
}

select {
	height: 2em;
}

.btn-primary {
	width: 100%;
	background-color: #444;
	border-color: #222;
}

form > div.col-sm-6:nth-child(2) {
	opacity: 0;
	transition: 0.3s;
}
form > div.col-sm-6:nth-child(2):hover {
	opacity: 0.3;
	transition: 3s;
}

.nav-tabs>li.active>a, 
.nav-tabs>li.active>a:hover, 
.nav-tabs>li.active>a:focus,
.nav>li>a:hover, 
.nav>li>a:focus {
	background-color: transparent;
	border: 0px;
}
.nav-tabs>li>a {
	border-radius: 3px;
}
.nav-tabs {
	border-bottom: 0px;
}
}
