  }

  pinned-cheers--padding
  {
    background: #171717 !important;
  }

  .notification--unread
  {
    background-color: #17171f !important;
  }

  .notification--unread:hover
  {
    background-color: #17172f !important;
  }

  .sc-item__followed-channel .sc-item__metadata--1
  {
    max-width: 12rem !important;
    min-width: 12rem !important;
  }

  .mg-l-1
  {
    margin-left: .5rem !important;
  }

  .sc-live-indicator
  {
    height: .8rem !important;
    width: .8rem !important;
    border-radius: .4rem;
    background-color: #fc3636 !important;
    display: block;
    margin: 0px 0px -11px -2px !important;
  }

  #left_col
  {
    border-right: 1px solid !important;
  }

  .cn-bar
  {
    box-shadow: inset 0 -1px 0 #433f4a,0 -1px 1px rgba(0,0,0,.065) !important;
  }

  chat-container
  {
    border-left: 1px solid #433f4a !important;
    ;
  }

  .brick--theme-white, .brick--block, .brick--faint
  {
    background-color: #171717 !important;
    border-color: #6441A4 !important;
  }

  .warp__item--anchor-beta
  {
    background: #171717 !important;
  }

  .warp__item
  {
    color: #6441a4 !important;
  }

  .notification-center-balloon
  {
    width: 33rem;
    height: 30rem !important;
    position: absolute;
    bottom: 2rem;
  }

  .notification-center__header
  {
    background: #171717 !important;
  }

  .notification--read:hover
  {
    background-color: #101010 !important;
  }

  .notification--read
  {
    background-color: #171717 !important;
  }

  .notification-center__core-end
  {
    background: #171717 !important;
    box-shadow: inset 0 0 0 0 !important;
  }

  .notification-center__footer
  {
    background-color: #171717;
  }
    /* Main view background */
  #main_col, #dash_main
  {
    background: #171717 !important;
  }

  .main, .fullwidth_main,#site_header,.ember-chat .chat-room-list
  {
    background: #171717 !important;
  }

  .dropmenu, .ui-menu, .ui-multiselect-menu
  {
    background: #171717 !important;
  }
     /* Watch Last Broadcast background */
  .upsell-banner
  {
    background-color: #171717 !important;
  }

  .ember-chat .chat-hidden-overlay
  {
    background: #171717 !important;
  }

  .cn-bar
  {
    background: #171717 !important;
  }

  .cn-bar__displayname, .cn-tabs__item > a
  {
    color: #FFF !important;
  }

  .card__title
  {
    color: #FFF !important;
  }

  #channel .player-column #broadcast-meta .info .title:hover,#channel .player-column #broadcast-meta .info .title
