  {
    color: #FFF !important;
  }

  .following-col .following-list .channel .game,.following-col .following-list .channel .viewers
  {
    color: #9056FF !important;
  }
    /* Main header background */
  #main_col .content #broadcast_meta, #main_col #broadcast-meta, #main_col .archive_info
  {
    background-color: #171717 !important;
    color: #FFF !important;
  }
    
    /* Following lines */
  #main_col ul.nav.js-nav a.active
  {
    color: #FFF !important;
  }

  ul.tabs::before, .directory_header .nav::before, ul.tabs_fake::before
  {
  }

  ul.tabs li > a, .directory_header .nav li > a, ul.tabs li > button, .directory_header .nav li > button, ul.tabs_fake li > a, ul.tabs_fake li > button
  {
    color: #9056FF !important;
  }

  ul.tabs li > a:hover, .directory_header .nav li > a:hover, ul.tabs li > button:hover, .directory_header .nav li > button:hover, ul.tabs_fake li > a:hover, ul.tabs_fake li > button:hover
  {
    color: #FFF !important;
  }
    /* Follow button */
  .notification-controls .toggle-notification-menu
  {
    background-color: #6441A4  !important;
  }

  .follow-button:not(.ember-follow) .follow
  {
    background: #9056FF !important;
  }

  .collectionWrapper,.titleBar
  {
    background: #171717 !important;
  }

  .resultView__titlesep
  {
    color: #fff !important;
    background: #171717 !important;
  }

  .resultView__titlesep .resultView__titleMore
  {
    color: #FFF !important;
  }
    /*Menu*/
  .player-menu__menu
  {
    color: #222 !important;
    background: #171717 !important;
  }
    /* Main stream stats background */
  .channel-stats
  {
    background-color: #171717 !important;
  }

  .conversations-content .conversations-list-bottom-bar,.conversations-list .conversations-list-header
  {
    background: #1F1F1F !important;
    color: #FFF !important;
  }
    /* Main description background */
  .activity-container .activity-card > div
  {
    background-color: #1F1F1F !important;
  }

  .activity-container .activity-card > div .activity-meta::before, .activity-container .activity-card > div .activity-react a, .activity-container .activity-card > div .activity-options .balloon, .activity-container .activity-card > div .activity-options .balloon::after
  {
    background-color: #3A3A3A !important;
  }

  .activity-container .activity-card > div .activity-meta
  {
    box-shadow: inset 0 -1px 0 #6D6D6D !important;
  }

  .ember-application ul.tabs li.selected a
  {
  }

  .js-panel-content
  {
    background-color: #1F1F1F !important;
  }

  .balloon
  {
    color: #b0b0b0 !important;
    background-color: #171717 !important;
    box-shadow: 0 0 0 1px #433f4a,0 1px 1px rgba(0,0,0,.25) !important;
  }

  .player-menu__section
  {
    background: #171717 !important;
  }

  .player-menu__header
  {
    color: #FFF !important;
  }
    /* Left header 4F4BFF  */
  .warp
  {
    background-color: #171717 !important;
  }

  .warp__item > a:hover
  {
    background: #4F4BFF !important;
  }
    /* Search */
  .leaf
  {
    background-color: #FFF !important;
    box-shadow: #FFF 0 0 0 1px inset !important;
  }
    /* Following, Games, Channels, Videos selected background */
  #left_col .warp .warp__item--toggled > a
  {
    background: #6441a4  !important;
  }
    /* More at Twitch line */
  .warp__item--header::before
  {
    background-color: #171717 !important;
  }
    
    /* Left footer */
  .warp__item--anchor
  {
    background: #171717 !important;
  }
    /* Drawer */
  #left_col .warp .warp__drawer
  {
    background: #171717 !important;
    box-shadow: 0 -1px 0 #FFF !important;
  }

  .button__status--primary
  {
  }
    /* Purple buttons */
  #right_col #channel .channel-actions .button
  {
    background-color: #171717 !important;
    box-shadow: 0px 0px 0px #000 !important;
  }

  .chat-buttons-container .button
  {
    background-color: #171717 !important;
    box-shadow: 0px 0px 0px #000 !important;
  }

  .chat-buttons-container .button.primary
  {
    background-color: #171717 !important;
  }

  .tabs--fullwidth li:not(.selected) a:hover
  {
    background: transparent !important;
  }

  .button figure svg, .button figure img
  {
    fill: #FFF !important;
  }

    /* Games right col */
  #right_col
  {
    background-color: #171717 !important;
  }

