  {
    top: 0px !important;
  }

  #right_col .top .tabs li.selected a
  {
  }

  ul.tabs li > a
  {
  }

  .panel-formatting .panel
  {
    list-style-position: outside !important;
    color: #ededed !important;
    text-align: left;
  }
    /* Chat header */
  .ember-chat .chat-header
  {
    background-color: #171717 !important;
    color: #FFF !important;
  }
    
    /* Chat footer */
  .ember-chat .chat-interface
  {
    background-color: #171717 !important;
    color: #FFF !important;
  }
    
    /* Send a message field */
  .ember-chat .chat-interface textarea
  {
    background-color: #FFF !important;
  }
    
    /* Chat cleanup */
  #chat_line_list .line, .chat-messages .chat-line
  {
    background-color: #101010 !important;
    color: #FFF !important;
    line-height: 20px !important;
  }

  #chat_line_list .line:nth-child(2n), .chat-messages .chat-line:nth-child(2n)
  {
    background-color: #171717 !important;
    color: #FFF !important;
  }

  .ember-chat .chat-messages
  {
    background: #171717 !important;
  }
    
    /* global moderator chat badge */
  .ember-chat .chatters-view .chatters-container #viewers
  {
    background: #171717 !important;
  }

  .ember-chat .chatters-view .chatters-container .chatters .list-header
  {
    color: #FFF !important;
  }

  .ember-chat .badges .global-moderator
  {
    background-color: #34AE0A !important;
  }
    /* twitch staff chat badge */
  .ember-chat .badges .staff
  {
    background-color: #6441A5 !important;
  }
    /* bot chat badge */
  .line .bot, .ember-chat .badges .bot
  {
    background: url('data:image/png;base64,')  repeat scroll 0% 0% #444 !important;
  }
    /* betterttv supporter chat badge */
  .line .bttvSupporter, .ember-chat .badges .bttvSupporter
  {
    background: url('data:image/png;base64,') repeat scroll 0% 0% #444 !important;
    display: inline-block !important;
    vertical-align: middle !important;
  }
    /* betterttv developer chat badge */
  .line .bttv-developer, .ember-chat .badges .bttv-developer
  {
    background: url('data:image/png;base64,') repeat scroll 0% 0% #F00 !important;
    display: inline-block !important;
    vertical-align: middle !important;
  }
    /* betterttv emote approver */
  .ember-chat .badges .bttv-emotes
  {
    background: url('data:image/png;base64,') repeat scroll 0% 0% #444 !important;
    display: inline-block !important;
    vertical-align: middle !important;
  }
    /* grandfathered betterttv swag tag */
  .ember-chat .badges .oldbroadcaster
  {
    background-color: #444 !important;
    display: inline-block !important;
    vertical-align: middle !important;
    text-align: center !important;
  }
    /* grandfathered betterttv swag tag, bacon */
  .ember-chat .badges .bacon
  {
    background: url("data:image/png;base64,") repeat scroll 0% 0% #444 !important;
    display: inline-block !important;
    vertical-align: middle !important;
  }

  .conversations-list
  {
    border: 0px solid #dad8de !important;
    background: #1F1F1F !important;
  }

  .conversations-content .conversations-list-bottom-bar
  {
    border: 0px solid #dad8de !important;
  }

  .conversations-list-container.no-conversations .conversations-blank-slate
  {
    display: block !important;
    background: #1f1f1f !important;
  }

  .conversations-list .scroll-container
  {
    background: #1F1F1F !important;
  }

  .conversations-content .conversations-list-search-bar input
  {
    background: url(https://web-cdn.ttvnw.net/images/xarth/left_col_search_icon.svg) no-repeat 8px center #1F1F1F !important;
    width: 235px !important;
    margin-left: 1px !important;
    padding-left: 25px !important;
    margin-top: 11px !important;
  }

  .conversation-window .convoHeader
  {
    height: 42px;
    background: #1F1F1F !important;
    box-shadow: inset 0 0 0 1px #dad8de,0 2px 0 rgba(0,0,0,.05) !important;
    font-weight: 700;
    position: relative;
    cursor: pointer;
  }

  .conversation-window .conversation-content
  {
    height: 195px;
    overflow-y: auto;
    transition: all ease-in-out 75ms;
    background: #1F1F1F !important;
  }

  .conversation-window
  {
    background: #1F1F1F !important;
    border-bottom: none;
    float: right;
    width: 300px;
    position: relative;
    margin-right: 10px;
    pointer-events: auto;
    height: 282px;
    bottom: 239px;
    box-shadow: inset 0 0 0 1px #efeef1,0 15px 45px 0 rgba(0,0,0,.19) !important;
    overflow: hidden;
    transition: all ease-in-out 75ms;
  }
    /* remove betterttv logo */
  img[id="bttv_logo"]
  {
    display: none !important;
  }
