.main-login {
    background: #2f3d51;
    color: #d2d5d9;
}
.main-login .header .header__link {
    color: #e0e2e5;
}
.main-login .thumbnail {
    border: 1px solid #4e5e75;
}
.main-login .content__left__box.content__left__box--topics .content__left__box__topic {
    border-bottom: transparent;
    color: #d2d5d9;
    background: #212c3b;
}
.main-login .content__left__box h3 {
    color: #d2d5d9;
    border-bottom: 1px solid #2f3d51;
    background: #212c3b;
    border-radius: 5px;
}
.main-login .content__left__box {
    background: #212c3b;
    border: transparent;
    border-radius: 5px;
}
.main-login .content .content__right .content__right__box {
    background: transparent;
    border: transparent;
    border-radius: 5px;
}
.main-login .post .post__score .post__score__score .active-score.up-score {
    color: #d2d5d9;
}
.main-login .post .post__meta .post__meta__name .post__meta__name__full {
    font-weight: bold;
    color: #d2d5d9;
}
.main-login .post .post__meta .post__meta__actions .post__meta__actions__link {
    color: #c9cdd0;
}
.main-login .post .post__meta .post__meta__body {
    color: #dedfe1;
}
.main-login .content .content__right .content__right__box h3 {
    color: #e6e6e6;
    border-bottom: 1px solid #2f3d51;
    background: #212c3b;
}
.main-login .verified-badge {
    background: transparent;
}
