style.css:
@-moz-document domain("iciba.com") {
.nav,
.list-item,
.daily-box,
.goto-top,
.search-fix-bg,
.search-suggest,
.info-article,
.article,
.hot_recomment,
.words,
.back-top {
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.26);
}
.search-box {
    box-shadow: 0 6px 10px 0 rgba(0, 0, 0, 0.14), 0 1px 18px 0 rgba(0, 0, 0, 0.12), 0 3px 5px -1px rgba(0, 0, 0, 0.4);
}
.search-box:focus,
.list-item:focus,
.daily-box:focus,
.hot-item:focus,
.goto-top:focus,
.back-top:focus {
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, 0.14), 0 1px 10px 0 rgba(0, 0, 0, 0.12), 0 2px 4px -1px rgba(0, 0, 0, 0.4);
}
.nav {
    border-bottom: 2px solid transparent;
}
.nav-item {
    color: #000000 !important;
    background-color: transparent !important;
    border-bottom: 2px solid #1596f5;
}
.download,
.search-ad,
.adsbygoogle,
.container-right-ad-box,
.baidu_ad,
.banner_download {
    display: none !important;
}
.search-history {
    height: 35px;
    line-height: 45px;
    background-color: transparent !important;
    border: none !important;
}
.position {
    font-size: 0.9em !important;
}
}
