@-moz-document domain("welt.de") {
/* ==UserStyle==
@name         WELT.DE DARK THEME 2019
@namespace    de.sidneys.userstyles
@version      2.1.2
@description  A modern, dark theme for WELT ONLINE (Axel Springer Verlag).
@author       sidneys <sidneys.github.io@outlook.com> (https://github.com/sidneys)
@homepageURL  https://userstyles.org/users/301302
@supportURL   https://forum.userstyles.org/messages/add/sidneys
@license      MIT
@preprocessor default
==/UserStyle== */
}

@-moz-document domain("welt.de") {
/**
     ==========================================================
     VARIABLES
     ==========================================================
     */

    /**
     * :root
     */
    :root
    {
        --color-border-1: rgb(40, 39, 36);
        --color-border-2: rgb(80, 78, 73);
        --color-border-3: rgb(116, 113, 106);
        --color-border-4: rgb(166, 166, 166);
        --color-container-1: rgb(0, 0, 0);
        --color-container-1-translucent-30: rgba(0, 0, 0, 0.30);
        --color-container-1-translucent-50: rgba(0, 0, 0, 0.50);
        --color-container-1-translucent-60: rgba(0, 0, 0, 0.60);
        --color-container-1-translucent-85: rgba(0, 0, 0, 0.85);
        --color-container-2: rgb(40, 40, 40);
        --color-container-3: rgb(49, 49, 49);
        --color-container-4: rgb(62, 62, 62);
        --color-text-1: rgb(0, 0, 0);
        --color-text-2: rgb(191, 191, 191);
        --color-text-3: rgb(210, 210, 210);
        --color-text-4: rgb(255, 255, 255);
        --color-accent-blue-1: rgb(0, 58, 90);
        --color-accent-blue-2: rgb(36, 110, 167);
        --color-accent-blue-3: rgb(87, 162, 219);
        --color-accent-red-1: rgb(220, 0, 0);
    }
}

@-moz-document domain("welt.de") {
/* GLOBAL
   ==================================== */

/* Ads
   ------------------------------------ */

.c-ad__billboard,
.c-inline-element--has-commercials
{
    display: none;
}

/* Font Smoothing
   ------------------------------------ */

.c-article-text,
.ffmark-400-normal .o-text,
#Comments div,
.o-teaser__footer-content-list-item
{
    -webkit-font-smoothing: subpixel-antialiased;
    -moz-osx-font-smoothing: grayscale;
}

.ffmark-800-normal .o-headline,
.c-teaser-inline__headline-link,
.c-author,
.c-author__link,
.c-publish-date,
.c-readingtime
{
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

/* Typography
   ------------------------------------ */

.c-channel-header__label,
.o-headline,
.o-headline-light,
.o-headline-ultra-light
{
    color: var(--color-text-4);
}

.c-author,
.c-author__link,
.c-publish-date,
.c-readingtime
{
    color: var(--color-text-3);
}

.o-text,
.c-article-text,
.c-article-text__drop-cap,
.c-article-text > h2,
.c-article-text > h3,
.c-article-text > p a
{
    color: var(--color-text-4);
}

.c-article-text > p a
{
    border-color: var(--color-text-3);
}

.c-section
{
	color: var(--color-accent-blue-2);
}

.c-topic
{
	color: var(--color-accent-blue-3);
}

/* Box Containers
   ------------------------------------ */

.c-browser-container,
.c-page-container--has-commercials
{
    background-color: var(--color-container-4);
}

.c-page-container,
.c-channel-navigation,
.c-stage--has-theme::before
{
    background-color: var(--color-container-3);
}

.c-stage--has-theme.c-stage--is-light,
.c-themepages
{
    background-color: var(--color-border-1);
}

.c-video-sidebar-container
{
    background-color: var(--color-container-2);
}

/* Navigation
   ------------------------------------ */

.c-inline-navigation__link,
.o-section,
.o-topic
{
    color: var(--color-accent-blue-2);
    filter: brightness(150%);
}

.c-breadcrumb,
.c-breadcrumb__element
{
    color: var(--color-text-3);
}

.c-channel-navigation.c-inline-navigation,
.c-inline-navigation.c-stage-header__navigation
{
    background-color: var(--color-container-3);
}

/* Dialogues / Modals
   ------------------------------------ */

.c-dialog__is-white
{
    background-color: var(--color-container-1-translucent-60);
    border-color: transparent;
    -webkit-backdrop-filter: blur(5px);
    backdrop-filter: blur(5px);
}

.c-dialog__content h1,
.c-dialog__content h2,
.c-dialog__content h3
{
    color: var(--color-text-4) !important;
}

.c-follow-list__heading
{
    color: var(--color-accent-blue-2);
}

.c-dialog__close path,
.c-follow-list__svg path
{
    fill: var(--color-accent-blue-2);
}

.c-dialog__shadow
{
    background: none;
}

.c-dialog .c-inactivity-hint__user-forwarding
{
    display: none;
}

/* Images / SVG
   ------------------------------------ */

.c-sticky-video__close-svg,
.c-inline-navigation__icon,
.c-article-sharer__svg,
.o-teaser__brand-logo,
.c-stage-logo
{
    -webkit-filter: brightness(200%) saturate(85%);
    filter: brightness(200%) saturate(85%);
}

}

@-moz-document domain("welt.de") {
/* CONTENT SECTIONS
   ==================================== */

/* User Profile
   ------------------------------------ */

.la-profile > div > div > div > span,
.la-profile > div > div > div > div > div > div > span,
.la-profile > div > div > div > div > div > div,
.la-profile > div > div > div > div,
.la-profile > div > div > a,
.la-profile > div > div > span
{
    color: var(--color-text-4) !important;
}

.la-profile > div > div > div a
{
    color: var(--color-accent-blue-2) !important;
    filter: brightness(150%) !important;
}

.la-profile svg
{
    -webkit-filter: brightness(200%) saturate(85%);
    filter: brightness(200%) saturate(85%);
}

/* Meine Welt
   ------------------------------------ */

main div div header nav span a,
main div div header nav a,
main div div div div div div div div div a,
main div div div div div a,
main div div div div div div div a
{
    color: var(--color-accent-blue-2) !important;
}

main div div header svg *,
main div div div div div div div div div a span svg *
{
    fill: var(--color-accent-blue-2) !important;
}

main div div header h2 a,
main div:not([data-component-name="la-widget-miniprofile"]) div div div div div div a,
main div:not([data-component-name="la-widget-miniprofile"]) div div div span,
main div:not([data-component-name="la-widget-miniprofile"]) div div div div div div div a,
main div:not([data-component-name="la-widget-miniprofile"]) h2
{
    color: var(--color-text-3) !important;
}

main div[data-component-name="la-widget-miniprofile"] div div div div,
main div[data-component-name="la-widget-miniprofile"] div div div div div,
main div[data-component-name="la-widget-miniprofile"] div div div div div span
{
    color: var(--color-text-1) !important;
}

main div[data-component-name="settings"] div
{
    color: var(--color-text-3) !important;
}


.faq-topic-title-counter
{
    background-color: var(--color-accent-blue-1);
}

.faq-question h3,
.faq-question span
{
    color: var(--color-text-4);
}

.faq-svg svg *
{
    fill: var(--color-accent-blue-2);
}

/* Themen Landingpage
   ------------------------------------ */

#themen2
{
    background-color: var(--color-container-4);
}

#themen2 #content,
#themen2 #pageWrapper
{
    background-color: var(--color-container-3);
}

#themen2 .widget.themepage.header h1,
#themen2 .widget.themepage2 .headline a
{
    color: var(--color-text-4) !important;
}

#themen2 .widget.stories .tsrText,
#themen2 .widget.themepage2 p
{
    color: var(--color-text-3) !important;
}

#themen2 .widget.themepage2 .topLine,
#themen2 .widget.themepage.header .title
{
    color: var(--color-text-2) !important;
}

#themen2 .widget.themepage2 .ressort
{
    color: var(--color-accent-blue-2) !important;
}

#themen2 > .widget.code.html
{
	margin-bottom: 0 !important;
}

#themen2 #viewport
{
	margin-top: 0 !important;
}

#themen2 #pageWrapper
{
 	box-shadow: none !important;   
}

#themen2 #header .breadcrumb
{
    background-color: var(--color-container-3) !important;
}

#themen2 #pageWrapper.x940-config #header .widget.navigation.breadcrumb a,
#themen2 #pageWrapper.x940-config #header .widget.navigation.breadcrumb a:visited
{
    color: var(--color-accent-blue-2) !important;
}

#themen2 .widget.themepage2.teaserTable > div.row,
#themen2 .widget.themepage2.teaserTable .themepageTeaserMedium,
#themen2 .widget.themepage2.teaserTable .themepageTeaserLarge,
#themen2 .widget.themepage2.multipleTeasers,
#themen2 .widget.themepage2.multipleTeasers .themepage2Column,
#themen2 .widget.themepage2.multipleTeasers .themepage2Row,
#themen2 .widget.themepage2 .footer
{
    border-color: var(--color-border-2) !important;
}

#themen2 .themepage2.themeTags .themeTagLink
{
    color: var(--color-accent-blue-3) !important;
}

#themen2 .themepage2.themeTags .themeTag
{
    background-color: var(--color-container-2) !important;
}

.o-teaser__comment-count
{
    color: var(--color-text-2);
}

.o-teaser__comment-count-icon > svg
{
    fill: var(--color-text-2);
}
}

@-moz-document domain("welt.de") {
/* ARTICLE WIDGET COMPONENTS
   ==================================== */

/* Article Widgets
   ------------------------------------ */

.c-article-sharer,
.c-inline-teaser__body
{
    border-color: var(--color-border-3);
}

.c-article-sharer__label
{
    color: var(--color-text-2);
}

.c-citation-element__blockquote,
.c-citation-element__footer
{
    color: var(--color-text-3);
}

.c-citation-element__svg svg
{
    fill: var(--color-text-3);
}

path#heart-icon
{
    stroke: var(--color-accent-red-1) !important;
}

.c-themepages__link
{
    color: var(--color-accent-blue-2);
    filter: brightness(150%);
}

.o-element__main--is-colored,
.o-teaser__link-wrapper--is-light
{
    color: var(--color-text-3);
    background-color: var(--color-border-1);
}

.o-element__text--is-small
{
    color: var(--color-text-2);
}

.o-teaser__footer
{
    color: var(--color-text-2);
    border-color: var(--color-border-2);
}

.c-teaser-inline__body
{
    background-color: var(--color-border-1);
}

.c-teaser-inline__headline-link,
.c-teaser-newsticker__date,
.c-teaser-list__item
{
    color: var(--color-text-3);
}

.__slide-toggle-container .__slide-toggle-name
{
    color: var(--color-text-3);
}

.offer h2,
.offer p,
.c-inline-teaser__headline-link
{
    color: var(--color-text-4);
}

.service-container
{
    background-color: var(--color-border-1);
}

.service-container h2,
.service-container h3
{
    color: var(--color-text-4);
}

.service-container p
{
    color: var(--color-text-3);
}

.service-container a
{
    color: var(--color-accent-blue-2);
}

.c-page-container--has-separator
{
    border-color: var(--color-border-2);
}

/* Article Image Carousel
   ------------------------------------ */

.c-content-slider__overlay
{
    background-color: var(--color-container-1-translucent-85);
    -webkit-backdrop-filter: blur(2px);
    backdrop-filter: blur(2px);
}

.c-content-slider__caption
{
    color: var(--color-text-3);
    background-color: var(--color-border-1);
    border-color: var(--color-border-1);
}

.c-content-slider__nav,
.c-content-slider__icons,
.c-content-slider__icon
{
    color: var(--color-text-3);
    fill: var(--color-text-3);
    background-color: var(--color-container-1-translucent-85);
    border-color: var(--color-border-1);
}

.c-content-slider__icon:active
{
    background-color: var(--color-border-1);
}

/* "Sticky" Video
   ------------------------------------ */
.c-sticky-video__body
{
    background-color: var(--color-container-1-translucent-30);
    border-color: transparent;
    -webkit-backdrop-filter: blur(5px);
    backdrop-filter: blur(8px);
}

.c-sticky-video__divider
{
    border-color: var(--color-border-2);
}

.c-sticky-video__close,
.c-sticky-video__source
{
    color: var(--color-text-3) !important;
}

/* Social Sharing Bar
   ------------------------------------ */

@media screen and (max-width:56.875em) {
    .c-social-bar--is-sticky {
        background-color: var(--color-container-1-translucent-50);
        border-color: transparent;
        -webkit-backdrop-filter: blur(5px);
        backdrop-filter: blur(5px);
    }
}

.c-social-bar .c-social-bar__icon-link svg *
{
     fill: var(--color-accent-blue-2) !important;
}

.c-social-bar .c-social-bar__icon-label
{
     color: var(--color-text-4) !important;
}

div[data-external-component="User.Article.Likes"] > div:nth-child(1) > div:nth-child(1)
{
    color: var(--color-text-4) !important;
}

div[data-external-component="User.Article.Likes"] svg *
{
    fill: var(--color-accent-blue-2) !important;
}

}

@-moz-document domain("welt.de") {

/* ARTICLE COMMENTS
   ==================================== */

/* Article Comment Section
   ------------------------------------ */

#Comments a,
#Comments h2
{
    color: var(--color-accent-blue-2) !important; 
    filter: brightness(150%) !important;
}

#Comments div,
#Comments span
{
    color: var(--color-text-4) !important;
}

#Comments .c-content-container > div > div > div > div > div > div:first-child,
#Comments .c-content-container > div > div > div > div > div > div > div > div > div:first-child
{
    background: none !important;
}

#Comments hr
{
    background-color: var(--color-border-4) !important;
    height: 0.5px !important;
    border: none !important;
}

#Comments .c-content-container:nth-child(1) > section > div > div > div > div > div > div > div > div hr
{
    background-color: transparent !important;
}

#Comments div
{
    border-top-color: var(--color-border-4) !important;
}

#Comments textarea
{
    border-color: var(--color-border-2) !important;
    background-color: var(--color-border-1) !important;
    color: var(--color-text-3) !important;
}

#Comments .c-content-container:nth-child(1) > section > div > div > div > div > div:nth-child(1) > div > div > div,
#Comments .c-content-container:nth-child(1) > section > div > div > div > div > div:nth-child(2) > div > div > div,
#Comments .c-content-container:nth-child(1) > div:nth-child(4) > div:nth-child(2) > div:nth-child(2) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3),
#Comments .c-content-container:nth-child(1) > div:nth-child(4) > div:nth-child(2) > div:nth-child(2) > div:nth-child(2) > div:nth-child(1) > div:nth-child(3)
{
    border-color: var(--color-border-2) !important;
    background-color: var(--color-border-1) !important;
}

#Comments .c-content-container:nth-child(1) > div:nth-child(4) > div:nth-child(2) > div:nth-child(2)
{
    background-color: transparent !important;
}

#Comments div[data-qa="Comments.Header"] > div:nth-child(2)
{
    background-color: transparent !important;
}

#Comments div[data-qa="Comments.Header"] > div:nth-child(2) > div,
#Comments div[data-qa="Comments.Header"] > div:nth-child(2) > div > div > div:nth-child(3)
{
    background-color: var(--color-container-2) !important;
}

}
