style.css:
@-moz-document domain("miestai.net"), domain("miestai.net") {

html,
body,
.ui-widget,
#channel-subtabbar .channel-subtabbar-list li a,
#breadcrumbs,
.widget-tabs .widget-tabs-nav li,
.forum-list-container .forum-list-header,
.b-button, .ui-widget .b-button,
.conversation-toolbar-wrapper .conversation-toolbar,
.forum-list-container .forum-list-header,
#footer-tabbar ul.nav-list li a,
.b-comp-menu-dropdown,
#channel-tabbar .channel-tabbar-list li a,
#channel-tabbar .channel-tabbar-list li.current a,
#channel-tabbar .channel-tabbar-list li.current a:hover,
.edit-mode #channel-tabbar .channel-tabbar-list li.current a:hover,
.topic-list-container .topic-list-header,
#main-navbar > ul > li > a,
.b-comp-menu-dropdown__trigger--headerbar,
.b-post__content,
.b-post-control {
  font-family: verdana, sans-serif;
}

body.vb-page.view-mode {
  background: linear-gradient(to right, #131d2f 0%, #b9c0cc 50%, #131d2f 100%);
}

.widget-tabs.ui-tabs .ui-widget-content a, .widget-tabs.ui-tabs .ui-widget-content a:active, .widget-tabs.ui-tabs .ui-widget-content a:visited, .sb-dialog.ui-dialog-content.ui-widget-content a, .sb-dialog.ui-dialog-content.ui-widget-content a:active, .sb-dialog.ui-dialog-content.ui-widget-content a:visited {
  color: #22229C;
}

a,
a:active,
a:visited,
.b-link,
.ui-widget-content a,
.widget-tabs.ui-tabs .ui-widget-content a.topic-title:active, .widget-tabs.ui-tabs .ui-widget-content a.topic-title:visited {
  color: #22229C;
  text-decoration: underline;
}

a,
a:hover,
.ui-widget-content a:hover,
.widget-tabs.ui-tabs .ui-widget-content a.topic-title {
  color: #2828d9;
  text-decoration: underline;
}

.widget-tabs.ui-tabs .ui-widget-content a:hover, .sb-dialog.ui-dialog-content.ui-widget-content a:hover {
  
}

div#header {
  background: #e3ebf8;
}

.horizontal-arrows,
.search-container input,
.horizontal-arrows .left-arrow,
.horizontal-arrows .right-arrow {
  border-color: #697da6;
}

.arrow.h-disabled {
  background-color: #5c6983;
}

.toolbar-divider-inner {
  background-image: linear-gradient(to bottom,#415888 40%,rgba(0,0,0,0.0) 20%);
}

.lastpost-wrapper .avatar,
.cell-lastpost .avatar {
  display: none;
}

.forum-list-container .forum-item .lastpost > .lastpost-wrapper > .avatar + .lastpost-info {
  padding-left: 0;
}

table.forum-list-container.stretch,
table.topic-list-container.conversation-list {
  background-color: #d1d1e1;
  border: 1px solid #273657;
}

table.forum-list-container.stretch {
  border-spacing: 1px;
  border-collapse: separate;
}

.conversation-toolbar-wrapper .conversation-toolbar, .forum-list-container .forum-list-header {
  background: #415888;
  color: #fff;
}

.subchannel-widget .forum-list-container .forum-item td {
  border-top: 0;
}

td.cell-forum,
td.topics-count,
td.posts-count,
td.lastpost {
  background-color: #efefef;
}

.forum-list-container .forum-item td.lastpost,
.forum-list-container .forum-list-header th.header-lastpost,
.topic-list-container .topic-list .topic-item .cell-icons,
.topic-list-container .topic-list .topic-item td.cell-lastpost {
  padding-left: 5px;
  padding-right: 5px;
}

.forum-list-container .forum-item .cell-forum > .forum-wrapper > .forum-info {
  padding-left: 16px;
}

.forum-list-container .subforum-list .subforum-item .icon,
.forum-list-container .forum-item.sub .cell-forum .icon {
  margin-top: 3px;
}

.topic-item.sticky .cell-topic,
.topic-item.sticky .cell-lastpost {
  background-color: #ebebeb;
}

.topic-item.sticky .cell-icons,
.topic-item.sticky .cell-count {
  background-color: #e4e4e4;
}

.topic-item.posted .topic-title {
  font-weight: bold;
} 

.cell-topic,
.cell-lastpost {
  background-color: #F7F7F7;
}

.cell-icons,
.cell-count {
  background-color: #EFEFEF;
}

a.topic-title {
    font-size: 1.2em;
  color: #22229C;
}

.b-button--primary, .b-button--primary:link, .b-button--primary:visited {
  background-color: #415888;
  border-color: #415888;
}

.b-button--primary:hover {
  background-color: #576fa2;
}

.b-button--primary-light, .b-button--primary-light:link, .b-button--primary-light:visited {
  background-color: #bcd0e8;
}

#footer-copyright {
  margin-top: 0;
  background-color: #052733;
  color: #fff;
}

#footer-copyright a {
  color: #fff;
}

.b-post {
  background-color: #EFEFEF;
  border: 1px solid #273657;
  border-radius: 0;
}

.b-post--first {
  background-color: #d8dfe8;
}

.widget-tabs.ui-tabs .ui-widget-content .b-post__title > a {
  color: #fff;
  text-decoration: underline;
}

.b-post__body {
  background-color: #F7F7F7;
  border-left: 1px solid #e0e0e0;
}

.b-post__arrow {
  display: none;
}

.b-post--first .b-post__body {
  background-color: #e2e9f2;
}

.b-media {
  background-color: #415888;
  margin-left: -19px;
  margin-right: -19px;
  margin-top: -14px;
  padding: 5px 20px;
  color: #fff;
}

.b-media .b-post__count, .b-media .b-post__timestamp {
  color: #fff !important;
}

.pagenav-container a {
  text-decoration: none;
}

.pagenav-container .b-button:hover {
  color: #576fa2;
  text-decoration: none;
}

.pagenav-container .b-button--primary:hover {
  color: #fff;
}

#header,
#channel-tabbar,
#channel-subtabbar,
#footer,
#footer-copyright,
#content,
#header-axd,
#breadcrumbs,
.notices,
#main-navbar {
  max-width: /*[[pageWidth]]*/;
}

}
