style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/**
 * Fix:
 *
 * Please change the following when you want to do a
 * similar correction inã€€other themes.
 */
#urlbar[fission="fusion"] .autocomplete-textbox-container-fission .textbox-input-box {
  border-top: solid 1px #aaa !important;
  border-bottom: solid 1px #aaa !important;
  margin: 0px !important;
  padding-top: 3px !important;
  padding-bottom: 2px !important;
}

#feed-button .button-box {
  padding-top: 3px !important;
  padding-bottom: 2px !important;
}

/**
 * style
 */
#urlbar[fission="fusion"] > #statusbar-icon > .progress-bar {
  background-color: #CFEFFF !important;
}

#urlbar[fission="fusion"][progress]:not([progress="0"]):not([progress="100"]) {
  color: #000000 !important;
}

/*Different text color for linkified URLs */
#urlbar[fission-link] .textbox-input-box {
  color: blue !important;
}
