style.css:
/*
     * Name:    DarkInterface for ComunidadFeliz
     * Author:  JTContreras
     * Version: v0.7
     */
@-moz-document domain("comunidadfeliz.com"),
domain("comunidadfeliz.cl"), domain("comunidadfeliz.mx") {

header .navbar-header,
.green-box,
.plans,
.like-cf,
.btn-success {
  background-color: #608b4e;
  color: #EEE;
}

.event_liner_panel .common_expense_title{
  color: #C5C5C5;
}

.modal-header {
  border-bottom: 1px solid #161616;
  background: none no-repeat scroll 0 0 #608b4e;
}

.landing-demo .home-title, .btn-success {
  color: #608b4e;
}

.embedded-wrapper .modal-header h3,
.btn,
body>pre,
body,
a.love,
h1 small,
h1 .small,
h2 small,
h2 .small,
h3 small,
h3 .small,
h4 small,
h4 .small,
h5 small,
h5 .small,
h6 small,
h6 .small,
.h1 small,
.h1 .small,
.h2 small,
.h2 .small,
.h3 small,
.h3 .small,
.h4 small,
.h4 .small,
.h5 small,
.h5 .small,
.h6 small,
.h6 .small,
.panel-info .lead,
body>pre,
body,
a.love,
a.cke_path_item,
span.cke_path_empty,
.left-bar-menu ul li a {
  color: #EEE;
}

.header-line,
.header-line tr {
  background-color: #569cd6 !important;
  color: #EEE
}

.contGeneral,
.event_liner .timeline-centered .timeline-entry .timeline-entry-inner .timeline-label,
body:not(.pdf),
#content,
textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input,
#feedback-suggest .suggest-search .searchField,
.redactor_box,
.redactor_box textarea,
.redactor_toolbar,
.attach-wrapper,
.cke_editable,
#container,
.form-control,
.bootstrap-tagsinput,
.btn-default:hover,
.btn-default:focus,
.btn-default:active,
.btn-default.active,
.open>.btn-default.dropdown-toggle,
.multiselect-container,
.dropdown-menu>li>a,
.table-striped>tbody>tr:nth-child(odd)>td,
.table-striped>tbody>tr:nth-child(odd)>th,
.timepicker,
.datepicker,
.bootstrap-datetimepicker-widget, .property-panels .base_color {
  background-color: #242424;
  color: #C5C5C5;
}

.container-fluid, .property-panels .panel-heading {
  background-color: #161616;
  color: #C5C5C5;
}

.container-fluid h1,
.title-container {
  background-color: #242424;
  color: #00b49f;
}

.content,
#loginIframeContainer,
#loginForm,
.dropdown-menu .divider,
.green-box .benefits,
.source .data,
.cke_top {
  background-color: #161616;
}

.modal-body,
.btn-default,
input,
.table-index,
.table-row,
html:not(.pdf),
.panel,
.ce-pdf-container .details-content,
.pagination>.disabled>span,
.pagination>.disabled>span:hover,
.pagination>.disabled>span:focus,
.pagination>.disabled>a,
.pagination>.disabled>a:hover,
.pagination>.disabled>a:focus,
.pagination>li>a,
.pagination>li>span {
  background-color: #242424;
}

.modal-footer,
#main_dropdown {
  background-color: #242424;
  border-top: 1px solid #1b1b1b
}

.products .payment-box,
.common_expense-box {
  background-color: #00223f;
}

.btn-primary {
  background-color: #569cd6 !important;
}

.btn-warning {
  background-color: #C78626 !important
}

.btn-orange {
  background-color: #CE9178 !important
}

.btn-info {
  background-color: #569CD6;
  color: #EEE;
}

.btn-default.disabled {
  color: #242424;
}

.title-container small,
.landing-demo .landing-1,
a,
#attachment-type .ficon-camera,
#attachment-type .ficon-attachment {
  color: #C5C5C5;
}

a:hover {
  color: #A0A0A0;
}

a:visited {
  color: #888;
}

a.blue-font {
  color: #88c9ff !important;
}

.table-hover>tbody>tr:hover>td,
.table-hover>tbody>tr:hover>th,
.custom-table-hover:hover,
.line:hover {
  background-color: #1A1A1A;
}

.line.active {
  background-color: #702321;
}

.cke_bottom {
  background-color: #161616;
  color: #EEE;
}

.cke_toolbar span {
  filter: brightness(2.8);
}

.cke_toolbar a:hover,
.cke_toolbar a:active,
.cke_toolbar a:visited,
.cke_toolbar a:focus {
  background-color: #242424;
  filter: brightness(0.4);
}

a.cke_button_on {
  filter: brightness(0.3);
}

.form-control[disabled],
.form-control[readonly],
fieldset[disabled] .form-control {
  background-color: #101010;
}

.table>thead>tr>td.info,
.table>thead>tr>th.info,
.table>thead>tr.info>td,
.table>thead>tr.info>th,
.table>tbody>tr>td.info,
.table>tbody>tr>th.info,
.table>tbody>tr.info>td,
.table>tbody>tr.info>th,
.table>tfoot>tr>td.info,
.table>tfoot>tr>th.info,
.table>tfoot>tr.info>td,
.table>tfoot>tr.info>th {
  background-color: #09202b;
}

element.style {
  background-color: #101010;
}

.alert-danger,
.bg-danger {
  background-color: #702321;
  color: #f2dede;
}

.bg-info {
  background-color: #78bfe3;
  color: #EEE;
}

.bg-lightblue {
  background-color: #18a7c6;
  color: #EEE;
}

.bg-success {
  background-color: #223c17;
  color: #EEE;
}

.bg-warning {
  background-color: #C78626;
  color: #222;
  text-shadow: 0 1px 0 rgba(0, 0, 0, 0.5);
}

.dashboard .green {
  color: #00b49f;
}

rect.highcharts-background {
  fill: #161616;
  color: #C5C5C5
}

.highcharts-container text,
.highcharts-container tspan {
  color: #C5C5C5 !important;
  fill: #C5C5C5 !important;
  stroke: #C5C5C5 !important;
  stroke-width: 0px;
}

.highcharts-button rect,
.highcharts-legend rect.highcharts-legend-box {
  fill: #242424 !important;
}

.highcharts-label>path {
  fill: rgba(16, 16, 16, 0.85)
}


.pagination>li>a:hover,
.pagination>li>a:focus,
.pagination>li>span:hover,
.pagination>li>span:focus,
.pagination>li>a:hover,
.pagination>li>a:focus,
.pagination>li>span:hover,
.pagination>li>span:focus,
.btn-white,
.bg-grey,
.faq {
  background-color: #555;
}

.input-group-addon {
  background-color: #555;
  color: #C5C5C5;
}

.dropdown-menu>a,
.dropdown-menu>.active>a:hover,
.dropdown-menu>.active>a:focus {
  background-color: #EEE;
  color: #242424;
}

.small-table tr.header {
  background-color: #569cd6;
  color: #EEE;
}

.second-header-line {
  background-color: #6796e6 !important;
}

.third-header-line {
  background-color: #555 !important;
}

.fc-state-default {
  background-color: #242424;
  color: #C5C5C5;
  background-image: linear-gradient(to bottom, #242424, #555)
}

table.dataTable tbody tr {
  background-color: #242424 !important;
  color: #C5C5C5;
}

.gc.ce-pdf-container {
  color: white;
}
}
