style.css:
@-moz-document domain("www.pcgamer.com") {
/* BACKGROUND */
body {
background: url("https://c2.staticflickr.com/9/8647/30219334841_014d10476f_o.jpg") 100% 100% !important;
background-repeat: no-repeat !important;
background-attachment: fixed !important;
background-position: 25% !important;
-webkit-background-size: cover !important;
-o-background-size: cover !important;
background-size: cover !important;
overflow-y: auto !important;
overflow-x: hidden !important;
}

html:not(h1):not(h2):not(h3):not(h4):not(h5):not(h6), [class^="by-author"], [class^="date-"], .listingResult .synopsis, header .byline time { 
color: #999;
}

/* HEADERS TEXT COLORS */  
h1, h2, h3:not(.recommendation-container), .carousel__title, h4, h5, h6, a[class^="articleTitleLink"], 
h1 a, h2 a, h3 a, h4 a, h5 a, h6 a, .strapline { 
color: #CCC !important; 
}

/* VARIOUS DARK LINKS */
.comment-jump span, 
.trending-wrapper .trending-item a, 
.trending-wrapper .trending-item a:visited, 
.article.pagination.internal.current-prev-next .direction, 
.pagination.internal.current-prev-next.news-article .direction, 
.pagination.internal.current-prev-next.review-article .direction, 
.pagination.internal.current-prev-next.static-article .direction { 
color: #BBB !important;
}

/* VARIOUS DARK ARROWS TURNED RED */
label .icon-arrow-left, 
.article.pagination.internal.current-prev-next .direction::after, 
.article.pagination.internal.current-prev-next .direction::before, 
.pagination.internal.current-prev-next.news-article .direction::after, 
.pagination.internal.current-prev-next.news-article .direction::before, 
.pagination.internal.current-prev-next.review-article .direction::after, 
.pagination.internal.current-prev-next.review-article .direction::before, 
.pagination.internal.current-prev-next.static-article .direction::after, 
.pagination.internal.current-prev-next.static-article .direction::before, 
.pagination-gallery-sequential .pagination-gallery-sequential-button i { 
background: #DC191B !important; 
}

.pagination-gallery, 
.pagination-gallery-sequential { 
border-color: #333 !important;
}

.trending-wrapper .trending-item a:hover { 
color: #DC191B;
}

.recommendation-container h3 { 
color: #FFF !important; 
}
  
/* NAVIGATOR -or- HEADER */
.primary-nav { 
position: fixed !important; 
width: 100% !important; 
z-index: 1000 !important; 
top: 0px !important; 
}

.burgerbar { 
position: relative; 
z-index: 1002 !important; 
}

.wrapper { 
position: relative;
top: -38px;
z-index: 1001 !important; 
transition: all 0.25s linear 0s;
}

.wrapper:hover { 
position: relative;
top: 0px;
}

/* MAIN COLUMN */
#main { 
background: #222 !important; 
background-clip: padding-box !important;
border-left: 1px solid rgba(0, 0, 0, 0) !important;
border-right: 1px solid rgba(0, 0, 0, 0) !important;
box-shadow: 0 0 10px 4px rgba(0, 0, 0, 0.8) !important;
padding-top: 30px !important; 
}

/* INNER-NAVIGATOR FOR POPULAR GAMES */
.trending-wrapper .trending-bar { 
background: none !important; 
border-bottom: 1px solid #333;
}

/* DELETED CONTENTS & MODULES */
#expanding-articles, 
.jump-to-comments, 
.newsletter-signup, 
.next-prev-container, 
.read-more-container, 
.future__jwplayer--carousel, 
aside.hawk-widget { 
display: none !important; 
}

.trending-wrapper #fadeout { 
background: none !important; 
}

/* RIGHT FLOATING SIDEBAR BOXES */
.popular-box, 
.recommended-content { 
background: #161616 !important; 
border: 1px solid #333 !important;
border-radius: 3px !important;
box-shadow: 1px 2px 4px rgba(0,0,0,1) !important;
overflow: hidden !important; 
}

#popular-box-wrapper.style1 a:active, #popular-box-wrapper.style1 a:focus, #popular-box-wrapper.style1 a:link, #popular-box-wrapper.style1 a:visited { 
color: #666 !important; 
}

.most-popular-item, 
.recommendation-articles .recommended-article.item-1 { 
border-color: #333 !important; 
border-style: solid !important; 
}

.recommendation-articles .recommended-article.item-2, 
#popular-box-wrapper.style1 .most-popular-tab-bar #popularcontent ol#poplist-1.popular-list.active li.most-popular-item:last-child { 
border: none !important; 
}

#articleTag.box .tag { 
background: #161616 !important; 
border: 1px solid #333 !important;
border-radius: 3px !important; 
}

#articleTag.box .tag a { 
color: #BBB !important;
}

#articleTag.box .tag a:hover { 
color: #DC191B !important; 
}

.load-comments { 
color: #BBB !important;
}

.load-comments:hover { 
color: #DC191B !important;
}

.block-link { 
background: #161616 !important; 
border: 1px solid transparent !important;
}

.block-link:hover { 
background: #161616 !important; 
border: 1px solid #333 !important;
}

.separator-heading { 
border-color: #333 !important;
}

#articleTag.box h3.separator-heading { 
color: #BBB !important;
}

.feature-block.top-featured .feature-block-item-wrapper:nth-of-type(1), 
.feature-block.top-featured .feature-block-item-wrapper:nth-of-type(2), 
.feature-block.top-featured .feature-block-item-wrapper:nth-of-type(3),
.feature-block.top-featured .feature-block-item-wrapper:nth-of-type(4) { 
border-color: #222 !important; 
}

.list-title { 
border-color: #333 !important; 
}

.listingResults .listingResult { 
background: #161616 !important; 
border: 1px solid #333;
border-radius: 3px !important;
box-shadow: 1px 2px 4px rgba(0,0,0,1) !important;
margin-top: 20px !important;
margin-bottom: 20px !important;
margin-right: 5px !important; 
padding: 5px 10px 5px 10px !important; 
transition: all 0.25s linear 0s;
}

.input-bar, 
.list-grid { 
background: #161616 !important; 
border: 1px solid #333;
border-radius: 3px !important;
box-shadow: 1px 2px 4px rgba(0,0,0,1) !important;
padding: 5px 10px 5px 10px !important; 
transition: all 0.25s linear 0s;
}

.input-bar-item + .input-bar-item, 
.jump-to-section .head, .pagination.internal { 
border-color: #333 !important; 
}

.vertical-hardware .listingResultsWrapper.buying-guides #buyingGuideSearch, .vertical-hardware .listingResultsWrapper.reviews #buyingGuideSearch { 
border-color: #161616 !important; 
}

#buyingGuideSearch input { 
border-bottom: 1px solid rgba(255,255,255,.18) !important;
border-top: 1px solid #000 !important;
border-right: 1px solid #111 !important;
border-left: 1px solid #111 !important;
border-radius: 3px !important;
box-shadow: inset 0px 1px 5px 0px #000 !important;
background: #333 !important;
margin-right: 5px !important; 
overflow: hidden !important; 
color: #CCC !important; 
}

#buyingGuideSearch button { 
background: none !important; 
border: none !important; 
box-shadow: none !important; 
}

.article .text-copy h3, 
.news-article .text-copy h3, 
.review-article .text-copy h3, 
.static-article .text-copy h3, 
.trending-wrapper .trending-lead { 
background: #222 !important; 
}

.article .text-copy blockquote, .news-article .text-copy blockquote, .review-article .text-copy blockquote, .static-article .text-copy blockquote { 
border-color: #DC191B !important; 
}

.pagination.internal > .listings-pagination-button { 
background: #161616 !important; 
border: 1px solid #333 !important;
border-radius: 3px !important; 
margin-top: 30px !important; 
}

.impact #content > .list-text-links > .listingResult.small.result1, .impact #content > .list-text-links > .listingResult.small.result2, .impact #content > .list-text-links > .listingResult.small.result3, .impact .list-text-links .search-result .content { 
background: #161616 !important; 
border-color: #333 !important;
}

.list-text-links .list-heading-link { 
background: #333 !important; 
 }

.list-text-links .list-heading-link:hover { 
background: #444 !important; 
 }

.impact .list-text-links { 
border: 1px solid #333 !important; 
}

/*Most Ads I could find.*/
#affiliateDisclaimer,
.slotify-slot, 
.placeholder, 
.zergnet-header-container, 
[id^="zergnet-widget"], 
.club-boilerplate, 
.hawk-widget-insert, 
html body.limit-width.limit-width.js.no-touch.has-takeover::before, 
div[id*="google_ads"], body [style="position: relative; box-sizing: border-box; height: 90px; margin: 20px auto; text-align: center; display: block; pointer-events: none; transform: none; width: 728px;"], 
#sidebar [style*="display: block; transform: none; width: 300px; background-color: rgb(237, 237, 237);"], 
#sidebar [style*="text-align: center; display: block; pointer-events: none; transform: none; width: 300px; background-color: rgb(237, 237, 237); padding-bottom: 10px;"], 
#sidebar [style*="font-size: 12px; width: 100%; height: 30px; display: flex; flex-flow: column nowrap; align-items: center; justify-content: center;"], 
#sidebar .slot-after_popular_box, 
#sidebar .slot-before_popular_box, 
#sidebar .slot-top_of_sidebar { 
display: none !important; 
pointer-events: none !important;
}

/*Remove the cookie banner asking you if you're okay with it and stuff...*/
body [aria-label="cookieconsent"] { 
display: none !important; 
}

.fancy-box { 
background: #161616 !important; 
border: 1px solid #333;
border-radius: 3px !important;
box-shadow: 1px 2px 4px rgba(0,0,0,1) !important;
overflow: hidden;
}

/* ★ WIDE VERSION MODIFICATION ★ */

/*Makes the left column wider*/
#content-after-image, 
body.limit-width .news-article, 
body.limit-width .static-article { 
max-width: 970px !important; 
width: 970px !important; 
}

/*Makes the left column wider*/
#article-body, 
#content, 
#expanding-articles, 
.verdict-boxout, 
.mainCarousel { 
max-width: 922px !important; 
width: 922px !important; 
}

/* Enlarged Image Fix*/
#expanding-articles [class^="cutoffImage-"] { 
overflow: hidden !important; 
}
/*Enlarged Image Fix*/
#expanding-articles [class^="image-"] { 
max-width: 922px !important; 
min-width: 922px !important; 
width: 100% !important; 
}

#expanding-articles article a[class*="c"], 
#expanding-articles article img[class*="c"] { 
max-width: 922px !important; 
min-width: 922px !important; 
width: 100% !important; 
}

/* < - - - END - - - > */

/*Sliding Sidebar*/
#sidebar { 
position: fixed !important; 
background: #222 !important;
padding-left: 20px;
padding-right: 20px;
top: 0px !important; 
right: -290px !important; 
margin-top: 0 !important; 
overflow-y: auto !important; 
height: 100% !important; 
border-left: 10px solid #DC191B;
transition: all 0.25s linear 0s;
z-index: 999999 !important;
}

/*Sliding Sidebar*/
#sidebar:hover { 
position: fixed !important; 
right: 0px !important; 
border-left: 1px solid #333;
}

  body.limit-width .article { 
max-width: 100% !important; 
}
}

