style.css:
@-moz-document domain('magister.net') {  

/*Warning: may not be the newest update*/
/*user settings*/
:root{
   --background: url("/*[[bg]]*/");
  /*replace with image url, must be direct!*/
  /* these colors can be changed!*/
  --button-color: /*[[main]]*/; 
  --button-hover: /*[[second]]*/;
  --border-color: /*[[border]]*/;
}
/* goto https://www.w3schools.com/colors/colors_picker.asp to get color codes*/

/*Warning: this version can be behind check https://github.com/Gamerein/Reins-Magister-theme/edit/master/r-magister for the latest update*/
 
 /*new login*/

.col-xs-12.col-sm-6.challenge-container {
    position: relative;
    right: 25%;
    text-align: center;
}
button.primary-button.ripple {
    background-color: var(--button-color);
    animation: pulse 2s infinite;
}
button.primary-button.ripple:hover {
    background-color: var(--button-hover);
    color: #FFF;
    transition: all 0.3s ease-in-out 0s;
    animation: pulse 2s infinite;
}
path {
    fill: var(--border-color);
}
label.placeholder {
    color: var(--button-color) !important;
}
.podium > .header {
    border-bottom: none;
}
.podium >.header > div.header__tenant.ng-scope {
    border-bottom: none;
    text-decoration: underline;
}
p.header__tenant-title.ng-binding {
    margin-right: 0px;
    text-align: center;
}
input#password {
    font-size: 25px;
    letter-spacing: 1px;
}
input {
    color: var(--button-hover);
    font-weight: bold;
    font-family: Arial;
}
.container-fluid.app-container {
    color: #FFF;
    background-color: transparent;
}
.row.row-no-padding {
    background-color: transparent;
}
.splash {
    background-image: none;
    background-color: transparent;
}
.password__eye-icon {
    fill: var(--button-color);
}
.col-xs-12.col-sm-6.challenge-container > .logo {
    visibility: hidden;
}
/*end of new login*/

body {
    background-image: var(--background) !important;
    background-color: var(--button-color);
    background-size: cover;
    background-repeat: no-repeat;
}
.header-user img {
    border-radius: 5px;
}
.header-user:hover {
    background-color: rgba(255, 255, 255, 0.0) !important;
}
span#btn-user-settings:hover::before {
    -webkit-animation-name: spinnerRotate;
    -webkit-animation-duration: 5s;
    -webkit-animation-iteration-count: infinite;
    -webkit-animation-timing-function: linear;
}
.logoff:hover {
    background-color: rgba(255, 255, 255, 0.0) !important;
}
.asideTrigger.trigger-btn {
    border-left: 0px solid var(--border-color) !important;
}
span.text-icon {
    background-color: #333 !important;
}
span.text-icon.ng-scope {
    background-color: #333;
}
aside .asideTrigger:after {
    border-color: var(--button-hover) rgba(255, 255, 255, 0) rgba(255, 255, 255, 0) rgba(255, 255, 255, 0) !important;
}
li.ng-scope.asideTrigger.active a.ng-binding {
    background-color: var(--button-hover) !important;
}
.icon-logo {
    position: relative;
    bottom: 3px;
}
.logo > a {
    border: 0px solid var(--border-color) !important;
}
.logo {
    padding: 0px 5px;
    line-height: 40px !important;
    box-sizing: border-box;
    height: 40px;
}
.logo:hover {
    background-color: var(--button-color);
    border-radius: 5px;
}
.icon-recyclebin {
    position: relative;
    bottom: 5px
}
.icon-plus {
    position: relative;
    bottom: 5px
}
.header a {
    border-left: 2px solid var(--border-color);
}
.header {
    background-color: rgba(51, 51, 51, 0.0);
    border-bottom: 2px solid var(--border-color);
}
.menu > ul {
    margin-top: 0px;
    box-sizing: border-box;
    padding-top: 40px;
    border-top: 0px solid var(--border-color);
    width: 40px;
}
.message-content.ng-binding.ng-scope > p {
    background-color: #FFF !important;
    color: #000 !important;
}
.menu {
    top: 18px;
    border-radius: 5px;
    background-color: rgba(51, 51, 51, 0.0) !important;
}
.master-view {
    padding-left: 41px !important;
    background-image: var(--background);
    background-size: cover;
}
.endlink a:hover {
    background-color: var(--button-hover);
}
.endlink a {
    color: white !important;
    text-decoration: none !important;
    background-color: var(--button-color);
    border-radius: 10px;
    line-height: 30px;
    padding-left: 8px;
    height: 30px;
    position: relative;
    top: 5px;
    right: 5px;
}
.endlink {
    background-color: rgba(51, 51, 51, 0.3);
}
.block {
    background-color: rgba(51, 51, 51, 0.3);
    border: 2px solid var(--border-color);
    border-radius: 5px;
}
.head-bar {
    background-color: rgba(51, 51, 51, 0.0);
    border-bottom: 2px solid var(--border-hover);
}
.head-bar div > a {
    border-left: 0px !important;
    background-color: var(--button-color);
    border-radius: 10px;
    line-height: 30px;
    padding-left: 8px;
    height: 30px;
    position: relative;
    top: 5px;
    right: 5px;
}
.head-bar div > a:hover {
    background-color: var(--button-hover) !important;
    background: none;
}
.icon-pencil {
    position: relative;
    bottom: 5px;
}
.content-container {
    background-color: rgba(0, 0, 0, 0);
    background-image: none !important;
    border: 2px solid var(--border-color);
}
b {
    font-family: "Arial Black";
    color: grey;
}
.msg-list .unread {
    font-weight: normal;
    border-left: 4px solid #000;
}
.widget .agenda-list li.alert span.time:before {
    color: #000 !Important;
}
.widget .agenda-list li.alert span.time:after {
    color: #000 !Important;
}
.alert {
    border-left: 4px solid #000;
    background-color: #f2f2f2 !important;
}
.alert span.nrblock {
    background-color: #000;
}
.block h3 .icon-back:before {
    color: var(--button-color);
}
#cijfers-leerling .last-grade {
    background-color: var(--button-color);
}
#afsprakenlijst-container .today:before {
    margin-right: 3px;
    box-sizing: border-box;
    content: "Vandaag";
    color: #be2f37;
    background-color: var(--button-hover);
    padding: 3px 5px;
    border-radius: 10px;
}
#afsprakenlijst-container .today {
    color: #000 !important;
}
p.ng-binding {
    background-color: var(--button-color);
    color: #FFF !important;
    border-radius: 10px;
    font-family: monospace
}
.ng-binding > p {
    padding: 4px !important;
    color: #FFF !important;
    border-radius: 10px;
    font-family: monospace;
}
.toast em {
    display: none;
}
.alert-toast span {
    font-size: 30px;
    line-height: 30px;
}
.alert-toast:hover {
    font-size: 30px;
    border: 2px solid #d00005;
}
.toast .glyph:before,
div.splash:before {
    display: none;
}
@keyframes pulse {
    0% {
        -moz-box-shadow: 0 0 0 0 #0096db;
        box-shadow: 0 0 0 0 var(--button-hover);
    }
    70% {
        -moz-box-shadow: 0 0 0 10px rgba(64, 64, 64, 0);
        box-shadow: 0 0 0 15px rgba(64, 64, 64, 0);
    }
    100% {
        -moz-box-shadow: 0 0 0 0 rgba(64, 64, 64, 0);
        box-shadow: 0 0 0 0 rgba(64, 64, 64, 0);
    }
}
@-webkit-keyframes spinnerRotate {
    from {
        -webkit-transform: rotate(0deg);
    }
    to {
        -webkit-transform: rotate(360deg);
    }
}
/*cijfers*/

.cijfers-k-grid.k-grid .grade.gemiddeldecolumn.insufficient {
    background-color: #ff4d4d !important;
    color: #FFF!important;
}
.cijfers-k-grid.k-grid .grade.gemiddeldecolumn {
    background-color: #6ddf6d !important;
}
.cijfers-k-grid.k-grid .k-selectable .k-state-selected .grade {
    box-shadow: inset 0 0 0 1px #0066ff !important;
}
/* end of cijfers*/
/*6/9 grote transparantie update in agenda en afsprakenlijst*/
/*laatste cijfer window*/

.list.arrow-list > li,
#vandaag-container .grade-widget ul {
    background: transparent;
}
.content {
    background-color: transparent !important;
}
.widget li a {
    color: white !important;
}
.widget li {
    background-color: rgba(255, 255, 255, 0.1);
}
.widget li:hover,
.alert a:hover {
    background-color: rgba(255, 255, 255, 0.6) !important;
    transition: background-color .7s ease;
}
li.alert {
    background-color: rgba(255, 255, 255, 0.4) !important;
    border-left: 4px solid #fff;
}
.widget .agenda-list li.alert span.time:before,
.widget .agenda-list li.alert span.time:after {
    color: white !important;
}
.widget span.nrblock {
    background-color: white;
    color: #000;
}
.alert span.nrblock {
    color: black;
    background-color: white !important;
}
/*in bijv huiswerk*/

.comment.ng-binding,
.attachment-bar:before {
    color: #fff;
}
/*afsprakenlijst*/

.sm-grid.k-grid .k-grid-content tbody,
.sm-grid.k-grid .k-grid-content,
.k-widget,
.afsprakenlijst-container .main > .content-container {
    background: transparent;
}
.sm-grid.k-grid .k-grid-content tr {
    transition: background-color .7s ease;
    background: rgba(255, 255, 255, 0.2);
}
.ng-scope.k-state-selected {
    background: rgba(255, 255, 255, 0.5) !important;
}
.sm-grid.k-grid .k-grid-content tr:hover {
    background: rgba(255, 255, 255, 0.3);
}
tr,
td {
    height: 35px;
    font-size: 13px !important;
}
td[colspan="7"] {
    background: rgba(51, 51, 51, 0.3);
}
.sm-grid.k-grid .k-grid-content .nrblock {
    color: black !important;
    background-color: white;
}
.sm-grid.k-grid .k-grid-content tbody span {
    color: rgb(240, 240, 240);
}
.ng-scope.k-state-selected span {
    color: rgb(65, 86, 119) !important;
}
dl.list-dl {
    background: transparent;
}
dl.list-dl dt,
dl.list-dl dd {
    color: #fff;
}
aside .tabs li a {
    transition: color 0.5s ease;
    background-color: rgba(10, 10, 10, 0.3);
    color: #fff;
}
aside .tabs li:hover a {
    color: grey;
}
/* 6/9 end */

.content-container {
    border: none;
}
.head-bar div > a:hover,
.endlink a:hover {
    animation: pulse 2s infinite;
}

/*Nav menu*/

.menu > ul a {
    border: none;
}
.menu > ul > li {
    background-color: var(--button-color);
}

.menu > ul > li:first-child {
    visibility: hidden;
}

/* .menu > ul > li > a {
    background: none;
    text-align: center;
    padding-left: 8px;
    padding-top: 7px;
    visibility: hidden;
} */
/* .menu > ul > li:hover > a {
    visibility: visible;
}
.menu > ul > li > a:hover {
    background: none !important;
    visibility: visible;
}
.menu > ul > li:hover {
    margin: 15px;
    right: 20px;
    width: 60px !important;
    height: 60px !important;
    transition: 0.5s;
} */

.menu li:after {
    position: relative;
    background: black;
    padding: 2px 5px 2px 5px;
    border-radius: 10px;
    left: 52px;
    bottom: 45px;
}

.menu > ul > li:nth-child(2):hover:after {
    content: "Vandaag"
}
.menu > ul > li:nth-child(3):hover:after {
    content: "Agenda"
}
.menu > ul > li:nth-child(4):hover:after {
    content: "Afwezigheid"
}
.menu > ul > li:nth-child(5):hover:after {
    content: "Cijfers"
}
.menu > ul > li:nth-child(7):hover:after {
    content: "Berichten"
}
.menu > ul > li:nth-child(9):hover:after {
    content: "Leermiddelen";
}

.menu > ul > li  ul {
    background: rgba(0, 0, 0, 0.9);
/*     position: relative;
    left: 184px;
    top: 44px; */
}
.menu li.active,
.menu li.highlight-menu.active {
    background-color: var(--button-hover) !important;
}
/*Nav menu end*/

/* 18/10/2017 login fix*/

.image-carrousel {
    visibility: hidden;
}

.background-rood .color-label {
    background-color: red;
}
.background-blauw .color-label {
    background-color: blue;
}
.background-groen .color-label {
    background-color: green;
}
.background-geel .color-label {
    background-color: yellow;
}
.background-donker-oranje .color-label {
    background-color: orange;
}

.head-bar {
    z-index: 2;
}

/* 31/1/18*/
.clearfix.user-content
 {
    background-color: rgba(51, 51, 51, 0.3) !important;
  color: #fff;
}

ul.sources.ng-scope li.ng-scope{
  background-color: rgba(0, 0, 0, 0.1) !important;
  color: #000;
}

#studiewijzer-detail-container .coloring-rood > div, #studiewijzer-detail-container .coloring-rood > ul {
  border-left: 4px solid red;
}

#studiewijzer-detail-container .coloring-blauw > div, #studiewijzer-detail-container .coloring-blauw > ul {
  border-left: 4px solid blue;
}

#studiewijzer-detail-container .coloring-groen > div, #studiewijzer-detail-container .coloring-groen > ul {
   border-left: 4px solid green;
}

#studiewijzer-detail-container .coloring-geel > div, #studiewijzer-detail-container .coloring-geel > ul {
   border-left: 4px solid yellow;
}

#studiewijzer-detail-container .coloring-donker-oranje > div, #studiewijzer-detail-container .coloring-donker-oranje > ul {
   border-left: 4px solid orange;
}

#studiewijzer-container div.studiewijzer-list.normaal > ul > li, #studiewijzer-container div.studiewijzer-list.normaal > ul > li > a {
  color: #fff;
  border: 1px solid var(--border-color) !important;
 background-color: rgba(51, 51, 51, 0.2);
 border-radius: 0px;
}

#studiewijzer-container div.studiewijzer-list > ul > li > a > span {
  border-right:3px solid var(--button-color)
}
/* end 31/1/18*/

::-moz-selection {
    background: var(--border-color);
}

::selection {
    background: var(--border-color);
}
/*0/2/18*/

#bronnen-container .bronnen-quota-label {
  background: rgba(51, 51, 51, 0.1) !important;
  color: #fff;
  font-weight: 100;
  font-size: 14px;
}
div#afsprakenLijst tr.ng-scope:nth-last-child(odd) {
  background: rgba(255, 255, 255, 0.14);
}

aside .tabs li.active a {
  background-color: var(--button-color);
}

div#bestanden li:nth-last-child(odd){
  background: rgba(255, 255, 255, 0.14)
}

.sources li ul {
  background: transparent;
}

.rest-column.ng-isolate-scope, div#mappen {
  background: rgba(51, 51, 51, 0.3);
}

.block b {
    color: #fff;
}

.block h3 {
  background: rgba(51, 51, 51, 0.1) !important;
  color: #fff;
}

.column-container li.selected, .column-container li.checked, .sources li.selected > a{
  background: rgba(255, 255, 255, 0.2) !important;
}

.sources li.selected > a:after, .sources li:hover > a:after {
  color: var(--button-color);
}

.sources li:hover, .sources li.selected  {
  border-top: 1px solid var(--border-color);
}

/*end 0/2/18*/

.widget .list li, .endlink {
  border-top: 1px solid var(--border-color) !important;
}

.block h3, .widget .list, #vandaag-container .grade-widget ul {
  border-bottom: 1px solid var(--border-color);
}

/* begin 23/3/20, corona times */
.head-bar h1[data-ng-bind="datum"] {
    text-transform: capitalize;
}

.view.ng-scope {
    margin-left: 10px;
}

.fancy-select span {
    background-color: var(--button-color) !important;
    color: white !important;
    border: none !important;
}

/* some dropdowns */
.fancy-select span.k-dropdown-wrap.k-state-hover,
.fancy-select span.k-dropdown-wrap.k-state-border-down,
.fancy-select span.k-state-hover span,
.fancy-select span.k-state-border-down span
{
    border: none !important;
    background-color: var(--button-hover) !important;
}

label[data-ng-bind="selector.name"] {
    color: white;
}

b {
    font-weight: bold !important;
}

ul[role="listbox"] li {
    background-color: var(--button-color) !important;
}

.block h3 span.icon-up-arrow, .block h3 span.icon-down-arrow {
    color: #fff;
}

.k-dropdown .k-dropdown-wrap .k-select:after {
    border-color: #fff rgba(255,255,255,0) rgba(255,255,255,0) rgba(255,255,255,0) !important;
}

/* version checker*/

div.logo::after {
    position: relative;
    font-family: Arial;
    margin-left: 10px;
    font-size: 11px;
    width: 300px;
    box-sizing: border-box;
    content: "Reins Magister theme: V1.4.0!";
    font-weight: bold;
    padding: 5px;
    background-color: var(--button-hover);
    border-radius: 20px;
}
}
