style.css:
@-moz-document domain("vk.com") {
  html, body {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
 background: #2d2d2d;   
    color: #ececec;
}
a {
 color: #d02030;   
}
h2 {
    color: #fff;
}

#page_header_cont .back {
        background: #3e3e3e;
}

input.text.ts_input {
background: #b1000e;
}

#side_bar ol li .left_row {
    color: #d00000;
}

.left_count_wrap {
        color: #ff0f0f;
}

.left_count_wrap.left_count_wrap_hovered:hover {
        background-color: #393939;
}

.im-chat-input {
        background: #3e3e3e;
}

.im-page--toolsw {
        background: #3e3e3e;
}
.im-page--title-meta {
     color: #d00000;   
}

.im-page--title-main-inner {
    color: #d3dee7;
}
.ui_search {
    background-color: #3e3e3e;
        border-bottom: 1px solid #474747;
}
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected {
    background: #565656;
}
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_selected.nim-dialog_classic.nim-dialog_unread, .nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_selected.nim-dialog_hovered, .nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_selected:hover, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered+.nim-dialog.nim-dialog_selected, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered.nim-dialog_selected+.im-page--messages-search, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected+.im-page--messages-search, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected+.nim-dialog, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected:hover+.im-page--messages-search, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic+.nim-dialog.nim-dialog_selected, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic.nim-dialog_selected+.im-page--messages-search, .nim-dialog:not(.nim-dialog_deleted):hover+.nim-dialog.nim-dialog_selected {
        border-top: solid 1px #3e3e3e;
}
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--date, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--name, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--name-w, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--preview, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--text-preview, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--who, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--date, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--name, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--name-w, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--preview, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--text-preview, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--who {
color: #ececec;
}
.im-page .im-page--dcontent {
    background: #3e3e3e;
}
.im-page .im-page--dialogs-footer.ui_grey_block {
        background: #3e3e3e;
        border-image: linear-gradient(to top, black, rgba(0, 0, 0, 0)) 1 100%;
}
.im-page .im-page--dialogs-footer {
    border-top: solid 1px #474747;
}
.im-page .im-page--chat-body-wrap-inner {
    background: #3e3e3e;
}
.im-mess.im-mess_unread {
 background: #2d2d2d;
}
.im-mess.im-mess_selected:last-child:before, .im-mess.im-mess_unread:last-child:before {
    background: #d00f1d;
}
.page_block {
    box-shadow: 0 0px 15px -5px #ff0000, 0 16px 30px 23px #2d2d2d;
}
.im-mess-stack .im-mess-stack--content .im-mess-stack--pname>a {
    color: #d02030;
}

.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered .nim-dialog--preview.nim-dialog--preview-attach, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic .nim-dialog--preview.nim-dialog--preview-attach, .nim-dialog:not(.nim-dialog_deleted):hover .nim-dialog--preview.nim-dialog--preview-attach {
    color: #d02030;
}
.nim-dialog .nim-dialog--preview-attach {
        color: #d02030;
}
.nim-dialog .nim-dialog--name .nim-dialog--name-w {
            color: #d02030;
}

.nim-dialog .nim-dialog--preview, .nim-dialog .nim-dialog--text-preview {
    color: #e1e9ec;
}
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered .nim-dialog--photo, .nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic .nim-dialog--photo, .nim-dialog:not(.nim-dialog_deleted):hover .nim-dialog--photo {
  border-color: Transparency;
}

.box_body, .box_controls {
    background-color: #3e3e3e;
}

.box_controls {
border-top: 1px solid #606060; 
}

.box_title_wrap {
    background-color: #d02030;
}

.button_blue button, .button_gray button, .button_light_gray button, .flat_button{
        background-color: #d02030;
}

.button_blue button.hover, .button_blue button:hover, .flat_button.hover, .flat_button:hover {
    background-color: #df2030;
}

.box_controls .flat_button.secondary:hover, .flat_button.secondary.button_light:hover {
background-color: #4b4b4b;
}

.box_controls .flat_button.secondary, .flat_button.secondary.button_light {
        color: #a3a3a3;
}
.page_block {
    background: #2d2d2d;
}

.im-page--toolsw {
        border-bottom: 1px solid #474747;
}
.im-chat-input {
        border-top: solid 1px #474747;
}
.nim-dialog .nim-dialog--content {
    border-image:
      linear-gradient(to top, black, rgba(0, 0, 0, 0)) 1 100%;
}
.module_header .header_top {
 color: #fff;   
}
.im-mess.im-mess_selected+.im-mess:before, .im-mess.im-mess_unread+.im-mess:before { 
    background: #3b3b3b;
        height: 1px;
}
.fakeinput, div[contenteditable=true], input.big_text, input.dark, input.search, input.text, textarea {
    background: #3e3e3e;
}

.submit_post_box {
    background: #3e3e3e;
        border-radius: 30px;
}

.submit_post {
        background: #3e3e3e;
    border-radius: 0 0 5px 5px;
        border-top: 1px solid #474747
}

.ui_tab_sel, .ui_tabs .ui_tab_sel, .ui_tabs_box .ui_tab_sel {
       color: #bbb; 
}

.ui_rmenu_item, .ui_rmenu_subitem,.post_like, .post_reply, .post_share {
        color: #d02030;
}
.ui_rmenu_sep,.feed_updates .wall_module .feed_row .post,.post_full_like_wrap {
    border-top: 1px solid #474747
}

.feed_updates .explain, .feed_update_row .post_author {
    color: #e1e9ec;
}

.ui_rmenu_item:hover, .ui_rmenu_subitem:hover, .ui_rmenu_item_sel, .ui_rmenu_item_sel:hover {
    background-color: #3e3e3e;
}

.ui_rmenu_item_sel, .ui_rmenu_item_sel:hover {
 color: #bbbbbb;   
}
.ui_rmenu_sliding .ui_rmenu_slider {
    background-color: #6e6e6e;
}

.wall_module .reply_form {
      background-color: #3e3e3e;
    border-radius: 30px;  
}

.wall_module .reply_box, .wall_module .reply_fakebox_wrap {
    border-top: 0px solid #e7e8ec; 
    border-radius: 0 0 2px 2px;
    margin-top: -1px;
    background-color: #2d2d2d;
}

.wall_module .reply~.reply .dld, .wall_module .reply~.reply .reply_wrap {
        border-top: 1px solid #474747;
}
.audio_page_player .audio_page_player_title {
color: #939393;
}

.replies_open, .wr_header {
    color: #bbbbbb;
    background: #3e3e3e;
}
.wall_module .replies_list {
     border-top: 1px solid #474747;   
}
.reply_box_open {
    margin-bottom: 20px;
}

.wall_module .reply_fakebox {
        background: #3e3e3e;
}
.wall_module .reply_box, .wall_module .reply_fakebox_wrap {
    background-color: #3e3e3e;
    border-radius: 30px;
}
.wide_column .page_top {
        border-bottom: 1px solid #474747;
}

.counts_module {
  border-top: 1px solid #474747;  
}
.ui_tabs {
border-bottom: 1px solid #474747;
}

.module {
    border: solid #474747;
        border-width: 1px 0 0;
}

.post_video_title {
    color: #fff !important;
}
.page_counter .count {
    color: #b1000e;
}
}
