style.css:
/* Author: HKK */

@namespace url(http://www.w3.org/1999/xhtml);

/* Front page */
@-moz-document url("http://www.google.com/") {

html > body > div#gbar,
html > body > div.gbh,
html > body > div#guser,
html > body > center > form[name="f"] td > input[type="submit"][name*="btn"],
html > body > center > form[name="f"] td > font[size="-2"],
html > body > center > font[size="-1"],
html > body > center > font + p {
  display: none !important;
}

}


/* Normal (web) results page */
@-moz-document url-prefix("http://www.google.com/search?") {

html > body#gsr > div#header > div#gbar,
html > body#gsr > div#header > div.gbh,
html > body#gsr > div#header > p#gb,
html > body#gsr > div#header > form#tsf > table#sft td > table td.nobr.xsm > *,
html > body#gsr > div#header > form#tsf > table#sft td > table td[nowrap] > input[type=submit],
html > body#gsr > div#res > div + div.e,
html > body#gsr > div#wml,
html > body#gsr > div.clr[style="text-align: center; margin-top: 1.4em;"]
{
  display: none !important;
}

}
