style.css:
@-moz-document domain("insanejournal.com") {
/* MUST BE USED WITH MONOCHROMATIC LEFT SIDEBAR THEME */

/* LEFT SIDEBAR: BACKGROUND COLOR */

#IJ-sitepage-nav,
#IJ-sitepage-body,
#IJ-sitepage-userpic,
#header-nonmenu {
    background-color: #eeeff7 !important;
}

/* BORDER TO THE RIGHT OF USERPIC */

#IJ-sitepage-userpic {
    border-right: 1px solid #dddfef !important;
}
/* LEFT SIDEBAR: LINKS */

#IJ-sitepage-nav a,
#IJ-sitepage-userpic a,
#header-nonmenu a {
    color: #868DC7 !important;
}
/* LEFT SIDEBAR: LINKS HOVER */

#IJ-sitepage-nav a:hover,
#IJ-sitepage-userpic a:hover,
#header-nonmenu a:hover {
    color: #373942 !important;
    text-decoration: none;
}
/* LEFT SIDEBAR: HEADINGS */

.notlink {
    color: #000000 !important;
}
/* MAIN BODY BORDER */

.sidebar #IJ-sitepage-content,
.sidebar #IJ-sitepage-footer {
    border-left: 1px solid #dddfef;
}
/* TOP NAV: MY IJ, POST, ETC. */

#IJ-sitepage-freqlinks {
    background-color: #dddfef !important;
    border: 1px solid #cccfe7 !important;
}
#IJ-sitepage-freqlinks a:hover {
    background-color: #dddfef !important;
    color: #373942 !important;
    text-decoration: underline;
}
/* IJ PAGES: GENERAL STYLING */

#IJ-smallfont td {
    font-family: arial, helvetica;
    font-size: 12px;
}
#IJ-sitepage-content a:link,
#IJ-sitepage-footer a:link {
    text-decoration: none !important;
    color: #6b709f !important;
}
#IJ-sitepage-footer {
    border-right: 0px solid #dddfef !important;
}

/* PAGE DIVIDERS */
hr { border-top: 1px solid #eeeeee; }
}
