style.css:
@-moz-document domain('www.omegle.com') {
body
{
	background: rgb(59,59,59);
}

#header, #intro
{
	background: rgb(80,80,80);
    box-shadow: none;
    border-bottom: solid rgb(68,66,64) 1px;
}

#header #tagline img
{
    visibility: hidden;
}

img[src*="textbtn.png"], 
img[src*="textbtn@2x.png"]
{
	height: 0 !important;
    width: 0 !important;
    padding-left: 124px !important;
    padding-top: 50px !important;
    background: url(http://imgur.com/lKeQDhJ.png) no-repeat !important;
	background-size: 100% !important;
}

img[src*="videobtn-enabled.png"],
img[src*="videobtn-enabled@2x.png"],
img[src*="videobtn-disabled.png"],
img[src*="videobtn-disabled@2x.png"] 
{
	height: 0 !important;
    width: 0 !important;
    padding-left: 124px !important;
    padding-top: 50px !important;
    background: url(http://imgur.com/Ep38i1y.png) no-repeat !important;
	background-size: 100% !important;
}

a#spymodebtn[style],
a#videobtnunmoderated[style],
a[style="display: block; margin-top: 0.5em; border: 1px solid rgb(204, 204, 204); color: black; text-align: center; text-decoration: none; padding: 0.25em; border-radius: 0.5em; position: relative; background: rgb(238, 238, 238);"] ,
#topicsettingscontainer a
{
	background: rgb(59,59,59) !important;
    border-color: rgb(59,59,59) !important;
    color: rgb(170,170, 170) !important;
    border-radius: 1px !important;
}

img[src*="newchatbtn.png"], 
img[src*="newchatbtn@2x.png"]
{
	height: 0 !important;
    width: 0 !important;
    padding-left: 124px !important;
    padding-top: 50px !important;
    background: url(http://imgur.com/Qb7KJYD.png) no-repeat !important;
	background-size: 100% !important;    
}

a#spymodebtn[style],
a#videobtnunmoderated[style]
{
    width: 124px !important;
}

.goog-te-gadget-simple
{
    visibility: hidden !important;
}

.topictag 
{
    background: rgb(39,39,39);
}

.topictageditor ,  .topicplaceholder,  .topicplaceholder:focus
{
    background: rgb(59,59,59) !important;
    border: none;
    color: rgb(20,20,20);
}

#mobilesitenote, #topterms, .chattypeorcell, .logitem .statuslog, .newchatbtnwrapper a, .logitem a, #feedback div a
{
    color: rgb(20,20,20);
    text-decoration: none;
}

.logwrapper, .disconnectbtnwrapper, .sendbtnwrapper,.sendbtn, .chatmsgwrapper, .chatmsg
{
    background: rgb(80,80,80) !important;
	border-radius: 1px !important; 
    border: rgb(59,59,59) solid 1px!important;
    box-shadow: none;
}

.logwrapper
{
    margin: 0;
    width: 98.5%;    
}

.chatmsgcell
{
    margin: 0;
    width: 100%;
}

.chatmsgwrapper
{
    border: none;
}

#intro
{
 	position: relative;
    left: 0;
    right: 0;
    margin-left: auto;
    border: solid rgb(68,66,64) 1px;
}

#monitoringnotice, #monitoringnotice h2, #monitoringnotice #adultalternative, #monitoringnotice #unmonitoredvideobtn
{
	border-radius: 1px;   
    box-shadow: none;
	background: rgb(59,59,59);
    color: white;
}

#monitoringnotice
{
	border: rgb(39,39,39);
}

#feedback
{
    position: relative;
	width: 114%;
    text-align: center;
}

#feedback p, #onlinecount, #onlinecount strong
{
    color: rgb(39,39,39);
}

tbody img, tbody a
{
	display: none;
}

.youmsg .msgsource, .youmsg span
{
	color: rgb(201,201,201);    
}

.strangermsg .msgsource, .strangermsg span
{
	color: rgb(241,241,241);    
}

::-webkit-scrollbar
{
	background: rgb(59,59,59);
    width: 9px;
}

::-webkit-scrollbar-thumb
{
	background: rgb(80,80,80);
}

::-webkit-scrollbar-thumb:hover
{
	background: rgb(70,70,70);
}
}
