style.css:
@-moz-document domain("pinterest.com") {
body{overflow-y:auto}

/* Hide annoying login modal window */
div[data-test-giftwrap]{display:none !important}     /* pinterest.com/explore */
div[data-reactid="3"]{position:static !important}    /* pinterest.com/explore & pinterest.com/pin */
div[data-reactid="3"] ~ div{display:none !important} /* pinterest.com/pin     */

/* Make external and REAL links as default click in image and no annoying Save Pin*/
a.GrowthUnauthPinImage__contentLink{display:block; height:100% !important; width:100%}

/* Hide the cookie annoying advice */
.outerCookieBar{display:none !important}
}
