style.css:
@-moz-document domain("trello.com") {
/*
body,
button,
html,
input,
select,
textarea {
	font-family:  'Segoe UI';
}
*/
.list-wrapper.mod-add.is-idle {
	background-color: rgba(0, 0, 0, 0.12);
}

.list-wrapper.mod-add.is-idle:hover {
	background-color: rgba(0, 0, 0, 0.02);
}

.card-label {
	text-shadow: none;
}

.trellius-calendar-container {
	background-color: rgba(255, 255, 255, 0.65);
}

.trellius-toolbar-button,
.trellius-toolbar-button:hover {
	border-color: rgba(255, 255, 255, 0);
}

.trellius-toolbar-button:hover {
	background-color: rgba(255, 255, 255, 0.3);
}

.trellius-calendar-toolbar {
	padding: 1px;
	box-shadow: none;
}

.member,
.list,
.list-closed {
	background-color: rgba(255, 255, 255, 0.65);
}

.list-header-extras-menu:hover {
	background-color: rgba(255, 255, 255, 0.3)!important;
	text-decoration: none;
}

.open-card-composer:hover,
.list-header-extras-menu:hover {
	background-color: rgba(255, 255, 255, 0.3)!important;
	text-decoration: none;
	color: #111;
}

.list-card.active-card {
	background-color: rgba(255, 255, 255, 0.8)!important;
}

.board-menu,
.window {
	background-color: rgba(255, 255, 255, 0.9);
}

.list-card-operation,
.list-card-operation:hover {
	background-color: rgba(0, 0, 0, 0);
}

.open-card-composer {
	color: rgba(0, 0, 0, 0.6)
}

a:-webkit-any-link {
	text-decoration-skip: ink;
}

.markeddown a {
	text-decoration: none;
	color: #1e89c7;
}

.markeddown a:hover {
	text-decoration: underline;
	color: #1e89c7;
}
/* Button with theme */

body[style*="rgb(0, 174, 204)"] .button.primary,
body[style*="rgb(0, 174, 204)"] button.primary,
body[style*="rgb(0, 174, 204)"] input[type=button].primary,
body[style*="rgb(0, 174, 204)"] input[type=submit].primary {
	background-color: rgb(0, 174, 204);
	background-image: linear-gradient(rgb(0, 174, 204) 0px, rgb(0, 174, 204) 100%);
}
/* Add-on button color */

.mod-disable {
	background-color: #eb5a46;
	color: white;
}

.mod-disable:hover {
	background-color: #cf513d;
	color: white;
}

.mod-enable {
	background-color: #5eb64a;
	color: white;
}

.mod-enable:hover {
	background-color: #519839;
	color: white;
}

#header {
	background-color: rgba(0, 0, 0, 0)!important;
	box-shadow: none;
}

.trellius-button {
	background-color: rgba(0, 0, 0, 0)!important;
	border-radius: 3px!important;
}

.trellius-button:hover {
	background-color: rgba(0, 0, 0, 0.12)!important;
}
}

