style.css:
@-moz-document domain("marktplaats.nl") {
/* * * * * * * * * * * * * * * *\
 *   Marktplaats cleanup CSS   *
 *                             *
\* * * * * * * * * * * * * * * */ 

/* Hide elements */
#banner-top,
#top-banner-wrapper,
#highlights,
#banner-top,
#adsense-top,
#adsense-bottom,
#banner-rubrieks,
#banner-skyscraper,
#crossActivation,
#banner-bottom,
#banner-vipbottom,
#bottom-listings-divider,
#action-block-banners,
#banner-mr,
#vip-map-banner,
#vip-image-popup-banner,
#banner-top-dt,
#adsense-container,
#banner-vipbottom-dt,
.vip-banner-top-sticky-container,
.banner-vipbottom,
.listing-cas,
.listing-extension,
.main-banners,
.listing-banner,
.admarkt-cards,
.adsense-block ,
.banner-viptop,
.l-outside-left,
.mp-Header-menu li span:last-child,
.mp-Listing-banner,
.card-grid .card-grid__unit--large,
.mp-Listing-card-flex--ads,
.sticky-skyscraper-bottom,
.split-skyscrapers,
.premium-content,
.saved-searches-widget,
.banner-row,
.bucket-title,
.breadcrumbs .backlink-item a,
.mp-Listings__admarktTitle,
.mp-Listing--cas,
.mp-Banner
{
  display: none;
}

#main-content,
#ontdek-meer-op-marktplaats {
    margin-top: 0px !important;
}

.l2-list
{
  background-color: white;
  padding: 10px;
  padding-left: 20px;
  box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.14);
  margin-top: -10px;
}

.tabs-vertical .tab-vertical.tab-vertical
{
  background-color: white;
}

#navigation
{
  background-color: #FFF;
}

.breadcrumbs
{
  padding-top: 10px;
  padding-bottom: 10px;
  border-radius: 3px;
  background-color: #FFF;
  box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.14);
  padding-left: 10px;
}

.top-search-info
{
  background-color: transparent;
  box-shadow: none;
}

.results-nav-top
{
  padding: 10px;
  border-radius: 3px;
  background-color: #FFF;
  box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.14);
}

@media (min-width: 46.875em)
  {
    .results-nav-top {
    padding: 10px;
    border-radius: 3px;
    background-color: #FFF;
    box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.14);
  }
}

#sorting-header .label
{
  display: none !important;
}

.top-search-info .mp-Card-body
{
  padding: 0px;
}

.mp-Header--sticky .mp-Header-ribbonBottom.mp-header
{
  box-shadow: 0 0px 15px 0 rgba(0, 0, 0, 0.6);
}

#footer
{
  box-shadow: 0 0 15px 0 rgba(0, 0, 0, 0.3);
}

.mp-Header-searchBar.mp-header
{
  z-index: 100;
  position: relative;
}

#pagination-page-sizes .pagination-size a:hover,
.pagination-size.selected span
{
  border-radius: 5px;
}

#vip-ad-price-container .reserved-label
{
  background-color: #116db4;
  padding: 5px 10px 6px 10px;
  border-radius: 3px;
}

/* Homepage */
#fixed-groups .fixed-group-container
{
  overflow: visible;
  padding: 12px 0;
}

#fixed-groups .fixed-group-container .fixed-group
{
  display: inline-block;
  width: 100%;
}

.mp-Listing-card-body .mp-Listing-card-content
{
  border-top: solid 1px #EEE;
  background-color: #fafafa;
}

.mp-Listing-card:hover
{
  box-shadow: 0 12px 25px 0 rgba(0, 0, 0, 0.2);
  background-color: #FFF;
}

.mp-Listing-card
{
  transition: all 0.2s ease;
}

.mp-Listing-card:hover
{
  transform: translate(0, -4px);
}

/* UI update of side bar */
#category-list li
{
  padding: 2px 0;
}

#category-list li:hover
{
  background-color: #FFF;
  box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.14);
  border-radius: 3px;
}

#category-list li a:hover span
{
  text-decoration: none;
}

#category-list li.level-two
{
  padding: 2px 5px 2px 10px;
}

.filters .attribute
{
  padding: 2px 0;
  cursor: pointer;
}

.filters .attribute:hover
{
  background-color: #FFF;
  box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.14);
  border-radius: 3px;
}

.filters .attribute label:hover
{
  background-color: #FFF;
}

.filters .attribute .inline-input
{
  line-height: 1.3em;
}


/* UI update of results table */
.search-results-table > article
{
  margin-bottom: 6px;
  border-radius: 3px;
  box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.14);
}

.search-results-table article figure
{
  width: 130px;
}

.search-results-table .cell-group.listing
{
  margin-left: 130px;
  padding-left: 10px;
  border-left: solid 1px #EEE;
}

.search-results-table .listing-image
{
  display: flex;
  height: 100%;
}

.search-results-table .listing-image img
{
  margin: auto;
  padding: 0 12px 0 2px;
}

/* Images on product page */
.image-viewer-wrapper
{
  border: none;
}

#vip-image-viewer
{
  padding-bottom: 10px;
  border: none;
  border-bottom: solid 1px #DDD;
}

#large-image-viewer
{
  padding-right: 10px;
  border: none;
  border-right: solid 1px #DDD;
}

#vip-carousel .carousel-button-next
{
  margin-left: 5px;
}

/* "More advertisements from ___" */
.vip-soi-body .listings a
{
  border: solid 1px rgba(0,0,0,0);
  padding: 5px 0;
}

.vip-soi-body .listings a:hover
{
  background-color: #f7f7f6;
  border-radius: 3px;
  border: solid 1px #d4d1cd;
  box-shadow: 0 0 1px 0 rgba(0, 0, 0, 0.14);
}

.vip-soi-body .listings img
{
  padding: 5px;
  max-width: 100%;
}

.vip-soi-body .listings .content
{
  position: relative;
  background-color: transparent;
  color: #111;
  border-top: solid 1px #EEE;
  font-size: 13px;
}

.vip-soi-body .listings .header
{
  font-size: 12px;
}

.vip-soi-body .listings a:hover .content
{
  bottom: 0;
}

.vip-soi-foot
{
  padding: 15px 0 5px 0;
}

.vip-soi-foot a
{
  padding: 5px 10px;
  background-color: #f7f7f6;
  border: solid 1px #d4d1cd;
  border-radius: 3px;
  color: #111;
  box-shadow: inset 0 -2px 0 0 #e6e4e1;
}

.vip-soi-foot a:hover
{
  background-color: #FFF;
}

/* UI update of block overview */
.card-grid__unit:hover .card__link
{
  box-shadow: 0 12px 25px 0 rgba(0, 0, 0, 0.2);
  background-color: #FFF;
}

.card-grid__unit
{
  transition: all 0.2s ease;
}

.card-grid__unit:hover
{
  transform: translate(0, -4px);
}

.card-grid__unit .card .card__body
{
  padding: 10px 10px;
  border-top: solid 1px #EEE;
  background-color: #f6f6f6;
}

@media (min-width: 40em)
{
  .card-grid
  {
    margin: 0 4.5px;
  }
}

/* Fullscreen mode images */
#fullscreen-image-dialog {
    top: 30px;
    left: 50px;
    right: 50px;
    bottom: 30px;
    overflow-y: visible !important;
}
.mp-Carousel {
    border: none !important;
}

.mp-Carousel-nav--left.mp-Button--round,
.mp-Carousel-nav--right.mp-Button--round {
 	background: #e6e4e1;
    box-shadow: none;  
    border-radius: 5px;
}
.mp-Carousel-nav--left.mp-Button--round .mp-Button--round-icon,
.mp-Carousel-nav--right.mp-Button--round .mp-Button--round-icon{
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
}
.mp-Carousel-nav--right,
.mp-Carousel-nav--left {
    height: 100%;
}

#fullscreen-image-dialog .mp-Carousel-nav--right {
    right: 0;
}
#fullscreen-image-dialog .mp-Carousel-nav--left {
    left: 0;
}
}
