style.css:
@-moz-document domain("discordapp.com")
{
/* Author: Staplake, version: 0.1v */


/*Guild sidebar*/

.guilds-wrapper {
    background: rgba(2, 2, 2, 0.58);
}

.guilds-wrapper .guilds .guild .guild-inner {
    background: rgb(34, 34, 35);
}

.guilds-wrapper .guilds .guild .guild-inner:hover {
    background: rgb(44, 44, 47);
}

.new-messages-indicator.new-messages-indicator-mention,
.badge {
    background: rgb(234, 36, 36);
}

/*Freinds page*/

.theme-dark #friends .friends-header,
.theme-dark #friends {
    background: rgb(21, 21, 24);
}

.theme-dark #friends .friends-table .friends-row:hover {
	background: rgb(32, 32, 35);
}

.scrollerWrap-2uBjct,
.search-bar {
  background: rgb(21, 21, 24);
}

.private-channels .channel:hover a {
  background: rgb(44, 44, 47);
}

.private-channels .channel.selected a {
  background: rgb(34, 34, 37);
}

.theme-dark .friends-table,
.theme-dark .messages-wrapper {
  background: rgb(21, 21, 24);
}

.theme-dark .friends-table .messages .divider:before,
.theme-dark .friends-table .messages .divider span,
.theme-dark .messages-wrapper .messages .divider:before,
.theme-dark .messages-wrapper .messages .divider span {
  background: rgb(21, 21, 24);
}

#friends .friends-table .friends-row:hover {
  background: rgb(44, 44, 47);
}

/*Popouts*/

.popout-menu,
.small-popout-box {
  background: rgb(34, 34, 37);
}

.popout-menu .popout-menu-separator {
  border-bottom: 1px solid rgb(43, 39, 39);
}

.popout-menu .popout-menu-item:hover {
  background: rgb(44, 44, 47);
}

.user-popout .avatar-wrapper .avatar-hint {
	border-radius: 5%
}

.user-popout .avatar-wrapper .avatar-popout {
	width: 90px;
	height: 90px;
	background-size: 90px 90px;
	border-radius: 5%
}

.user-popout .body {
	background: rgb(44, 44, 47);
}

.user-popout .body .roles .member-role {
	background: transparent;
	color: rgba(122,129,136,1);
	border: 1px solid rgba(122,129,136,1);
}

.user-popout .footer{
	background: rgb(44, 44, 47);
	border-top: rgb(44, 44, 47);
}

.quick-message {
	background: rgb(39, 39, 41);
	border: 1px solid rgba(122,129,136,1);
  color: rgb(206, 206, 216);
}

.emoji-picker .scroller {
	background: rgb(53, 53, 53);
}

.theme-dark .messages-popout-wrap .messages-popout .message-group{
	background: rgba(47, 47, 51, 1);
}

.theme-dark .themed-popout .header,
.theme-dark .themed-popout {
	background: rgb(34, 34, 37);
}

.quickswitcher.dark .big-input,
.quickswitcher.dark .result.selected {
  background-color: rgb(36, 36, 41);
}

.quickswitcher.dark {
  background-color: rgb(21, 21, 24);
}

.quickswitcher.dark .results-scroller-outer:before {
  background-image: linear-gradient(0deg,#151518,#151518 60%,#2f3136);
}

#user-profile-modal .header:after,
#user-profile-modal .avatar-wrapper,
#user-profile-modal .tab-bar-container,
#user-profile-modal .scroller {
  background: rgb(21, 21, 24);
}

#user-profile-modal .tab-bar-container {
  border-bottom:  rgb(21, 21, 24);
}

#user-profile-modal .guilds .section .connected-accounts .connected-account {
  border: 1px solid #47494a;
}

#user-profile-modal .guilds .section+.section {
  border-top: 1px solid #3a3636;
}

#user-profile-modal .guilds .section .connected-accounts .connected-account .connected-account-name-inner .connected-account-name {
  color: #b3afb0;
}

#user-profile-modal .header .header-info .header-info-inner .discord-tag,
#user-profile-modal .tab-bar .tab-bar-item.selected,
#user-profile-modal .tab-bar .tab-bar-item,
#user-profile-modal .header .header-info .header-info-inner .activity,
#user-profile-modal .guilds .section .section-header,
.note textarea {
  color: #cac5c5;
}

#user-profile-modal .guilds .guild:hover{
  background: rgb(44, 44, 47);
  color: #cac5c5;
}

#user-profile-modal .empty {
  background: #151518;
}

/*Channel bar and user status controller*/

.channel-notices .channel-notice.quickswitcher-notice {
  background-color: #151518;
}

.channel-notices .channel-notice .message .btn {
  background-color: #212226;
}

.channels-wrap {
    background: rgb(21, 21, 24);
}

.container-RYiLUQ {
    background: rgb(21, 21, 24);
}

.wrapper-2xO9RX {
    background: rgb(234, 36, 36);
}

.contentSelectedText-3j5CXt {
    background: rgb(34, 34, 37);
}

.contentHoveredText-2HYGIY {
    background: rgb(44, 44, 47);
}

.container-iksrDt {
    background: rgba(37, 37, 37, 0.05);
}

.wrapper-2ldvyE {
	background: rgb(21, 21, 24);
}

/*Chat*/

.chat .new-messages-bar {
	background: rgb(58, 113, 193);
}

.theme-dark .chat .has-more button {
	background: rgb(21, 21, 24);
	border: 1px solid rgb(21, 21, 24);
}

.theme-dark .chat {
    background: rgb(21, 21, 24);
}

.theme-dark .chat form .typing {
    background: rgb(21, 21, 24);
}

.message-group .avatar-large {
  border-radius: 5px;
  width: 50px;
  height: 50px;
  background-size: 50px 50px;
}

.theme-dark .message-group .comment .markup code.inline,
.theme-dark .message-group .comment .markup pre code,
.theme-dark .message-group .comment .markup pre {
	background: rgb(34, 35, 39);
}

.theme-dark .modal-3HOjGZ {
	background: rgb(21, 21, 24);
}

.theme-dark .message-25xg43 {
	background-color: rgb(34, 34, 37);
}

.bd-blue .upload-modal,
.upload-modal {
  background: rgb(21, 21, 24);
}

.upload-modal .footer {
	background: rgb(34, 34, 37);
}

.upload-modal .footer .button {
	background: rgb(34, 34, 37);
}

.upload-modal .footer .button:hover {
	text-decoration: none;
	opacity: .8;
}

.upload-modal .footer .button-primary {
	background: rgb(44, 44, 47);
	color: rgb(255, 255, 255);
}

.theme-dark .invite-button.join {
    background: rgb(34, 34, 37);
}

.message-group .edit-message .edit-container-outer {
  margin-left: 10px;

}

.invite-button-icon {
    background: rgb(34, 34, 37);
    border: 1px solid rgb(43, 39, 39);
}

.theme-dark .chat>.content {
	background-color: rgb(21, 21, 24);
}

.flex-horizontal>.flex-spacer {
    background: rgb(21, 21, 24);
}

.theme-dark .channel-textarea-guard .countdown-number {
	background: rgb(30, 30, 33);
}

.theme-dark .inner-3if5cm {
  background-color: rgba(37, 39, 41, 0.56);
}

.theme-dark .textArea-20yzAH {
  color: rgb(206, 206, 216);
}

/*members sidebar*/

.theme-dark .channel-members {
    background: rgb(21, 21, 24);
}

.theme-dark .channel-members .member:hover {
    background: rgb(34, 34, 37);
}

.context-menu .theme-dark {
    background: rgb(34, 34, 37);
}

.theme-dark .ui-standard-sidebar-view .content-region {
	background: rgb(21, 21, 24);
}

.avatar-small {
	border-radius: 5%;
}

/*Search*/

.theme-dark .search-results-wrap .search-header {
	background: rgb(21, 21, 24);
}

.theme-dark .search-results-wrap,
.theme-dark .search-results-wrap .channel-separator:before {
	background: rgb(21, 21, 24);
}

.theme-dark .search-results-wrap .channel-separator .channel-name {
	background: rgb(34, 34, 37);
}

.theme-dark .search-results-wrap .search-result .hit {
	border: 2px solid rgb(34, 34, 37);
	background: rgb(34, 34, 37);
	box-shadow: 0 0 10px 6px rgb(21, 21, 24);
}

.theme-dark .search-results-wrap .search-result:before,
.theme-dark .search-results-wrap .search-result:after {
	background:  linear-gradient(180deg,hsla(0,0%,95%,0) 0,rgb(21, 21, 24) 100%);
}

/*Settings*/

.ui-standard-sidebar-view .bda-slist li:nth-child(odd),
.ui-standard-sidebar-view .bda-slist li {
	background: rgb(29, 30, 33);
}


}
