style.css:
@-moz-document url("https://www.mcmod.cn/"), url("https://www.mcmod.cn/404"), url("https://www.mcmod.cn/pages/error/"), url("https://www.mcmod.cn/almanacs/"), url-prefix("https://www.mcmod.cn/log/"), url-prefix("https://www.mcmod.cn/history/"), url-prefix("https://www.mcmod.cn/verify/"), url-prefix("https://server.mcmod.cn/"), url-prefix("https://www.mcmod.cn/tools/"), url-prefix("https://www.mcmod.cn/download/") {
body {
    background-image: url(/*[[URL]]*/ );
    background-size:cover;
    background-repeat:no-repeat; 
    background-attachment:fixed;
}
.class_block .left .list .name a {
    width: 175px;
    height: 20px;
    background-color: rgba(254,254,254,0);
    position: absolute;
}
.un_links {
    width: 100%;
    margin: 10px 0 -10px 0;
    padding: 20px 0 20px 0;
    border-top: 0px solid #CCC;
    background-color: rgba(254,254,254,0);
    display: inline-block;
    clear: both;
}
.under {
    width: 100%;
    margin-top: 10px;
    margin-bottom: 20px;
    background: rgba(254,254,254,0.3);
    float: left;
    border-top: 0px solid #CCC;
}
.tools_box {
    width: 100%;
    bottom: 0;
    margin: 0;
    padding: 0;
    position: fixed;
    background-color: rgba(254,254,254,0.3);
    border-top: 0px solid #DDD;
    z-index: 9990;
}
img{
    opacity:0.8;
}
.ooops .shadow, .ooops .text {
    left: /*[[left]]*/;
}
* {
    word-break: normal;
}
.block img {
    position:relative ;
    z-index: -1;
}
.news_block .left .block img {
    position: relative;
    z-index: 0;
}
.favorite_block ul li .img {
    position: relative;
    z-index: 0;
}
.chart_block .list li .img {
    position: relative;
    z-index: 0;
}
.class_block .left .title .text span {
    position: absolute;
    z-index: -1;
}
.class_block .left .title .icon.t1 i, .class_block .left .title .icon.t2 i, .class_block .left .title .icon.t3 i, .class_block .left .title .icon.t4 i, .class_block .left .title .icon.t5 i, .class_block .left .title .icon.t6 i, .class_block .left .title .icon.t7 i, .class_block .left .title .icon.t23 i, .class_block .left .title .icon.t24 i, .class_block .left .title .icon.today i, .list_block .title .icon.category i, .card_block .title .icon.card i, .favorite_block .title .icon.favorite.type1 i, .favorite_block .title .icon.favorite.type2 i, .favorite_block .title .icon.recently i, .class_block .title .icon.favorite.type1 i, .class_block .title .icon.favorite.type2 i, .class_block .title .icon.recently i, .mod_block .title .icon.mod i, .az_block .title .icon.pyaz i.py, .az_block .title .icon.pyaz i.az, .chart_block .title .icon.chart i {
    background-image: url(/*[[png]]*/);
}
.pages_system .Pbtn, .pages_system .Pbtn_on {
    background:Transparent!important;
}
.m_center li a {
    padding: 0 19px;
}
.v_table td {
    padding: 4.9px;
}
.topic_block {
    padding-bottom: 1em;
    padding-top: 1em;
    background: border-box border-box rgba(255, 255, 255, 0.25);
    overflow: hidden;
    box-shadow: rgba(255, 255, 255, 0.3) 0px 0px 0px 1px inset, rgba(0, 0, 0, 0.6) 0px 0.5em 1em;
}
.topic_block .title {
    padding-left: 4px;
}
.topic_block ul {
    padding-left: 4px !important;
}
.topic_block .dec span {
    color: #0f845f;
}
}

@-moz-document url-prefix("https://www.mcmod.cn/center/") {
body {
    background-attachment:fixed;
}
.top-main {
    padding: 0 5px;
    height: 49px;
    min-width: 800px;
    background: rgba(61,69,76,0.8);
    border-bottom: 0;
}
.centers_center {
    width: 958px;
    margin: 10px 0 0 0;
    padding: 10px 10px 10px 10px;
    background-color: rgba(254,254,254,0.5);
    border: 1px solid #DDD;
    float: left;
}
.main {
    background-color: rgba(254,254,254,0.3);
}
.block_album .title, .main .block .title {
    margin: 2px;
    padding: 0 0 0 10px;
    line-height: 40px;
    background-color: rgba(254,254,254,0.3);
    font-size: 16px;
}
.top, .top .logo, .class_block .title #icon1, .class_block .title #icon2, .class_block .title #icon3, .class_block .title #icon4, .block_hot .item #icon1, .block_hot .item #icon2, .mbx_left, .mbx_right, .mbx .line, .mbx, .mbx .home {
    background:rgba(254,254,254,0.3);
}
.tools_box {
    width: 100%;
    bottom: 0;
    margin: 0;
    padding: 0;
    position: fixed;
    background-color: rgba(254,254,254,0.5);
    border-top: 0px solid #DDD;
    z-index: 9990;
}
.under {
    width: 100%;
    margin-top: 10px;
    margin-bottom: 20px;
    background: rgba(254,254,254,0.6);
    float: left;
    border-top: 0px solid #CCC;
}
.img {
    background:rgba(254,254,254,0)!important;
}
}

@-moz-document url-prefix("https://www.mcmod.cn/class/"), url-prefix("https://www.mcmod.cn/item/"), url-prefix("https://www.mcmod.cn/post/") {
body{
	background-image: url(/*[[URL]]*/ )!important;
    background-size:cover !important;
    background-repeat:no-repeat !important; 
    background-attachment:fixed !important;
}
.class_block .left .list .name a {
    background:Transparent;
}
.under {
    background: rgba(254,254,254,0.3);
}
.tools_box {
    background-color: rgba(254,254,254,0.3);
    border-top: 0px solid #DDD;
}
.relation_block ol li {
    height: /*[[auto]]*/;
}
.body {
    opacity: /*[[op]]*/;
}
.class_block .left .list .name a {
    width: 175px;
    height: 20px;
    background-color: rgba(254,254,254,0);
    position: absolute;
}
.view {
    background:Transparent !important;
    background-color: /*[[color]]*/ !important;
}
.ItemsBlock .name {
    font-size: 19.3px;
}
}

@-moz-document url-prefix("https://www.mcmod.cn/s/"), url-prefix("https://www.mcmod.cn/s?key=") {
body {
    background-image: url(/*[[URL]]*/ )  ;
    background-size:cover;
    background-repeat:no-repeat; 
    background-attachment:fixed;
}
.nav {
    margin-bottom: 24px;
    height: 31px;
    background: transparent;
    border-bottom: 0px solid #e0e0e0;
    padding: 5px 0 0 210px;
}
.rightMenu {
    background:rgba(254,254,254,0.5);
}
#foot {
    height: 32px;
    line-height: 32px;
    text-align: center;
    background: rgba(244,244,244,0.5);
    border-top: 0px solid #e0e0e0;
    color: #ababab;
}
}

@-moz-document url-prefix("https://admin.mcmod.cn/") {
* {
    word-break: normal !important;
}
}

@-moz-document url-prefix("https://www.mcmod.cn/") {
* {
    font-family: /*[[text]]*/,sans-serif;
    word-break: break-all;
}
}

@-moz-document url-prefix("https://www.mcmod.cn/class/diff/") {
.difference_block .title {
    position: relative;
    margin: 10 auto;
    padding-bottom: 1em;
    padding-top: 1em;
    background: hsla(0, 0%, 100%, 0.5) border-box;
    overflow: hidden;
    box-shadow: 0 0 0 1px hsla(0,0%,100%,.3) inset, 0 0.5em 1em rgba(0, 0, 0, 0.6);
}
tbody {
    text-shadow: 0 1px 1px hsla(0,0%,100%,.3);
    background: hsla(0, 0%, 100%, 0.4) border-box;
    overflow: hidden;
}
}
