style.css:
@-moz-document regexp("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe.*") {
/*==================================
必要ないものは消す
==================================*/
/* 上部タイトル */
#title {
    display: none;
}
/* サムネイル */
.thumbnail {
    display: none;
}
/* 装飾 */
.entry {
    box-shadow: none;
}
.style-name {
    text-decoration: none;
}
.style-name:hover {
    text-decoration: underline;
}
/* 余白 */
.maintab {
    padding: 0;
}
body > DIV {
    padding: 0;
}
.entry:not(:first-child) {
    margin-top: 8px;
}
/* height設定 */
html,
#tab-item-installed, #tab-item-recommended {
    height: auto;
}

/*==================================
ほかの調整
==================================*/
/* 上部ヘッダーの文字が大きすぎてはみ出てるから小さく */
.tabs-container header label {
    font-size: 1vw;
}
/* 有効無効ボタンをシンプルに */
.thumb_action_link {
    box-shadow: none;
}
.thumb_action_link{
    font-size: 100%;
    display: inline;
    padding:8px 10px;
}
/* 一番下のボタン二つ */
.find-styles {
    height: auto;
    margin: auto;
    line-height: 30px;
    box-shadow: none;
}
/* 有効ボタン、無効ボタンが分かりにくいので変更 */
.thumbnail_activate {
    background: gray;
}
.thumbnail_deactivate {
    background: #f08080;
}
}
