style.css:
@-moz-document domain("videos.assemblee-nationale.fr") {
.fullscreen .video {
    width: 100%;
}

.fullscreen .tab {
    position: fixed;
}

.fullscreen .tab_content, .fullscreen .mediaIndex {
    background: none;
}

.fullscreen .day_order .day_order_toolbar {
    background: none;
}
.fullscreen .day_order .day_order_toolbar .day_order_filter {
    float: right;
}

.fullscreen .mediaIndex li {
    background-color: rgba(255,255,255,0.15);
    border: 0;
    border-radius: 3px;
    box-shadow: none;
}
.fullscreen .mediaIndex a {
    color: black!important;
}
.fullscreen .mediaIndex li img {
    border-radius: 50%;
    opacity: 0.5;
}
}
