style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("about:reader?") {

  #reader-toolbar,
  #reader-toolbar #close-button,
  #reader-toolbar .style-button,
  #reader-toolbar #narrate-toggle,
  #reader-toolbar #pocket-button {
    transition: background-color 0.4s, border-color 0.4s;
  }

  body.dark #reader-toolbar,
  body.dark #reader-toolbar #close-button:not(:hover),
  body.dark #reader-toolbar .style-button:not(:hover),
  body.dark #reader-toolbar #narrate-toggle:not(:hover),
  body.dark #reader-toolbar #pocket-button:not(:hover) {
    background-color: #333333 !important;
    border-color: #333333 !important;
  }
}
