style.css:
@-moz-document domain(www.redshift3d.com) {
  
/* Main Styles */
  body { background: #2D2D2D; }
  
  section#forum, .off-canvas-content {
    background-color: rgb(45,45,45);
  }
  
  section#forum .forum-heading,
  #fm-review-wrapper .forum-heading,
  section#forum .threadDateBox {
    background-color: rgb(92,92,92);
  }
  
  section#forum a { color: #A14040; transition: .6s; }
  section#forum a:hover { color: #CE7777; }
  section#forum a::-moz-selection {
    background-color: #A14040;
    color: #cccbcb;
  }
  
	section#forum .title, section#forum .forum-subhead, section#forum .forum-row a {
      color: #aaa8a8;
  }
  
  section#forum .forum-row .forum-number, #fm-review-wrapper .forum-row .forum-number {
    color: #AAA8A8;
  }
  
  section#forum .forum-row .fm-description a,
  section#forum .forum-row .fm-description, #fm-review-wrapper .forum-row .fm-description{
    color: #747373 !important;;
  }
  
  a.forum-marker>img { filter: invert(100%) hue-rotate(315deg) grayscale(4%) saturate(367%) grayscale(65%); }
  
  nav#submenu { background: #5c5c5c; }
  nav#submenu ul.menu li.label a { color: #cccbcb; transition: .3s; }
  nav#submenu ul.menu li.label a:hover {color: #2D2D2D; transition: .3s; }
  
  nav#submenu a.button.hollow.red {
    color: #A14040 !important;
    background: #2d2d2d !important;
    border: 2px solid #A14040 !important;
    transition: .3s !important;
  }
  
  nav#submenu a.button.hollow.red:hover {
    background: #A14040 !important;
    color: #CCCBCB !important;
    transition: .3s !important;
  }
  
  nav #button-reply, nav #button-new-topic { background: #2D2D2D !important; transition: .3s; }
  nav #button-reply:hover, nav #button-new-topic:hover { background: #3B9A24 !important; transition: .3s; }
  
  ul.forum-breadcrumbs li a.fa-home,
  ul.forum-breadcrumbs li a,
  ul.forum-breadcrumbs li {color: #CCCBCB !important;}
  
  nav#submenu .fa,
  nav#submenu #button_0 i,
  #button_0 nav#submenu i,
  nav#submenu #button_1 i,
  #button_1 nav#submenu i,
  nav#submenu #button_2 i,
  #button_2 nav#submenu i,
  nav#submenu #button_3 i,
  #button_3 nav#submenu i,
  nav#submenu #button_4 i,
  #button_4 nav#submenu i,
  nav#submenu #button_5 i,
  #button_5 nav#submenu i,
  nav#submenu #button_6 i,
  #button_6 nav#submenu i,
  nav#submenu #button_7 i,
  #button_7 nav#submenu i,
  nav#submenu #button_8 i,
  #button_8 nav#submenu i { color: #2D2D2D; }
  
  #menuContainer #mainMenu .fa-bars, #smallMenuContainer #mainMenu .fa-bars { color: #CCCBCB; }
  #menuContainer #mainMenu a.logo, #smallMenuContainer #mainMenu a.logo { filter: grayscale(100%) brightness(200%) contrast(150%) drop-shadow(0px 0px 5px rgba(1,1,1,.1)); }
/* Bottom Main Page */
  .fm-whos-online a {
    color: #FA4F4F;
  }
  
  #forum .fm-visitor-stats-wrapper table,
  #forum .fm-visitor-stats-wrapper thead,
  #forum .fm-visitor-stats-wrapper th,
  #forum .fm-visitor-stats-wrapper tr,
  #forum .fm-visitor-stats-wrapper td {
    background-color: #5c5c5c !important;
    color: #AAA8A8;
    border-color: #2D2D2D !important;
  }
  
  #forum .fm-visitor-stats-wrapper table.fm-has-th-row th {
    border-bottom: 1px solid #2D2D2D !important;
    border-right: 1px solid #2D2D2D !important;
  }
  
  #forum table thead, table tbody, table tfoot { border-color: #2D2D2D;}
  
  
  
/* Thread Styles */
  section#forum p,
  section#forum b,
  section#forum i,
  section#forum h1,
  section#forum h2,
  section#forum h4,
  section#forum h5,
  section#forum ul,
  section#forum small,
  section#forum .member-details,
  section#forum .threadDate {
    color: #aaa8a8;
  }
  
  section#forum h3 { color: #cccbcb; }
  
  section#forum p::-moz-selection,
  section#forum b::-moz-selection,
  section#forum i::-moz-selection,
  section#forum h1::-moz-selection,
  section#forum h2::-moz-selection,
  section#forum h4::-moz-selection,
  section#forum h5::-moz-selection,
  section#forum ul::-moz-selection,
  section#forum small::-moz-selection,
  section#forum .button::-moz-selection,
  section#forum textarea::-moz-selection,
  section#forum .threadDate::-moz-selection {
    background: #833a3a !important;
    color: #cccbcb !important;
  }
  
  section#forum .forum-thread .forum-author a:first-of-type, #fm-review-wrapper .forum-thread .forum-author a:first-of-type {
    color: #a14040 !important;
  }
  
  section#forum .forum-thread .forum-post .prmalink a, #fm-review-wrapper .forum-thread .forum-post .prmalink a { color: #cccbcb !important; }
  
  section#forum .forum-thread,
  .fm-attachments{
    border: 1px solid #aaa8a8;
  }
  
  section#forum .forum-thread .forum-post blockquote,
  section#forum #fm-review-wrapper .forum-thread .forum-post blockquote {
    border: 1px solid #aaa8a8;
  }
  
  section#forum div.quote_author {
    color: #5c5c5c !important;
  }
  
  section#forum a.quote-post,
  section#forum a.fm-fast-reply,
  section#forum a.fm-new-topic {
    border: 2px solid #a14040 !important;
    color: #a14040 !important;
  }
  
  section#forum a.quote-post:hover,
  section#forum a.fm-fast-reply:hover,
  section#forum a.fm-new-topic:hover {
    background-color: #833a3a !important;
    border: 2px solid #833a3a !important;
    color: #e6e6e6 !important;
  }
  
  section#forum a.fm-post-reply:hover,
  section#forum a.fm-subscribe:hover {
    color: #e6e6e6 !important;    
  }
  
  section#forum a.fm-subscribe {
    border: 2px solid #58a499 !important;
    color: #58a499 !important;
  }
  
  section#forum a.fm-subscribe:hover {
    background: #58a499 !important;
  }
  
  section#forum #submit_post {
    background-color: #5c5c5c;
  }
  
  section#forum textarea, section#forum textarea:focus {
    background-color: #2d2d2d;
    color: #cccbcb;
    border-color: #2d2d2d;
  }
  
  #forum span[style*="background-image: url('/themes/forum_themes/redshift3d/images/icon_profile.png')"]{
  filter: grayscale(100%);}

#forum img.rankImage {
  filter: contrast(65%) grayscale(100%);
}
  
/* New Post and Post Reply */
  .grey-bg { background-color: #2d2d2d; }
  #content { background-color: #2d2d2d; }
  #content #submit_post { background-color: #5c5c5c; }
  #content p,
  #content b,
  #content i,
  #content h1,
  #content h2,
  #content h4,
  #content h5,
  #content ul,
  #content small,
  #content .member-details,
  #content .threadDate,
  .column:last-child:not(:first-child), .columns:last-child:not(:first-child) {
    color: #aaa8a8;
    transition: .2s;
  }
  
  #content i:hover { color: #CE7777; transition: .2s; }
  #content #format tbody {background-color: #5C5C5C !important;}
  
  #content h3, #content .forum-post strong, #content { color: #cccbcb; }
  
  #content p::-moz-selection,
  #content b::-moz-selection,
  #content i::-moz-selection,
  #content h1::-moz-selection,
  #content h2::-moz-selection,
  #content h4::-moz-selection,
  #content h5::-moz-selection,
  #content ul::-moz-selection,
  #content small::-moz-selection,
  #content .button::-moz-selection,
  #content textarea::-moz-selection,
  #content .threadDate::-moz-selection,
  #content .forum-post strong::-moz-selection,
  #content h3::-moz-selection,
  #content div::-moz-selection {
    background: #833a3a !important;
    color: #cccbcb !important;
  }
  
  #content select {
    background: #2d2d2d !important;
    color: #aaa8a8;
    border: 1px solid #5C5C5C;
  }
  
  #content textarea, #content textarea:focus {
    background-color: #2d2d2d;
    color: #cccbcb;
    border-color: #2d2d2d;
  }
  
  #content .htmlButtonOff a {color: #CE7777; transition: .2s}
  #content .htmlButtonOff a:hover {color: #A14040; transition: .2s;}
  
  #content a { color: #A14040; transition: .6s; }
  #content a:hover { color: #CE7777; }
  #content a::-moz-selection {
    background-color: #A14040;
    color: #cccbcb;
  }
  
  #content .forum-thread,
  .fm-attachments{
    border: 1px solid #aaa8a8;
  }
  
  #content .button[name=preview],
  #content .fileUpload.button,
  #content #fm-toggle-poll,
  #content #fm-poll-questions a {
    border: 2px solid #a14040 !important;
    color: #a14040 !important;
    background: #2d2d2d !important;
  }
  
  #content .button[name=preview]:hover,
  #content .fileUpload.button:hover,
  #content #fm-toggle-poll:hover,
  #content #fm-poll-questions a:hover{
    background-color: #833a3a !important;
    border: 2px solid #833a3a !important;
    color: #e6e6e6 !important;
  }
  
  
  
/* New Topic */
  #fm-poll-questions b,
  #fm-poll-questions div { color: #cccbcb}
  
  #fm-poll-questions input.fm-poll-answer,
  #fm-poll-questions input[name=poll_question] {
    background: #2d2d2d;
    border-color: #2d2d2d;
    color: #cccbcb;
  }
  
  #fm-poll-questions input.fm-poll-answer::-moz-selection,
  #fm-poll-questions input[name=poll_question]::-moz-selection {
    background: #A14040;
  }
  
  #content input[name=title] {
    background: #2d2d2d;
    border-color: #2d2d2d;
    color: #cccbcb;
  }
  
  #content input[name=title]::-moz-selection {
    background: #A14040;
  }
  
  #content .fm-data-grid.attachments tbody,
  #content .fm-data-grid.attachments thead,
  #content .fm-data-grid.attachments tr > *{
    background: #2D2D2D !important;
    color: #CCCBCB !important;
  }
  
  #submit_post .fm-data-grid.attachments input.submit { background: #A14040 !important;}
  #submit_post .fm-data-grid.attachments input.submit:hover { background: #833a3a !important;}
  
  
  
/* Polls */
  #forum .fm-poll-wrapper table,
  #forum .fm-poll-wrapper thead,
  #forum .fm-poll-wrapper th,
  #forum .fm-poll-wrapper tr,
  #forum .fm-poll-wrapper td {
    background: #5c5c5c;
    color: #CCCBCB;
  }
  
  #forum input[type=submit] {
    background: #2d2d2d;
    border: 2px solid green;
    padding: 6px 12px;
    border-radius: 7px;
    color: #CCCBCB;
    transition: .4s;
  }
  
  #forum input[type=submit]:hover {
    color: #A14040;
    border: 2px solid #A14040;
    transition: .2s;
  }



/* Login Page */
  #login_form .white-input {
    	background-color: #5C5C5C !important;
  }
  
  #login_form input {
    	color: #CCCBCB;
    	background: #2D2D2D;
    	border: 1px solid #2D2D2D;
  }
  
  #login_form label.checkbox {
    color: #CCCBCB;
  }
  
  #login_form input::-moz-selection,
  #login_form label.checkbox::-moz-selection {
    background: #A14040;
  }
  
  #content input[value=Login] {
    background: #2D2D2D !important;
    border: 2px solid #3B9A24 !important;
    color: #CCCBCB !important;
    transition: .6s !important;
  }
  
  #content input[value=Login]:hover {
    background: #3B9A24 !important;
    color: white !important;
    transition: .6s !important;
  }
  
/* Pagination */
  #forum ul.pagination a { color: #AAA8A8 !important; }
  #forum ul.pagination a:hover {
    background: #A14040 !important;
    color: #CCCBCB !important;
    border-radius: 3px;
  }

  #forum ul.pagination .paginateCur {
    background: #A14040;
    color: #CCCBCB !important;
    }
  
  
  
/* Advanced Search */
  #forum .fm-advanced-search-wrapper .white-input { background-color: #5C5C5C; }
  #forum .fm-advanced-search-wrapper input {
    background: #2D2D2D;
    border: 1px solid #2D2D2D;
    color: #CCCBCB !important;
  }
  #forum .fm-advanced-search-wrapper input::-moz-selection { background: #A14040 !important; }
  #forum .fm-advanced-search-wrapper input::-moz-placeholder { color: #CCCBCB !important; }

  #forum .fm-advanced-search-wrapper select {
    background: #2D2D2D;
    color: #AAA8A8;
    border: 1px solid #2D2D2D;
  }

  #forum .fm-advanced-search-wrapper select[name=search_in],
  #forum .fm-advanced-search-wrapper select[name=search_criteria],
  #forum .fm-advanced-search-wrapper select[name=order_by],
  #forum .fm-advanced-search-wrapper select[name=date] {
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' version='1.1' width='32' height='24' viewBox='0 0 32 24'><polygon points='0,0 32,0 16,24' style='fill: rgb(200,200,200)'></polygon></svg>");
    background-repeat: no-repeat;
    background-size: 9px 6px;
    background-position: right -1rem center;
    background-origin: content-box;
  }

  #forum input[value=Search] { background: #2D2D2D !important; }
  #forum input[value=Search]:hover {
    background: green !important;
    border: 2px solid green !important;
  }



/* Code */
  #forum div.codeblock, #content div.codeblock {
    background: rgb(230,230,230);
    border: 1px solid rgb(230,230,230);
    border-radius: 5px;
    padding: 5px;
    margin: .8rem auto;
  }

  #forum code, #content code {
    background: none !important;
    border: none !important;
    font-family: Courier, monospace;
    font-size: .8em;
    padding: 0px;
  }

  #forum code span::-moz-selection,
  #content code span::-moz-selection {
    color: #CCCBCB !important;
    background: #A14040 !important;
  }
  
  
  
/* Main Nav */
#menuContainer {
 background-color: #5C5C5C !important;
}

#mainMenu ul li a { color: #CCCBCB !important;}
#mainMenu ul li a:hover { color: #2D2D2D !important;}

#menuContainer .dropdown.menu > li.is-dropdown-submenu-parent > a::after, #smallMenuContainer .dropdown.menu > li.is-dropdown-submenu-parent > a::after {
  border-color: #AAA8A8 transparent transparent;
}

#menuTrialButton {
  background: #2D2D2D !important;
  border: 2px solid #A14040 !important;
  color: #CCCBCB;
}

#mainMenu ul li a#menuTrialButton:hover {
  background: #A14040 !important;
  color: #CCCBCB !important;;
}

#mainMenu ul li a#menuBuyButton { color: #CCCBCB !important; }
#mainMenu ul li a#menuBuyButton:hover { color: #CCCBCB !important; }



/* Forum Search Splash */
#forumSplash .forum-search-form .input-append {
  background: #5C5C5C;
}

#forumSplash input[name=keywords] {
  background: #5C5C5C;
  color: #CCCBCB;
}

#forumSplash input[name=keywords]::-moz-placeholder {
  color: #CCCBCB;
  opacity: .7;
}

#forumSplash input[name=keywords]::-moz-selection {
  background: #A14040;
}

#forumSplash .forum-search-form .input-append button.fa {
  color: #AAA8A8;
}

#forumSplash h5 a {
  color: #CCCBCB !important;
}
}
