style.css:
@-moz-document domain("joinjoaomgcd.appspot.com") {
body {
    background-color: #000;
}
#input.paper-input, .devicename, #countdown, #userinfo {
    color: #FFF;
}
#content {
    background-color: #000;
}
#top.sms-messenger, #header.join-app {
    background-color: #277caa;
    color: #ffffff;
}
#text.sms-message {
    font-weight: normal;
}
.speech.sms-message {
    background-color: #003265;
    border: 1px solid black;
    margin-right: 30%;
    color: #EEFFDD;
    font-size: 11pt;
}
.sent.sms-message .speech.sms-message {
    background-color: #474747;
    margin-left: 30%;
}
.sent.sms-message {
    text-align: left;
}
.sms-contact, .sms-contact-compose {
    color: #EEE;
}
#contact.sms-contact:hover, #contact.sms-contact-compose:hover {
    background-color: #103250;
    color: #000;
}
.devicebutton.command-button:hover {
    background-color: #103250;
}
.device.join-device #deviceiconcontainer.join-device,
#encryptionbuttons.sms-messenger paper-button.sms-messenger,
paper-fab.paper-fab,
paper-fab.paper-fab-0,
paper-fab.paper-fab-1,
#buttonshowapikey,
#buttonclose {
    background-color: #185487;
}
.deviceproperty.join-app {
    color:#277caa;
}
#shadow.join-app {
    background: linear-gradient(#277caa, rgba(255, 255, 255, 0));
}
}
