style.css:
/* i really want this to be global */
#subscriptions_nav_below,
#sinner_container,
#sb_rp_upgrade {
    display: none !important;
}
#tree_pane {
    font-size: 12px !important;
}
#sb_tp_search_overlay,
#subscriptions_buttons,
#sb_rp_tools {
    background-image: none;
    background-color: #e6e6e6;
}
#subscriptions_buttons.sb_upgrade #sb_rp_notifications {
    right: 50px
}
#subscriptions_buttons.sb_upgrade #sb_rp_tools {
    right: 90px;
}
#subscriptions_buttons.sb_upgrade #sb_rp_gear {
    right: 10px;
}
#reader_pane.reader_pane_sinner {
    padding-right: 0;
}
#article_dialog .article_content {
    max-width: none;
    font-size: 16px;
    line-height: 2.3rem;
}
#article_dialog_wrapper {
    width: 90% !important;
    left: 5% !important;
}
.article_title.article_title_expanded_view .article_title_link {
    font-size: 30px;
}
#article_dialog .article_full_contents {
    width: 80%;
    margin: auto;
}
p {
    margin-top: 2rem;
    margin-bottom: 2rem;
}
pre {
    line-height: 0.9rem;
}
.article_content img {
    max-width: 100% !important;
}
