style.css:
@-moz-document domain('lichess.org'), domain('l.org') {
body.playing.zen #site_title {
    opacity: 0;
    transition: 1s;
}
body.playing.zen .board_left:hover #site_title {
    opacity: 1!important;
}
@media (pointer:fine) {
    /* do not hide this on touch screens */
    body.playing.zen .dasher:not(:hover):not(.shown) {
    	opacity: 0!important;
    }
}
}
