style.css:
@-moz-document domain("www.vidlii.com") {
 div.pr_hd_bar{
    background: transparent;
    top: -40px;
    left: -120px;
    z-index: 50000;
    position: relative;
    width: 514px;
    margin-top: 0px;
    order: initial;
    left: 120px;
  }
  #pr_hd ul, .n_head ul{
    top: -99px
  }

a.yel_btn{
  color: black !important;
  background: linear-gradient(white,#ececec);
  border: 1px solid lightgrey;
  border-radius: 3px;
  font-weight: 200;
  }

.yel_btn:hover {
  color: black !important;
  background: linear-gradient(white,ghostwhite);
  border: 1px solid lightgrey;
  border-radius: 3px;
  }

  input.search_button{
  color: black !important;
  background: linear-gradient(white,#ececec);
  border: 1px solid lightgrey;
  border-radius: 3px;
  font-weight: 200; 
  }

.pr_hd_bar > .yel_btn{
  right: -5px;
    top: 7px;
  }

  input.search_bar{
    width: 300px;
    border-radius: 0px;
  }

  #hd_vidlii{
    top: 12px;
  }

  nav#sm_nav{
    top: 41px;
    right: 0px;
  }

  .n_head{
    margin: 0 0 0px;
  }
  div.pr_hd_wrapper{
}
}
