style.css:
@-moz-document domain("admin.df.eu") {

/* Domainfactory Adminpanel - Emailfilter RegEx-Editorfenster vergrößern */
#filter_popup_window {
    top: 10px !important; }

#extra_wordlist,
#extra_custom {
    width: /*[[wwidth]]*/px !important;
    left: calc(50% - /*[[wwidth]]*/px/2) !important; }

#div_filter_values,
#cheader_div_filter_values {
    height: /*[[wheight]]*/px !important;
    width: calc(/*[[wwidth]]*/px - 30px) !important; }

#table_condition_values #div_filter_values .editbox,
#cheader_div_filter_values .editbox {
    width: calc(100% - 65px) !important; }

#table_condition_values th:last-of-type,
.popup-list-thead th:last-of-type {
    text-align: right !important;
    padding-right: 58px !important; }

}
