	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}


	/*Outline for links*/
 	a
 	{
		outline-color: var(--main-color) !important;
	}


	/**Removing text decorations*/
	*
	{
		text-decoration: none !important;
	}


	/*Body*/
	body.pg-video-view, .content_wrapper, body, .main
	{
		background-color: var(--main-background) !important;
	}


	/*Header*/
	.header.opaque
	{
		background-color: var(--second-background) !important;
	}
	.navbar-inverse .navbar-collapse, .navbar-inverse .navbar-form
	{
		border-color: var(--hover-background) !important;
	}
	/*Logo*/
	.header a.logo .logo-black, .header_gutter .logo-gutter
	{
		background-image: url("https://raw.githubusercontent.com/RaitaroH/Vidme-DeepDark/master/Images/logo-white.png") !important;
	}
	.header a.logo > span.logo-black, .header a.logo > span.logo-white, .header_gutter .logo-gutter
	{
		opacity: .9;
	}
	.header a.logo > span.logo-black:hover, .header a.logo > span.logo-white:hover, .header_gutter .logo-gutter:hover
	{
		opacity: 1;
	}
	/*Sidebar button*/
	.header.opaque .menu-hamburger > a
	{
		opacity: .8 !important;
		filter: invert(100%) !important;
  	}
	.header.opaque .menu-hamburger > a:hover
	{
		opacity: 1 !important;
  	}
	/*Text in the header*/
	.header.opaque .main-links > li > a, .header.opaque .menu > li > a
	{
		color: var(--main-text) !important;
	}
	.header.opaque .main-links > li > a:hover, .header.opaque .menu > li > a:hover, .header .main-links > li > a:hover:hover, .header .menu > li > a:hover
	{
		background-color: transparent !important;
		color: var(--main-color) !important;
	}
	/*Upload button*/
	.header .menu > .menu-upload > a
	{
		background: var(--main-color) !important;
	}
	/*Search*/
	.header.opaque .search
	{
		border-color: var(--hover-background) !important;
		background: var(--main-background) !important;
	}
	.header.opaque .search input
	{
		color: var(--dimer-text) !important;
	}
	.header.opaque .search:active, .header.opaque .search:hover, .header.opaque .search:focus, .header .search:active, .header .search:focus, .header .search:hover
	{
		border-color: var(--main-color) !important;
	}


	/*Recommended videos sidebar*/
	#video_view .video_suggestions
	{
		background-color: var(--second-background) !important;
	}
	#video_view .gutter_label
	{
		color: var(--dimer-text) !important;
	}
	/*Video title*/
	#video_view .video_suggestions .title > a
	{
		color: var(--main-text) !important;
	}
	#video_view .video_suggestions .title > a:hover
	{
		color: var(--main-color) !important;
	}
	/*Author*/
	#video_view .video_suggestions .owner > a, .users_list .user_username
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	#video_view .video_suggestions .owner > a:hover, .users_list .user_username:hover
	{
		opacity: 1 !important;
	}
	#video_view .dfp-unit-wrapper
	{
		background-color: var(--second-background) !important;
	}

	/*Bellow video*/
  	#video_view .video_info3
	{
		background-color: var(--second-background) !important;
	}
	/*Text*/
	#video_view .video_actions a.action .text, #video_view .video_info3 .video_stats > li
	{
		color: var(--dimer-text) !important;
	}
	#video_view .video_stats > li.video_watching, #video_view .video_actions a.action .num
	{
		color: var(--main-color) !important;
	}
	/*Dropdown menus*/
	#video_view .video_actions_more .dropdown-menu, .dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus
	{
		background-color: var(--main-background) !important;
	}
	.dropdown-menu > li > a
	{
		color: var(--dimer-text) !important;
	}
	.dropdown-menu > li > a:hover
	{
		color: var(--main-color) !important;
		background-color: var(--hover-background) !important;
	}
	#video_view .video_actions a.action svg
	{
		fill: var(--dimer-text) !important;
	}


	/*Video title*/
	#video_view .video_info2
	{
		background-color: var(--second-background) !important;
	}
	#video_view .video_title
	{
		color: var(--main-text) !important;
	}
	#video_view .video_author_wrapper .video_author_username
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	#video_view .video_author_wrapper .video_author_username:hover
	{
		opacity: 1 !important;
	}
	/*Badge*/
	#video_view .video_author .video_author_badge.featured, #video_view .video_author .video_author_badge.verified, #user_view .user_header .user_vip
	{
		filter: grayscale(100%);
	}
	/*Follow*/
	#video_view .video_follow .btn, #user_view .user_header .user_actions a.user_follow, .btn-default
	{
		border-color: var(--main-color) !important;
		color: var(--main-color) !important;
		opacity: .9;
		line-height: 20px;
	}
	.users_list .user_follow
	{
		background-color: transparent !important;
	}
	#video_view .video_follow .btn:hover, #user_view .user_header .user_actions a.user_follow:hover, .btn-default:hover, .users_list .user_follow:hover
	{
		opacity: 1 !important;
		background-color: transparent !important;
	}
 	#video_view .video_category
	{
		color: var(--main-text) !important;
		opacity: .9;
	}
	#video_view .video_category:hover
	{
		color: var(--main-color) !important;
		opacity: 1 !important;
	}


	/*Subscribe*/
	#user_view .user_header .user_actions a.user_subscribe, #user_view .gutter .levels .level_button > a, #subscriptions_levels_view .levels .level_button > a, #video_view .video_subscribe .btn
	{
		background: transparent !important;
		border: 1px solid var(--main-color) !important;
		color: var(--main-color) !important;
		opacity: .9;
	}
	#user_view .user_header .user_actions a.user_subscribe:hover, #user_view .gutter .levels .level_button > a:hover, #subscriptions_levels_view .levels .level_button > a:hover, #video_view .video_subscribe .btn:hover
	{
		opacity: 1 !important;
		background-color: transparent !important;
	}
	#user_view .gutter .levels > li, #user_view .level_description_wrapper, #subscriptions_levels_view .levels > li
	{
		background-color: var(--second-background) !important;
	}
	#user_view .gutter .levels .level_title, #subscriptions_levels_view .levels .level_title, #subscriptions_levels_view h3
	{
		color: var(--main-text) !important;
	}
