	{
		color: var(--dimer-text) !important;
	}
	#subscriptions_levels_view .back a:hover
	{
		color: var(--main-color) !important;
	}
	.form-control, #subscriptions_levels_view .form-control, #subscriptions_levels_view .user_header .avatar
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}


	/*Description*/
	#video_view .video_description_wrapper
	{
		background-color: var(--second-background) !important;
	}
	/*test*/
	#video_view .video_description
	{
		color: var(--dimer-text) !important;
	}
	.video_description_more_border_top_truncated
	{
		background: linear-gradient(to bottom,rgba(255,255,255,0) 0%,var(--main-background) 300%) !important;
	}
	/*Show more*/
	#video_view .video_description_more
	{
		background-color: var(--second-background) !important;
	}
	#video_view .video_description_more:hover, #video_view .video_description_more:hover i
	{
		color: var(--main-color) !important;
	}
	#video_view .video_description_wrapper_expanded .video_description_more
	{
		border-color: var(--hover-background) !important;
	}
	/*Links*/
	#video_view .video_description a
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	#video_view .video_description a:hover
	{
		opacity: 1 !important;
	}


	/*Tips*/
	#video_view .video_likes, #video_view .video_tips
	{
		background-color: var(--second-background) !important;
	}
	#video_view .video_headline
	{
		color: var(--main-text) !important;
	}
	.video_tips .become-tipper > a
	{
		border-color: var(--main-color) !important;
		color: var(--main-color) !important;
		opacity: .9;
	}
	.video_tips .become-tipper > a:hover
	{
		opacity: 1 !important;
	}


	/*Upvotes*/
	.video_likes > ul .more a:hover
	{
		color: var(--main-color) !important;
	}
	.comments_list .comment_vote .comment_upvote.active *, .comments_list .comment_vote li a
	{
		color: var(--dimer-text);
	}


	/*Coments*/
	#video_view .comments
	{
		background-color: var(--second-background) !important;
	}
	/*Sort*/
	#video_view .sort_comments > *
	{
		color: var(--dimer-text) !important;
	}
	#video_view .sort_comments_button .btn-xs:hover
	{
		color: var(--main-color) !important;
	}
	/*Dropdown menu*/
	.open > .dropdown-menu
	{
		background-color: var(--main-background) !important;
	}
	.dropdown-menu
	{
		background-color: var(--main-background) !important;
	}
	.dropdown-menu .divider, .comments_list .comment_spam
	{
		background-color: var(--hover-background) !important;
	}
	/*Comment*/
	.comments_composer_input
	{
		border-color: var(--hover-background) !important;
	}
	.comments_composer_input:hover, .comments_composer_input:active, .comments_composer_input:focus
	{
		border-color: var(--main-color) !important;
	}
	.comments_composer_input textarea
	{
		color: var(--dimer-text) !important;
	}
	/*Post button*/
	.btn-info
	{
		background: var(--main-color) !important;
		border-color: var(--main-color) !important;
		opacity: .9;
	}
	.btn-info:hover, .btn-info:active, .btn-info:active:hover, .btn-info:focus
	{
		opacity: 1 !important;
	}
	/*Commentor username*/
	.comments_list .comment_author_username
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	.comments_list .comment_author_username:hover
	{
		opacity: 1 !important;
	}
	.comments_list .comment_body
	{
		color: var(--dimer-text) !important;
	}
	.comments_list .comment_vote:hover *
	{
		color: var(--main-color) !important;
	}
	/*Hashtags*/
	.auto-tagged
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	.auto-tagged:hover
	{
		opacity: 1 !important;
	}
	/*Heart icon*/
	.comments_list .comment-subscriber > .self > .inner > .comment_inner > .comment_meta > .comment_author_username::after
	{
		filter: grayscale(100%) invert(100%);
	}


	/*Footer*/
	.footer, .footer_static
	{
		background-color: var(--second-background) !important;
		border-top: 1px solid var(--hover-background) !important;
