@-moz-document domain("4chan.org"), domain("4channel.org") {
body div#doc div:nth-child(7) {
    display: none;
}
#site-stats .boxcontent {
    margin-top: 0;
    padding-bottom: 2em;
}
div.boxcontent {
    background-color: #22313F;
    padding-bottom: 0px;
}
div#boards,
div#popular-threads,
div#site-stats {
    padding-bottom: 0;
}
.boxbar {
    background-color: #22313F;
}
div#ft ul {
    display: none;
}
div#copyright {
    margin: 0;
}
div.post {
    margin: 0 auto;
}
.box-outer {
    padding-bottom: 0;
}
.boxcontent {
    margin-top: 0;
}
img {
    max-width: auto;
    max-height: 320px;
}
#logo {
    background: url('https://unsplash.it/300/120');
}
.extPanel.reply {
    background: #191919;
}
#wot-cnt > p {
    color: lightgray;
}
.bd {
    background-color: #2D404F;
}
