style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sat24.nl"), domain("sat24.com") {
/* Weghalen grijze balk bovenin */
  table[height="19"] { display: none !important; }
  div#Div1,#Div3,#Div4,#Div5,#DivZoek,#languageBanner { display: none !important; }
  iframe[src="http://www.beurs.nl/toolbar.php"] { display: none !important; }

/* Weghalen balken aan de zijkant */
  td[width="20"] { display: none !important; }
  td[background="images/balk_l_02.gif"] { display: none !important; }
  td[style="width: 25px;"] { display: none !important }

/* Centreren pagina */
  form[id="aspnetForm"] > div + div > table { width: auto !important; margin: 0px auto !important; }
  table[width="950"] { width: 820px !important }

/* Weghalen footer */
  #Div1 + table td[width="900"] > table > tbody > tr + tr + tr, form[id="aspnetForm"] > div + div > p, form[id="aspnetForm"] > div + div > table > tbody > tr > td + td > p,#Div1 + table td[width="900"] > table > tbody > tr + tr > td + td > table > tbody > tr > td[align="left"], img[src="images/footer.gif"] { display: none !important; }

/* Weghalen banners */
  div#banner,#banner2,#bannervast,#adl_skyscraper { display: none !important; }
  iframe[src="http://www.sat24.com/120x600.aspx"],iframe[name="google_ads_frame"],iframe[src="120x600-google.aspx"],td[style="width: 120px; vertical-align: top;"] { display: none !important; }

/* Weghalen stipjes en streepjes */
  img[src="images/rm.gif"],img[src="images/stip.gif"] { display: none !important; }

/* Menubalk links uitlijning wijzigen */
  td[width="120"][align="right"] { text-align: left !important; }
  td[align="right"][width="115"] { text-align: left !important }
 
/* Resizen banner bovenin zodat hij matcht met de inhoud eronder qua grootte */
  a[target="_top"][href="http://www.sat24.nl"] > img[alt="Header"], a[target="_top"][href="http://www.sat24.com"] > img[alt="Header"] {
    height: 78px !important;
    width: 820px !important;
    overflow: hidden !important;
    display: block !important; 
    margin: 0px 0px 0 7px !important;
  }

/* Uitlijnen tekst */
  td[width="300"], table[width="300"] { width: auto !important }
}
