style.css:
@-moz-document domain("workflowy.com")
{

/* 140+ BACKGROUND COLORS */ 

 :root {
	--circle-opacity-1: rgba(0,0,0,0.0);
	--circle-opacity-2: rgba(0,0,0,0.0); 
    --circle-hover-color-1: rgba(255,255,255, 1);
	--circle-hover-color-2: rgba(0,0,0, 1.0); 
	--content: "â—¯";
	--font-family: AvenirNext-UltraLight;
}

.contentTag[title="Filter #indian-red"]:before {
	content: var(--content);
	display: inline !important;
	visibility: visible !important;
	color: var(--circle-opacity-1) !important;
	font-family: var(--font-family) !important;
}

.contentTag[title="Filter #indian-red"]:hover:before {
	color: var(--circle-hover-color-1) !important;
}

span.contentTag[title="Filter #indian-red"] {
	visibility: hidden;
}

span.contentTag[title="Filter #indian-red"] span {
	display: none;
}.indian-red-proj > .name > .content {
    color: white !important;
    background: indianred !important;
    padding: 3px 10px 3px 10px; 
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.08), 0 6px 20px 0 rgba(0, 0, 0, 0.08);
    border-radius: 5px;    
    margin-top: 10px;
    margin-bottom: 10px;
