style.css:
@-moz-document domain("www.wordreference.com") {
body,
input,
.inputcontainer,
#forumapi *,
#headerTabs span,
#headerTabs a {
    background-color: #101010 !important;
}
#headerTabs .selected span,
#headerTabs .selected a,
#headerTabs span:hover,
#headerTabs a:hover {
    background-color: #000 !important;
}
input {
    color: #646464 !important;
}
tbody .even {
    background-color: #101010 !important;
}
tbody .odd:hover {
    background-color: #080808 !important;
}
* {
    border-color: #323232 !important;
    font-family: Nyala !important;
    font-size: medium;
}
.namecell,
#header {
    color: #e3e3e3 !important;
}
.namecell font span,
#header a span {
    color: #66bbff !important;
}
.custom-select * {
    background-image: none !important;
    background-color: rgba(0, 0, 0, .5) !important;
    color: #A0A0A0 !important;
    box-shadow: none !important;
}
#container,
.layout {
    background-color: rgba(0, 0, 0, .5) !important;
    box-shadow: 0px 0px 5px #e3e3e3 !important;
}
#container #header,
.namecell {
    background-color: #323232 !important;
}
#container #search,
th {
    background-color: #161616 !important;
}
#container .content {
    background-color: rgba(0, 0, 0, .5) !important;
}
#container #footer {
    background-color: #161616 !important;
    color: #646464 !important;
}
.content * {
    color: #969696 !important;
}
#centercolumn {
    width: 832px !important;
}
#rightcolumn,
#rightcolumn * {
    position: absolute !important;
    width: 0px !important;
    height: 0px !important;
    margin-left: 0px !important;
    padding: 0px !important;
    margin: 0px !important;
    color: transparent !important;
}
#listen_widget,
#listen_widget * {
    background-color: #161616 !important;
    color: #969696 !important;
}
.content span {
    color: #66bbff !important;
}
.content a {
    background-color: transparent !important;
    color: #66bbff!important;
}
.content a:visited {
    color: #2a7ec1 !important;
}
#gamesad {
    background-color: #554a00 !important;
    color: #e3e3e3 !important;
}
#navbar {
    padding: 0;
    margin: 0;
    height: 50px;
    width: 100%;
    background-color: rgba(94, 88, 198, 0.5);
    float: left;
}
.logo,
.dotcom {
    font-size: 45px;
    font-family: Arial, Helvetica, sans-serif;
    color: #5e58c6;
    font-size: 3.5em !important;
}
#infoone,
#infotwo,
#infothree {
    width: 28%;
    margin: 40px 2% 20px;
    text-align: justify;
    border: 1px solid #ccc;
    display: block;
    float: left;
    opacity: 0;
}
#search input {
    background: rgba(0, 0, 0, 0)!important;
    border: 1px solid #d3d3d3;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    height: 30px;
    width: 280px;
    font-size: 16px;
    padding-right: 2px;
}
.submit-button {
    display: block;
    background: rgba(94, 88, 198, 0) url(http://www.wordreference.com//images/magnifying_glass_white_26x26.png) no-repeat 50%!important;
    background-size: 20px 20px!important;
    border: 1px solid #5e58c6;
    border-radius: 0px;
    border-radius: 0px 4px 4px 0px;
    width: 50px;
    height: 42px;
    cursor: pointer;
}
}
